pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IMecenateVault {
    function depositToken(
        address _token,
        uint256 _amount,
        bytes32 encryptedVaultId
    ) external;

    function depositETH(bytes32 encryptedVaultId) external payable;
}

interface IMecenateForwarderFactory {
    function getVault() external view returns (address);
}

contract MecenateForwarder {
    bytes32 public encryptedVaultId;

    address public factory;

    constructor(bytes32 _encryptedVaultId, address _factory) {
        encryptedVaultId = _encryptedVaultId;
        factory = _factory;
    }

    function depositToken(address token, uint256 amount) external {
        address vault = IMecenateForwarderFactory(factory).getVault();

        // Approva il token
        require(IERC20(token).approve(vault, amount), "Approve failed");

        IMecenateVault(vault).depositToken(token, amount, encryptedVaultId);
    }

    receive() external payable {
        address vault = IMecenateForwarderFactory(factory).getVault();

        // Esegui il deposito in ETH
        (bool success, ) = payable(vault).call{value: msg.value}(
            abi.encodeWithSignature("depositETH(bytes32)", encryptedVaultId)
        );

        require(success, "Deposit failed");
    }
}
","keccak256":"0x7d7938321e2c02ceef6bd71120e7407497721bca9236b6bab419bbc1af3da1af","license":"MIT"},"contracts/factories/MecenateForwarderFactory.sol":{"content":"import "../MecenateForwarder.sol";

// import Ownable
import "@openzeppelin/contracts/access/Ownable.sol";

contract MecenateForwarderFactory is Ownable {
    mapping(bytes32 => address) public forwardersMapping;

    address public mecenateVault;

    constructor(address _mecenateVault) {
        mecenateVault = _mecenateVault;
    }

    function getVault() public view returns (address) {
        return mecenateVault;
    }

    function changeVault(address _mecenateVault) public onlyOwner {
        require(
            msg.sender == _mecenateVault,
            "Only the factory can change the factory"
        );
        mecenateVault = _mecenateVault;
    }

    event ForwarderCreated(
        address indexed forwarder,
        bytes32 encryptedVaultId,
        address vaultContract
    );

    function createforwarder(
        bytes32 _encryptedVaultId
    ) public returns (address) {
        require(
            forwardersMapping[_encryptedVaultId] == address(0),
            "forwarder already created"
        );
        MecenateForwarder forwarder = new MecenateForwarder(
            _encryptedVaultId,
            address(this)
        );

        forwardersMapping[_encryptedVaultId] = address(forwarder);

        return address(forwarder);
    }

    function getforwarders(bytes32 _user) public view returns (address) {
        return forwardersMapping[_user];
    }
}
