contracts/modules/Finalization.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;
import "./Staking.sol";

abstract contract Finalization is Staking {
    function finalizePost(
        bool valid,
        uint256 punishment,
        bytes32 encryptedVaultId
    ) external virtual {
        onlyVault();
        require(
            post.postdata.settings.status == Structures.PostStatus.Submitted,
            "NOT_SUBMITTED"
        );
        require(
            keccak256(postSettingPrivate.vaultIdBuyer) == encryptedVaultId,
            "VAULTID_NOT_MATCH"
        );

        // Common contract addresses and variables
        address treasuryContract = IMecenateFeedFactory(
            settings.factoryContract
        ).treasuryContract();
        uint256 buyerFee = (post.postdata.escrow.payment *
            IMecenateTreasury(treasuryContract).globalFee()) / 10000;
        uint256 amountToAdd = post.postdata.escrow.payment - buyerFee;

        // Variables for stake changes
        uint256 buyerStake;
        uint256 sellerStake;

        if (post.postdata.settings.endTimeStamp < block.timestamp || valid) {
            // Code for both the timeout and the valid case
            buyerStake = Deposit._decreaseDeposit(
                post.postdata.settings.tokenId,
                keccak256(postSettingPrivate.vaultIdBuyer),
                post.postdata.escrow.payment
            );

            sellerStake = Deposit._increaseDeposit(
                post.postdata.settings.tokenId,
                keccak256(postSettingPrivate.vaultIdSeller),
                amountToAdd
            );

            TokenManager._burn(
                post.postdata.settings.tokenId,
                treasuryContract,
                buyerFee
            );

            // Update status and stakes
            post.postdata.escrow.stake = sellerStake;
            post.postdata.escrow.payment = buyerStake;
            _changeStatus(
                valid
                    ? Structures.PostStatus.Finalized
                    : Structures.PostStatus.Punished
            );

            emit Valid(post);
        } else if (!valid) {
            require(
                punishment <= post.postdata.escrow.stake,
                "PUNISHMENT_TOO_HIGH"
            );
            require(settings.punishmentRatio < 1e18, "PUNISHMENT_RATIO_HIGH");

            uint256 penalty = (punishment * settings.punishmentRatio) / 1e18;
            uint256 totalPunishmentFee = penalty + punishment;

            TokenManager._burn(
                post.postdata.settings.tokenId,
                treasuryContract,
                totalPunishmentFee
            );

            post.postdata.escrow.payment = _burnStake(
                post.postdata.settings.tokenId,
                keccak256(postSettingPrivate.vaultIdBuyer),
                penalty
            );

            post.postdata.escrow.stake = _burnStake(
                post.postdata.settings.tokenId,
                keccak256(postSettingPrivate.vaultIdSeller),
                punishment
            );

            // Update status and penalties
            post.postdata.settings.status = Structures.PostStatus.Punished;
            post.postdata.escrow.punishment = punishment;
            post.postdata.escrow.penalty = penalty;

            _changeStatus(Structures.PostStatus.Punished);

            emit Invalid(post);
        }
    }
}

contracts/modules/Message.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./Events.sol";

abstract contract Message is Events {
    function _isSellerOrBuyer(
        bytes32 encryptedVaultId
    ) internal view returns (bool) {
        bytes32 sellerVaultIdHash = keccak256(postSettingPrivate.vaultIdSeller);
        bytes32 buyerVaultIdHash = keccak256(postSettingPrivate.vaultIdBuyer);
        return (encryptedVaultId == sellerVaultIdHash ||
            encryptedVaultId == buyerVaultIdHash);
    }

    function getVaultIdSecret(
        bytes32 encryptedVaultId
    ) external view virtual returns (bytes memory) {
        require(_isSellerOrBuyer(encryptedVaultId), "NOT_THE_SELLER_OR_BUYER");
        return postSettingPrivate.vaultIdSeller;
    }

    function getTelegramIds(
        bytes32 encryptedVaultId
    ) external view returns (uint256, uint256) {
        require(
            postSettingPrivate.buyerTelegramId != 0,
            "NO_TELEGRAM_ID_FOR_BUYER"
        );

        require(
            postSettingPrivate.sellerTelegramId != 0,
            "NO_TELEGRAM_ID_FOR_SELLER"
        );

        require(
            encryptedVaultId == keccak256(postSettingPrivate.vaultIdSeller) ||
                encryptedVaultId == keccak256(postSettingPrivate.vaultIdBuyer),
            "NOT_THE_SELLER_OR_BUYER"
        );

        return (
            uint160(postSettingPrivate.buyerTelegramId),
            uint160(postSettingPrivate.sellerTelegramId)
        );
    }

    function getHashedVaultId(
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) external virtual returns (bytes32) {
        (bytes memory vaultId, , , ) = _verifyNonce(
            sismoConnectResponse,
            _to,
            _nonce
        );

        bytes32 encryptedVaultId = keccak256(vaultId);

        require(
            encryptedVaultId == keccak256(postSettingPrivate.vaultIdSeller) ||
                encryptedVaultId == keccak256(postSettingPrivate.vaultIdBuyer),
            "NOT_SELLER_OR_BUYER"
        );

        if (encryptedVaultId == keccak256(postSettingPrivate.vaultIdSeller)) {
            return keccak256(postSettingPrivate.vaultIdSeller);
        } else {
            return keccak256(postSettingPrivate.vaultIdBuyer);
        }
    }

    function write(
        bytes memory encodeMessage,
        bytes32 encryptedVaultId
    ) external virtual {
        onlyVault();
        require(_isSellerOrBuyer(encryptedVaultId), "NOT_THE_SELLER_OR_BUYER");
        _writeMessage(encodeMessage, encryptedVaultId);
    }

    function _writeMessage(
        bytes memory encodeMessage,
        bytes32 encryptedVaultId
    ) internal {
        if (encryptedVaultId == keccak256(postSettingPrivate.vaultIdSeller)) {
            settings.lastMessageForBuyer = encodeMessage;
        } else {
            settings.lastMessageForSeller = encodeMessage;
        }
    }

    function getMessage(
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) external virtual returns (bytes memory) {
        bytes32 encryptedVaultId = _getEncryptedVaultId(
            sismoConnectResponse,
            _to,
            _nonce
        );
        require(_isSellerOrBuyer(encryptedVaultId), "NOT_THE_SELLER_OR_BUYER");
        return _getMessage(encryptedVaultId);
    }

    function _getMessage(
        bytes32 encryptedVaultId
    ) internal view returns (bytes memory) {
        if (encryptedVaultId == keccak256(postSettingPrivate.vaultIdSeller)) {
            return settings.lastMessageForBuyer;
        } else {
            return settings.lastMessageForSeller;
        }
    }

    function _getEncryptedVaultId(
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) internal virtual returns (bytes32) {
        (bytes memory vaultId, , , ) = _verifyNonce(
            sismoConnectResponse,
            _to,
            _nonce
        );
        return keccak256(vaultId);
    }
}

contracts/modules/Renounce.sol.content:
/**
 * @title Renounce
 * @dev This abstract contract extends the Staking contract and provides functionality for a seller to renounce a post.
 * The seller can only renounce a post if they are the original creator of the post and the post is in the Accepted or Submitted status.
 * Renouncing a post refunds the buyer's payment and transfers the seller's stake to the seller's address.
 * The post struct is reset and the post status is updated to Renounced.
 * This contract also provides an internal function for refunding a post, which is called by the renouncePost function.
 */
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "./Staking.sol";

abstract contract Renounce is Staking {
    function renouncePost(
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) external {
        onlyVault();

        // Validate the post status
        Structures.PostStatus currentStatus = post.postdata.settings.status;
        require(validStatuses[uint8(currentStatus)], "INVALID_STATUS");

        // Verify the nonce and get the vault ID
        (bytes memory vaultId, , , ) = _verifyNonce(
            sismoConnectResponse,
            _to,
            _nonce
        );
        bytes32 encryptedVaultId = keccak256(vaultId);

        // Confirm that the caller is the seller
        bytes32 sellerVaultId = keccak256(postSettingPrivate.vaultIdSeller);
        require(encryptedVaultId == sellerVaultId, "NOT_SELLER");

        // Reset post and post settings
        post = Structures.Post(
            Structures.User({vaultId: bytes32(0)}),
            Structures.PostData({
                settings: Structures.PostSettings({
                    status: Structures.PostStatus.Renounced,
                    postType: Structures.PostType.Text,
                    creationTimeStamp: 0,
                    endTimeStamp: 0,
                    duration: 0,
                    tokenId: Structures.Tokens.NaN
                }),
                escrow: Structures.PostEscrow({
                    stake: 0,
                    payment: 0,
                    punishment: 0,
                    penalty: 0
                }),
                data: Structures.PostEncryptedData({
                    encryptedData: "",
                    encryptedKey: "",
                    decryptedData: ""
                })
            })
        );

        postSettingPrivate.vaultIdBuyer = ZEROHASH;
        postSettingPrivate.buyerTwitterId = 0;
        postSettingPrivate.buyerTelegramId = 0;
        postSettingPrivate.vaultIdSeller = ZEROHASH;
        postSettingPrivate.sellerTwitterId = 0;
        postSettingPrivate.sellerTelegramId = 0;

        // Emit event
        emit Renounced(post);
    }
}

contracts/modules/Staking.sol.content:
/**
 * @title Staking
 * @dev This contract handles staking functionality for the Mecenate platform.
 * It allows users to add, take, and burn stakes, and provides functions to get the total staked amount and individual stake amounts.
 * It also includes a modifier to check the status of a post before allowing stake-related transactions.
 */
pragma solidity 0.8.19;

import "./Deposit.sol";
import "./Events.sol";

abstract contract Staking is Events, Deposit, TokenManager {
    using SafeMath for uint256;

    event StakeBurned(bytes32 staker, uint256 amount);
    event StakeTaken(bytes32 staker, uint256 amount, Structures.Tokens tokenId);
    event StakeAdded(bytes32 staker, uint256 amount, Structures.Tokens tokenId);

    function _addStake(
        Structures.Tokens tokenId,
        bytes32 staker,
        address funder,
        uint256 amountToAdd
    ) internal returns (uint256 newStake) {
        Structures.PostStatus currentStatus = post.postdata.settings.status;
        require(
            currentStatus != Structures.PostStatus.Accepted &&
                currentStatus != Structures.PostStatus.Submitted,
            "INVALID_STATUS"
        );

        require(amountToAdd > 0, "STAKE_REQUIRED");

        if (tokenId != Structures.Tokens.NaN) {
            TokenManager._transferFrom(
                tokenId,
                funder,
                address(this),
                amountToAdd
            );
        }

        newStake = Deposit._increaseDeposit(tokenId, staker, amountToAdd);

        emit StakeAdded(staker, amountToAdd, tokenId);

        return newStake;
    }

    function _takeStake(
        Structures.Tokens tokenId,
        bytes32 staker,
        address _to,
        uint256 amountToTake
    ) internal returns (uint256 newStake) {
        // Memorizza lo status del post in una variabile per evitare accessi ridondanti allo storage
        Structures.PostStatus currentStatus = post.postdata.settings.status;

        // Verifica che lo status del post sia valido per procedere
        require(
            currentStatus != Structures.PostStatus.Accepted &&
                currentStatus != Structures.PostStatus.Submitted,
            "INVALID_STATUS"
        );

        // Effettua il trasferimento del token o dell'Ether
        if (tokenId == Structures.Tokens.NaN) {
            //(bool result, ) = payable(_to).call{value: amountToTake}("");
            //require(result, "CALL_FAILED");
            payable(_to).transfer(amountToTake);
        } else {
            TokenManager._transfer(tokenId, _to, amountToTake);
        }

        // Aggiorna il deposito e emette un evento
        newStake = Deposit._decreaseDeposit(tokenId, staker, amountToTake);
        emit StakeTaken(staker, amountToTake, tokenId);

        return newStake;
    }

    function _takeFullStake(
        Structures.Tokens tokenId,
        address _to,
        bytes32 staker
    ) internal returns (uint256 amountTaken) {
        uint256 currentDeposit = Deposit._getDeposit(tokenId, staker);

        uint256 newStake = _takeStake(tokenId, staker, _to, currentDeposit);

        return newStake;
    }

    function _burnStake(
        Structures.Tokens tokenId,
        bytes32 staker,
        uint256 amountToBurn
    ) internal returns (uint256 newStake) {
        uint256 newDeposit = Deposit._decreaseDeposit(
            tokenId,
            staker,
            amountToBurn
        );

        if (tokenId == Structures.Tokens.NaN) {
            (bool result, ) = payable(
                IMecenateFeedFactory(settings.factoryContract)
                    .treasuryContract()
            ).call{value: amountToBurn}("");
            require(result, "CALL_FAILED");
        } else if (tokenId == Structures.Tokens.DAI) {
            BurnDAI._burnDai(amountToBurn);
        } else if (tokenId == Structures.Tokens.MUSE) {
            BurnMUSE._burn(amountToBurn);
        }

        emit StakeBurned(staker, amountToBurn);

        return newDeposit;
    }

    function _burnFullStake(
        Structures.Tokens tokenId,
        bytes32 staker
    ) internal returns (uint256 amountBurned) {
        uint256 currentDeposit = Deposit._getDeposit(tokenId, staker);

        _burnStake(tokenId, staker, currentDeposit);

        return currentDeposit;
    }

    function getStake(
        Structures.Tokens tokenId,
        bytes32 staker
    ) external view returns (uint256 amount) {
        // get deposit
        amount = Deposit._getDeposit(tokenId, staker);
        // explicit return
        return amount;
    }

    function getTotalStaked() external view returns (uint256) {
        uint256 amountSeller = Deposit._getDeposit(
            post.postdata.settings.tokenId,
            keccak256(postSettingPrivate.vaultIdSeller)
        );

        uint256 amountBuyer = Deposit._getDeposit(
            post.postdata.settings.tokenId,
            keccak256(postSettingPrivate.vaultIdBuyer)
        );

        return (amountSeller + amountBuyer);
    }

    function addStake(
        Structures.Tokens tokenId,
        uint256 amountToAdd,
        bytes32 encryptedVaultId
    ) external payable returns (uint256) {
        require(tokenId == post.postdata.settings.tokenId, "WRONG_TOKEN");

        // Check if the encryptedVaultId matches with either the buyer or the seller
        require(
            encryptedVaultId == keccak256(postSettingPrivate.vaultIdBuyer) ||
                encryptedVaultId == keccak256(postSettingPrivate.vaultIdSeller),
            "VAULTID_MISMATCH"
        );

        // Determine the amount to add based on the role (buyer or seller)
        uint256 actualAmountToAdd = (encryptedVaultId ==
            keccak256(postSettingPrivate.vaultIdSeller))
            ? msg.value
            : amountToAdd;

        // Update the stake
        uint256 newStake = _addStake(
            tokenId,
            encryptedVaultId,
            settings.vaultContract,
            actualAmountToAdd
        );

        // Update the corresponding escrow value based on the role
        if (encryptedVaultId == keccak256(postSettingPrivate.vaultIdBuyer)) {
            post.postdata.escrow.payment = newStake;
        } else {
            post.postdata.escrow.stake = newStake;
        }

        return newStake;
    }

    function takeStake(
        Structures.Tokens tokenId,
        uint256 amountToTake,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) external returns (uint256) {
        onlyVault();

        require(tokenId == post.postdata.settings.tokenId, "WRONG_TOKEN");

        bytes32 encryptedVaultId = _commonTakeStake(
            sismoConnectResponse,
            _to,
            _nonce
        );

        uint256 currentDeposit = Deposit._getDeposit(tokenId, encryptedVaultId);
        require(currentDeposit >= amountToTake, "NOT_ENOUGH_STAKE");

        uint256 newBalance = _takeStake(
            tokenId,
            encryptedVaultId,
            _to,
            amountToTake
        );

        if (encryptedVaultId == keccak256(postSettingPrivate.vaultIdBuyer)) {
            post.postdata.escrow.payment = newBalance;
        } else {
            post.postdata.escrow.stake = newBalance;
        }

        return newBalance;
    }

    function takeFullStake(
        Structures.Tokens tokenId,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) external returns (uint256) {
        onlyVault();

        require(tokenId == post.postdata.settings.tokenId, "WRONG_TOKEN");

        bytes32 encryptedVaultId = _commonTakeStake(
            sismoConnectResponse,
            _to,
            _nonce
        );

        uint256 newBalance = _takeFullStake(tokenId, _to, encryptedVaultId);

        if (encryptedVaultId == keccak256(postSettingPrivate.vaultIdBuyer)) {
            post.postdata.escrow.payment = newBalance;
        } else {
            post.postdata.escrow.stake = newBalance;
        }

        return newBalance;
    }

    function getSellerStake() external view returns (uint256 amount) {
        // get deposit
        amount = Deposit._getDeposit(
            post.postdata.settings.tokenId,
            keccak256(postSettingPrivate.vaultIdSeller)
        );
        // explicit return
        return amount;
    }

    function getBuyerStake() external view returns (uint256 amount) {
        // get deposit
        amount = Deposit._getDeposit(
            post.postdata.settings.tokenId,
            keccak256(postSettingPrivate.vaultIdBuyer)
        );
        // explicit return
        return amount;
    }

    function _commonTakeStake(
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) internal view returns (bytes32) {
        (bytes memory vaultId, , , ) = _verifyNonce(
            sismoConnectResponse,
            _to,
            _nonce
        );
        bytes32 encryptedVaultId = keccak256(vaultId);

        require(
            encryptedVaultId == keccak256(postSettingPrivate.vaultIdBuyer) ||
                encryptedVaultId == keccak256(postSettingPrivate.vaultIdSeller),
            "UNAUTHORIZED"
        );

        return encryptedVaultId;
    }
}

contracts/modules/Submission.sol.content:
/**
 * @title Submission
 * @dev This contract is an abstract contract that defines the functions for submitting and revealing data for a post. It inherits from the Events contract.
 */
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./Events.sol";

abstract contract Submission is Events {
    function submitHash(
        bytes memory encryptedKey,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) external virtual {
        onlyVault();

        Structures.PostStatus currentStatus = post.postdata.settings.status;
        require(
            validStatuses[uint8(currentStatus)] &&
                (currentStatus == Structures.PostStatus.Accepted ||
                    currentStatus == Structures.PostStatus.Submitted),
            "WRONG_STATUS"
        );

        (bytes memory vaultId, , , ) = _verifyNonce(
            sismoConnectResponse,
            _to,
            _nonce
        );
        bytes32 encryptedVaultId = keccak256(vaultId);

        require(
            IMecenateUsers(settings.usersModuleContract).checkifUserExist(
                encryptedVaultId
            ),
            "User does not exist"
        );
        require(post.creator.vaultId == encryptedVaultId, "NOT_SELLER");

        _changeStatus(Structures.PostStatus.Submitted);

        settings.encodedSymKey = post.postdata.data.encryptedKey = encryptedKey;
        post.postdata.settings.status = Structures.PostStatus.Submitted;
        post.postdata.settings.endTimeStamp =
            block.timestamp +
            post.postdata.settings.duration;

        emit Valid(post);
    }

    function revealData(
        bytes memory decryptedData,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) external virtual returns (bytes memory) {
        onlyVault();

        Structures.PostStatus currentStatus = post.postdata.settings.status;
        require(
            validStatuses[uint8(currentStatus)] &&
                (currentStatus == Structures.PostStatus.Submitted ||
                    currentStatus == Structures.PostStatus.Revealed ||
                    currentStatus == Structures.PostStatus.Finalized),
            "INVALID_STATUS"
        );

        (bytes memory vaultId, , , ) = _verifyNonce(
            sismoConnectResponse,
            _to,
            _nonce
        );
        bytes32 encryptedVaultId = keccak256(vaultId);

        require(
            encryptedVaultId != keccak256(postSettingPrivate.vaultIdSeller),
            "YOU_ARE_THE_SELLER"
        );
        require(
            currentStatus == Structures.PostStatus.Finalized,
            "NOT_FINALIZED"
        );

        post.postdata.data.decryptedData = decryptedData;
        post.postdata.settings.status = Structures.PostStatus.Revealed;

        emit MadePublic(post);

        return decryptedData;
    }
}

contracts/modules/TokenManager.sol.content:
pragma solidity 0.8.19;

import "./BurnDAI.sol";

abstract contract TokenManager is BurnDAI {
    function _getTokenAddress(
        Structures.Tokens tokenID
    ) internal view returns (address) {
        if (tokenID == Structures.Tokens.DAI) return BurnDAI.getTokenAddress();
        if (tokenID == Structures.Tokens.MUSE)
            return BurnMUSE.getTokenAddress();
        return address(0);
    }

    function _checkAllowance(
        Structures.Tokens tokenID,
        address from,
        address to,
        uint256 value
    ) internal view {
        address tokenAddress = _getTokenAddress(tokenID);
        require(
            IERC20(tokenAddress).allowance(from, to) >= value,
            "INSUFFICIENT_ALLOWANCE"
        );
    }

    modifier onlyValidTokenID(Structures.Tokens tokenID) {
        require(
            tokenID == Structures.Tokens.MUSE ||
                tokenID == Structures.Tokens.DAI ||
                tokenID == Structures.Tokens.NaN,
            "WRONG_TOKEN"
        );
        _;
    }

    function isValidTokenID(
        Structures.Tokens tokenID
    ) internal pure returns (bool validity) {
        return
            tokenID == Structures.Tokens.MUSE ||
            tokenID == Structures.Tokens.DAI;
    }

    function _transfer(
        Structures.Tokens tokenID,
        address to,
        uint256 value
    ) internal onlyValidTokenID(tokenID) {
        require(
            IERC20(_getTokenAddress(tokenID)).transfer(to, value),
            "TRANSFER_FAILED"
        );
    }

    function _transferFrom(
        Structures.Tokens tokenID,
        address from,
        address to,
        uint256 value
    ) internal onlyValidTokenID(tokenID) {
        address tokenAddress = _getTokenAddress(tokenID);
        _checkAllowance(tokenID, from, to, value);
        require(
            IERC20(tokenAddress).transferFrom(from, to, value),
            "TRANSFER_FAILED"
        );
    }

    function _burn(
        Structures.Tokens tokenID,
        address _receiver,
        uint256 value
    ) internal onlyValidTokenID(tokenID) {
        address treasury = IMecenateFeedFactory(settings.factoryContract)
            .treasuryContract();

        IMecenateFeedFactory factory = IMecenateFeedFactory(
            settings.factoryContract
        );

        if (tokenID == Structures.Tokens.NaN) {
            (bool result, ) = payable(_receiver).call{value: value}("");
            require(result, "Transfer failed.");
            return;
        }

        address tokenAddress = (tokenID == Structures.Tokens.DAI)
            ? BurnDAI.getTokenAddress()
            : BurnMUSE.getTokenAddress();

        if (factory.burnEnabled() == false) {
            IERC20(tokenAddress).transfer(treasury, value);
            return;
        }

        if (tokenID == Structures.Tokens.DAI) {
            BurnDAI._burnDai(value);
        } else if (tokenID == Structures.Tokens.MUSE) {
            BurnMUSE._burn(value);
        }
    }

    function _burnFrom(
        Structures.Tokens tokenID,
        address from,
        uint256 value
    ) internal onlyValidTokenID(tokenID) {
        if (tokenID == Structures.Tokens.DAI) {
            BurnDAI._burnFrom(from, value);
        } else if (tokenID == Structures.Tokens.MUSE) {
            BurnMUSE._burnFrom(from, value);
        }
    }

    function _approve(
        Structures.Tokens tokenID,
        address spender,
        uint256 value
    ) internal onlyValidTokenID(tokenID) {
        if (tokenID == Structures.Tokens.DAI) {
            require(
                IERC20(BurnDAI.getTokenAddress()).approve(spender, value),
                "APPROVE_FAILED"
            );
        } else if (tokenID == Structures.Tokens.MUSE) {
            address MUSE = BurnMUSE.getTokenAddress();
            uint256 currentAllowance = IMUSE(MUSE).allowance(
                msg.sender,
                spender
            );

            uint256 newAllowance = currentAllowance + value;
            require(
                IMUSE(MUSE).increaseAllowance(spender, newAllowance),
                "APPROVE_FAILED"
            );
        }
    }

    function totalSupply(
        Structures.Tokens tokenID
    ) internal view onlyValidTokenID(tokenID) returns (uint256 value) {
        return IERC20(_getTokenAddress(tokenID)).totalSupply();
    }

    function balanceOf(
        Structures.Tokens tokenID,
        address who
    ) internal view onlyValidTokenID(tokenID) returns (uint256 value) {
        return IERC20(_getTokenAddress(tokenID)).balanceOf(who);
    }

    function allowance(
        Structures.Tokens tokenID,
        address owner,
        address spender
    ) internal view onlyValidTokenID(tokenID) returns (uint256 value) {
        return IERC20(_getTokenAddress(tokenID)).allowance(owner, spender);
    }
}

