import "./BurnMUSE.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

abstract contract BurnDAI is BurnMUSE {
    using SafeMath for uint256;

    function _burnFrom(address from, uint256 value) internal override {
        IERC20(IMecenateFeedFactory(settings.factoryContract).daiToken())
            .transferFrom(from, address(this), value);

        _burn(value);
    }

    function _burnDai(uint256 value) internal {
        // approve uniswap for token transfer
        IERC20(IMecenateFeedFactory(settings.factoryContract).daiToken())
            .approve(settings.router, value);

        // swap dai for MUSE
        uint256 tokens_sold = value;

        uint256 tokens_bought = _swapTokensForToken(
            getTokenAddress(),
            IMecenateFeedFactory(settings.factoryContract).wethToken(),
            uint24(
                IMecenateFeedFactory(settings.factoryContract).getRouterFee(0)
            ),
            tokens_sold
        );

        uint256 tokens_sold_to_muse = _swapTokensForToken(
            IMecenateFeedFactory(settings.factoryContract).wethToken(),
            BurnMUSE.getTokenAddress(),
            uint24(
                IMecenateFeedFactory(settings.factoryContract).getRouterFee(1)
            ),
            tokens_bought
        );

        // burn MUSE
        BurnMUSE._burn(tokens_sold_to_muse);
    }

    function getTokenAddress()
        internal
        view
        virtual
        override
        returns (address token)
    {
        return IMecenateFeedFactory(settings.factoryContract).daiToken();
    }

    function getExchangeAddress()
        internal
        view
        override
        returns (address exchange)
    {
        exchange = settings.router;
    }

    function _swapTokensForToken(
        address tokenIn,
        address tokenOut,
        uint24 fee,
        uint256 amountIn
    ) internal returns (uint256 amountOut) {
        // Check and approve allowance
        uint256 allowance = IERC20(tokenIn).allowance(
            address(this),
            settings.router
        );
        if (allowance < amountIn) {
            IERC20(tokenIn).approve(settings.router, type(uint256).max);
        }

        // Prepare parameters
        address recipient = address(this);
        uint256 deadline = block.timestamp + 15; // 15 seconds from the current block timestamp

        // Perform the swap
        // Refer to the Uniswap V3 documentation for details on the params.
        amountOut = ISwapRouter(settings.router).exactInputSingle(
            ISwapRouter.ExactInputSingleParams({
                tokenIn: tokenIn,
                tokenOut: tokenOut,
                fee: fee,
                recipient: recipient,
                deadline: deadline,
                amountIn: amountIn,
                amountOutMinimum: 0, // you may want to set a minimum here
                sqrtPriceLimitX96: 0
            })
        );

        return amountOut;
    }
}
","keccak256":"0xb0fb02f2fa8b4c5113c701bcdbe359e9cd053e94b247d5ecb1084d25abdcc51d"},"contracts/modules/BurnMUSE.sol":{"content":"pragma solidity 0.8.19;
import "../interfaces/IMUSE.sol";
import "../interfaces/IMecenateFeedFactory.sol";
import "./Events.sol";

abstract contract BurnMUSE is Events {
    function _burn(uint256 value) internal virtual {
        require(
            IMUSE(IMecenateFeedFactory(settings.factoryContract).museToken())
                .burn(value),
            "muse burn failed"
        );
    }

    function _burnFrom(address from, uint256 value) internal virtual {
        IMUSE(IMecenateFeedFactory(settings.factoryContract).museToken())
            .transferFrom(from, address(this), value);
        _burn(value);
    }

    function getTokenAddress() internal view virtual returns (address token) {
        return IMecenateFeedFactory(settings.factoryContract).museToken();
    }

    function getExchangeAddress()
        internal
        view
        virtual
        returns (address exchange)
    {
        exchange = IMecenateFeedFactory(settings.factoryContract).router();
    }
}
","keccak256":"0xe7ea0223ae255a9fcc54d0e7e67111067e02726f2ea633ef52f647195e24f233"},"contracts/modules/Data.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "../interfaces/IMecenateFeed.sol";
import "../interfaces/IMecenateUsers.sol";
import "../interfaces/IMecenateTreasury.sol";
import "../interfaces/IMecenateFeedFactory.sol";
import "../interfaces/IMecenateVerifier.sol";

// import openzeppelin ECDSA
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

/**
 * @title Data
 * @dev This contract stores data related to Mecenate posts and provides functions to interact with it.
 */
contract Data {
    bytes internal constant ZEROHASH = "0x00";

    bytes32 public owner;

    Structures.Post public post;
    Structures.PostSettingPrivate internal postSettingPrivate;
    Structures.FeedSettings internal settings;

    mapping(uint8 => uint256) internal postDurationToDays;
    mapping(uint8 => bool) internal validStatuses;

    function onlyVault() internal view {
        require(msg.sender == settings.vaultContract, "ONLY_VAULT");
    }

    constructor(
        address _usersModuleContract,
        address _verifierContract,
        address _vaultContract,
        address _factoryContract,
        string memory _version
    ) {
        settings.punishmentRatio = 100000000000000000; // Constant value
        settings.postCount = 0; // Initialize postCount to 0
        settings.usersModuleContract = _usersModuleContract;
        settings.verifierContract = _verifierContract;
        settings.vaultContract = _vaultContract;
        settings.factoryContract = msg.sender;
        settings.router = IMecenateFeedFactory(_factoryContract).router();
        settings.encodedSymKey = ZEROHASH;
        settings.lastMessageForBuyer = ZEROHASH;
        settings.lastMessageForSeller = ZEROHASH;
        post.postdata.settings.status = Structures.PostStatus.Waiting;

        postDurationToDays[uint8(Structures.PostDuration.OneDay)] = 1 days;
        postDurationToDays[uint8(Structures.PostDuration.ThreeDays)] = 3 days;
        postDurationToDays[uint8(Structures.PostDuration.OneWeek)] = 7 days;
        postDurationToDays[uint8(Structures.PostDuration.TwoWeeks)] = 14 days;
        postDurationToDays[uint8(Structures.PostDuration.OneMonth)] = 30 days;
        validStatuses[uint8(Structures.PostStatus.Waiting)] = true;

        settings.version = _version;
    }

    function version() external view returns (string memory) {
        return settings.version;
    }

    function _changeStatus(Structures.PostStatus _newStatus) internal {
        validStatuses[uint8(post.postdata.settings.status)] = false;
        validStatuses[uint8(_newStatus)] = true;
        post.postdata.settings.status = _newStatus;
    }

    function _verifyNonce(
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) internal view returns (bytes memory, uint256, uint256, bytes memory) {
        (
            bytes memory vaultId,
            uint256 twitterId,
            uint256 telegramId,
            bytes memory signedMessage
        ) = IMecenateVerifier(settings.verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        (, bytes32 nonce) = abi.decode(signedMessage, (address, bytes32));

        require(_nonce == nonce, "WRONG_NONCE");

        return (vaultId, twitterId, telegramId, signedMessage);
    }

    function sismoVerify(
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) internal view returns (bytes memory, uint256, uint256, bytes memory) {
        (
            bytes memory vaultId,
            uint256 twitterId,
            uint256 telegramId,
            bytes memory signedMessage
        ) = IMecenateVerifier(settings.verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        return (vaultId, twitterId, telegramId, signedMessage);
    }

    function getStatus() external view returns (Structures.PostStatus) {
        return post.postdata.settings.status;
    }

    function getTokenId() external view returns (Structures.Tokens) {
        return post.postdata.settings.tokenId;
    }

    function getTokenIdAddress() public view returns (address) {
        if (post.postdata.settings.tokenId == Structures.Tokens.MUSE) {
            return IMecenateFeedFactory(settings.factoryContract).museToken();
        } else if (post.postdata.settings.tokenId == Structures.Tokens.DAI) {
            return IMecenateFeedFactory(settings.factoryContract).daiToken();
        } else {
            return address(0);
        }
    }

    function _checkToken(Structures.Tokens _token) internal view {
        require(_token == post.postdata.settings.tokenId, "WRONG_TOKEN");
    }

    function getPaymentRequested() external view returns (uint256) {
        return post.postdata.escrow.payment;
    }

    function getStakeRequested() external view returns (uint256) {
        return post.postdata.escrow.stake;
    }

    function postCount() external view returns (uint256) {
        return settings.postCount;
    }

    receive() external payable {}
}
","keccak256":"0x7a0bfb25dd716d30d21f82c08a10ffefa72c82449f43c6fb64903c604b790806","license":"MIT"},"contracts/modules/Deposit.sol":{"content":"pragma solidity 0.8.19;
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./TokenManager.sol";

contract Deposit {
    using SafeMath for uint256;

    mapping(uint256 => mapping(bytes32 => uint256)) private _deposit;

    event DepositIncreased(
        Structures.Tokens tokenID,
        bytes32 user,
        uint256 amount,
        uint256 newDeposit
    );
    event DepositDecreased(
        Structures.Tokens tokenID,
        bytes32 user,
        uint256 amount,
        uint256 newDeposit
    );

    function _increaseDeposit(
        Structures.Tokens tokenID,
        bytes32 user,
        uint256 amountToAdd
    ) internal returns (uint256 newDeposit) {
        // calculate new deposit amount
        newDeposit = _deposit[uint256(tokenID)][user].add(amountToAdd);

        // set new stake to storage
        _deposit[uint256(tokenID)][user] = newDeposit;

        // emit event
        emit DepositIncreased(tokenID, user, amountToAdd, newDeposit);

        // return
        return newDeposit;
    }

    function _decreaseDeposit(
        Structures.Tokens tokenID,
        bytes32 user,
        uint256 amountToRemove
    ) internal returns (uint256 newDeposit) {
        // get current deposit
        uint256 currentDeposit = _deposit[uint256(tokenID)][user];

        // check if sufficient deposit
        require(currentDeposit >= amountToRemove, "INSUFFICIENT_DEPOSIT");

        // calculate new deposit amount
        newDeposit = currentDeposit.sub(amountToRemove);

        // set new stake to storage
        _deposit[uint256(tokenID)][user] = newDeposit;

        // emit event
        emit DepositDecreased(tokenID, user, amountToRemove, newDeposit);

        // return
        return newDeposit;
    }

    function _clearDeposit(
        Structures.Tokens tokenID,
        bytes32 user
    ) internal returns (uint256 amountRemoved) {
        // get current deposit
        uint256 currentDeposit = _deposit[uint256(tokenID)][user];

        // remove deposit
        _decreaseDeposit(tokenID, user, currentDeposit);

        // return
        return currentDeposit;
    }

    function _getDeposit(
        Structures.Tokens tokenID,
        bytes32 user
    ) internal view returns (uint256 deposit) {
        return _deposit[uint256(tokenID)][user];
    }
}
","keccak256":"0x702ea7645f1788873cddc980c582ce93152342e111141fde085b9854df5c7410"},"contracts/modules/Events.sol":{"content":"/**
 * @title Events
 * @dev This abstract contract defines the events related to the Mecenate platform's post lifecycle.
 */
pragma solidity 0.8.19;

import "./Data.sol";

abstract contract Events is Data {
    event Created(Structures.Post post);
    event Accepted(Structures.Post post);
    event Valid(Structures.Post post);
    event Invalid(Structures.Post post);
    event Finalized(Structures.Post post);
    event MadePublic(Structures.Post post);
    event Renounced(Structures.Post post);
}
","keccak256":"0x3aeaf067b1f9010a2456df46fb54d4727b3a24082b1446ac9266dbcdd8c838f6"},"contracts/modules/Staking.sol":{"content":"/**
 * @title Staking
 * @dev This contract handles staking functionality for the Mecenate platform.
 * It allows users to add, take, and burn stakes, and provides functions to get the total staked amount and individual stake amounts.
 * It also includes a modifier to check the status of a post before allowing stake-related transactions.
 */
pragma solidity 0.8.19;

import "./Deposit.sol";
import "./Events.sol";

abstract contract Staking is Events, Deposit, TokenManager {
    using SafeMath for uint256;

    event StakeBurned(bytes32 staker, uint256 amount);
    event StakeTaken(bytes32 staker, uint256 amount, Structures.Tokens tokenId);
    event StakeAdded(bytes32 staker, uint256 amount, Structures.Tokens tokenId);

    function _addStake(
        Structures.Tokens tokenId,
        bytes32 staker,
        address funder,
        uint256 amountToAdd
    ) internal returns (uint256 newStake) {
        Structures.PostStatus currentStatus = post.postdata.settings.status;
        require(
            currentStatus != Structures.PostStatus.Accepted &&
                currentStatus != Structures.PostStatus.Submitted,
            "INVALID_STATUS"
        );

        require(amountToAdd > 0, "STAKE_REQUIRED");

        if (tokenId != Structures.Tokens.NaN) {
            TokenManager._transferFrom(
                tokenId,
                funder,
                address(this),
                amountToAdd
            );
        }

        newStake = Deposit._increaseDeposit(tokenId, staker, amountToAdd);

        emit StakeAdded(staker, amountToAdd, tokenId);

        return newStake;
    }

    function _takeStake(
        Structures.Tokens tokenId,
        bytes32 staker,
        address _to,
        uint256 amountToTake
    ) internal returns (uint256 newStake) {
        // Memorizza lo status del post in una variabile per evitare accessi ridondanti allo storage
        Structures.PostStatus currentStatus = post.postdata.settings.status;

        // Verifica che lo status del post sia valido per procedere
        require(
            currentStatus != Structures.PostStatus.Accepted &&
                currentStatus != Structures.PostStatus.Submitted,
            "INVALID_STATUS"
        );

        // Effettua il trasferimento del token o dell'Ether
        if (tokenId == Structures.Tokens.NaN) {
            //(bool result, ) = payable(_to).call{value: amountToTake}("");
            //require(result, "CALL_FAILED");
            payable(_to).transfer(amountToTake);
        } else {
            TokenManager._transfer(tokenId, _to, amountToTake);
        }

        // Aggiorna il deposito e emette un evento
        newStake = Deposit._decreaseDeposit(tokenId, staker, amountToTake);
        emit StakeTaken(staker, amountToTake, tokenId);

        return newStake;
    }

    function _takeFullStake(
        Structures.Tokens tokenId,
        address _to,
        bytes32 staker
    ) internal returns (uint256 amountTaken) {
        uint256 currentDeposit = Deposit._getDeposit(tokenId, staker);

        uint256 newStake = _takeStake(tokenId, staker, _to, currentDeposit);

        return newStake;
    }

    function _burnStake(
        Structures.Tokens tokenId,
        bytes32 staker,
        uint256 amountToBurn
    ) internal returns (uint256 newStake) {
        uint256 newDeposit = Deposit._decreaseDeposit(
            tokenId,
            staker,
            amountToBurn
        );

        if (tokenId == Structures.Tokens.NaN) {
            (bool result, ) = payable(
                IMecenateFeedFactory(settings.factoryContract)
                    .treasuryContract()
            ).call{value: amountToBurn}("");
            require(result, "CALL_FAILED");
        } else if (tokenId == Structures.Tokens.DAI) {
            BurnDAI._burnDai(amountToBurn);
        } else if (tokenId == Structures.Tokens.MUSE) {
            BurnMUSE._burn(amountToBurn);
        }

        emit StakeBurned(staker, amountToBurn);

        return newDeposit;
    }

    function _burnFullStake(
        Structures.Tokens tokenId,
        bytes32 staker
    ) internal returns (uint256 amountBurned) {
        uint256 currentDeposit = Deposit._getDeposit(tokenId, staker);

        _burnStake(tokenId, staker, currentDeposit);

        return currentDeposit;
    }

    function getStake(
        Structures.Tokens tokenId,
        bytes32 staker
    ) external view returns (uint256 amount) {
        // get deposit
        amount = Deposit._getDeposit(tokenId, staker);
        // explicit return
        return amount;
    }

    function getTotalStaked() external view returns (uint256) {
        uint256 amountSeller = Deposit._getDeposit(
            post.postdata.settings.tokenId,
            keccak256(postSettingPrivate.vaultIdSeller)
        );

        uint256 amountBuyer = Deposit._getDeposit(
            post.postdata.settings.tokenId,
            keccak256(postSettingPrivate.vaultIdBuyer)
        );

        return (amountSeller + amountBuyer);
    }

    function addStake(
        Structures.Tokens tokenId,
        uint256 amountToAdd,
        bytes32 encryptedVaultId
    ) external payable returns (uint256) {
        require(tokenId == post.postdata.settings.tokenId, "WRONG_TOKEN");

        // Check if the encryptedVaultId matches with either the buyer or the seller
        require(
            encryptedVaultId == keccak256(postSettingPrivate.vaultIdBuyer) ||
                encryptedVaultId == keccak256(postSettingPrivate.vaultIdSeller),
            "VAULTID_MISMATCH"
        );

        // Determine the amount to add based on the role (buyer or seller)
        uint256 actualAmountToAdd = (encryptedVaultId ==
            keccak256(postSettingPrivate.vaultIdSeller))
            ? msg.value
            : amountToAdd;

        // Update the stake
        uint256 newStake = _addStake(
            tokenId,
            encryptedVaultId,
            settings.vaultContract,
            actualAmountToAdd
        );

        // Update the corresponding escrow value based on the role
        if (encryptedVaultId == keccak256(postSettingPrivate.vaultIdBuyer)) {
            post.postdata.escrow.payment = newStake;
        } else {
            post.postdata.escrow.stake = newStake;
        }

        return newStake;
    }

    function takeStake(
        Structures.Tokens tokenId,
        uint256 amountToTake,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) external returns (uint256) {
        onlyVault();

        require(tokenId == post.postdata.settings.tokenId, "WRONG_TOKEN");

        bytes32 encryptedVaultId = _commonTakeStake(
            sismoConnectResponse,
            _to,
            _nonce
        );

        uint256 currentDeposit = Deposit._getDeposit(tokenId, encryptedVaultId);
        require(currentDeposit >= amountToTake, "NOT_ENOUGH_STAKE");

        uint256 newBalance = _takeStake(
            tokenId,
            encryptedVaultId,
            _to,
            amountToTake
        );

        if (encryptedVaultId == keccak256(postSettingPrivate.vaultIdBuyer)) {
            post.postdata.escrow.payment = newBalance;
        } else {
            post.postdata.escrow.stake = newBalance;
        }

        return newBalance;
    }

    function takeFullStake(
        Structures.Tokens tokenId,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) external returns (uint256) {
        onlyVault();

        require(tokenId == post.postdata.settings.tokenId, "WRONG_TOKEN");

        bytes32 encryptedVaultId = _commonTakeStake(
            sismoConnectResponse,
            _to,
            _nonce
        );

        uint256 newBalance = _takeFullStake(tokenId, _to, encryptedVaultId);

        if (encryptedVaultId == keccak256(postSettingPrivate.vaultIdBuyer)) {
            post.postdata.escrow.payment = newBalance;
        } else {
            post.postdata.escrow.stake = newBalance;
        }

        return newBalance;
    }

    function getSellerStake() external view returns (uint256 amount) {
        // get deposit
        amount = Deposit._getDeposit(
            post.postdata.settings.tokenId,
            keccak256(postSettingPrivate.vaultIdSeller)
        );
        // explicit return
        return amount;
    }

    function getBuyerStake() external view returns (uint256 amount) {
        // get deposit
        amount = Deposit._getDeposit(
            post.postdata.settings.tokenId,
            keccak256(postSettingPrivate.vaultIdBuyer)
        );
        // explicit return
        return amount;
    }

    function _commonTakeStake(
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) internal view returns (bytes32) {
        (bytes memory vaultId, , , ) = _verifyNonce(
            sismoConnectResponse,
            _to,
            _nonce
        );
        bytes32 encryptedVaultId = keccak256(vaultId);

        require(
            encryptedVaultId == keccak256(postSettingPrivate.vaultIdBuyer) ||
                encryptedVaultId == keccak256(postSettingPrivate.vaultIdSeller),
            "UNAUTHORIZED"
        );

        return encryptedVaultId;
    }
}
","keccak256":"0x18db8d86d1cc124574f7d4aafd684f32e4c11f95d2661469551177212c372e5a"},"contracts/modules/TokenManager.sol":{"content":"pragma solidity 0.8.19;

import "./BurnDAI.sol";

abstract contract TokenManager is BurnDAI {
    function _getTokenAddress(
        Structures.Tokens tokenID
    ) internal view returns (address) {
        if (tokenID == Structures.Tokens.DAI) return BurnDAI.getTokenAddress();
        if (tokenID == Structures.Tokens.MUSE)
            return BurnMUSE.getTokenAddress();
        return address(0);
    }

    function _checkAllowance(
        Structures.Tokens tokenID,
        address from,
        address to,
        uint256 value
    ) internal view {
        address tokenAddress = _getTokenAddress(tokenID);
        require(
            IERC20(tokenAddress).allowance(from, to) >= value,
            "INSUFFICIENT_ALLOWANCE"
        );
    }

    modifier onlyValidTokenID(Structures.Tokens tokenID) {
        require(
            tokenID == Structures.Tokens.MUSE ||
                tokenID == Structures.Tokens.DAI ||
                tokenID == Structures.Tokens.NaN,
            "WRONG_TOKEN"
        );
        _;
    }

    function isValidTokenID(
        Structures.Tokens tokenID
    ) internal pure returns (bool validity) {
        return
            tokenID == Structures.Tokens.MUSE ||
            tokenID == Structures.Tokens.DAI;
    }

    function _transfer(
        Structures.Tokens tokenID,
        address to,
        uint256 value
    ) internal onlyValidTokenID(tokenID) {
        require(
            IERC20(_getTokenAddress(tokenID)).transfer(to, value),
            "TRANSFER_FAILED"
        );
    }

    function _transferFrom(
        Structures.Tokens tokenID,
        address from,
        address to,
        uint256 value
    ) internal onlyValidTokenID(tokenID) {
        address tokenAddress = _getTokenAddress(tokenID);
        _checkAllowance(tokenID, from, to, value);
        require(
            IERC20(tokenAddress).transferFrom(from, to, value),
            "TRANSFER_FAILED"
        );
    }

    function _burn(
        Structures.Tokens tokenID,
        address _receiver,
        uint256 value
    ) internal onlyValidTokenID(tokenID) {
        address treasury = IMecenateFeedFactory(settings.factoryContract)
            .treasuryContract();

        IMecenateFeedFactory factory = IMecenateFeedFactory(
            settings.factoryContract
        );

        if (tokenID == Structures.Tokens.NaN) {
            (bool result, ) = payable(_receiver).call{value: value}("");
            require(result, "Transfer failed.");
            return;
        }

        address tokenAddress = (tokenID == Structures.Tokens.DAI)
            ? BurnDAI.getTokenAddress()
            : BurnMUSE.getTokenAddress();

        if (factory.burnEnabled() == false) {
            IERC20(tokenAddress).transfer(treasury, value);
            return;
        }

        if (tokenID == Structures.Tokens.DAI) {
            BurnDAI._burnDai(value);
        } else if (tokenID == Structures.Tokens.MUSE) {
            BurnMUSE._burn(value);
        }
    }

    function _burnFrom(
        Structures.Tokens tokenID,
        address from,
        uint256 value
    ) internal onlyValidTokenID(tokenID) {
        if (tokenID == Structures.Tokens.DAI) {
            BurnDAI._burnFrom(from, value);
        } else if (tokenID == Structures.Tokens.MUSE) {
            BurnMUSE._burnFrom(from, value);
        }
    }

    function _approve(
        Structures.Tokens tokenID,
        address spender,
        uint256 value
    ) internal onlyValidTokenID(tokenID) {
        if (tokenID == Structures.Tokens.DAI) {
            require(
                IERC20(BurnDAI.getTokenAddress()).approve(spender, value),
                "APPROVE_FAILED"
            );
        } else if (tokenID == Structures.Tokens.MUSE) {
            address MUSE = BurnMUSE.getTokenAddress();
            uint256 currentAllowance = IMUSE(MUSE).allowance(
                msg.sender,
                spender
            );

            uint256 newAllowance = currentAllowance + value;
            require(
                IMUSE(MUSE).increaseAllowance(spender, newAllowance),
                "APPROVE_FAILED"
            );
        }
    }

    function totalSupply(
        Structures.Tokens tokenID
    ) internal view onlyValidTokenID(tokenID) returns (uint256 value) {
        return IERC20(_getTokenAddress(tokenID)).totalSupply();
    }

    function balanceOf(
        Structures.Tokens tokenID,
        address who
    ) internal view onlyValidTokenID(tokenID) returns (uint256 value) {
        return IERC20(_getTokenAddress(tokenID)).balanceOf(who);
    }

    function allowance(
        Structures.Tokens tokenID,
        address owner,
        address spender
    ) internal view onlyValidTokenID(tokenID) returns (uint256 value) {
        return IERC20(_getTokenAddress(tokenID)).allowance(owner, spender);
    }
}
