    using SafeERC20 for IERC20;

    Structures.BayRequest[] public allRequests;

    Structures.BayRequestPrivate[] public allRequestsPrivate;

    address public usersMouduleContract;

    address public verifierContract;

    address public vaultContract;

    address public museToken;

    address public daiToken;

    mapping(bytes32 => Structures.BayRequest[]) public requests;

    uint256 public contractCounter;

    mapping(uint256 => bytes) private sismoResponseMapping;

    event RequestCreated(
        bytes32 indexed user,
        Structures.BayRequest,
        uint256 indexed index
    );

    event RequestAccepted(
        bytes32 indexed user,
        Structures.BayRequest,
        uint256 indexed index
    );

    constructor(
        address _usersMouduleContract,
        address _verifierContract,
        address _vaultContract
    ) {
        usersMouduleContract = _usersMouduleContract;
        verifierContract = _verifierContract;
        vaultContract = _vaultContract;
    }

    function changeMuseToken(address _museToken) external onlyOwner {
        museToken = _museToken;
    }

    function changeDaiToken(address _daiToken) external onlyOwner {
        daiToken = _daiToken;
    }

    function createRequest(
        Structures.BayRequest memory request,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) public payable returns (Structures.BayRequest memory) {
        if (request.tokenId == Structures.Tokens.NaN) {
            require(msg.value > 0, "BAY:payment is not enough");
        } else if (request.tokenId == Structures.Tokens.DAI) {
            IERC20(daiToken).safeTransferFrom(
                msg.sender,
                address(this),
                request.payment
            );
        } else if (request.tokenId == Structures.Tokens.MUSE) {
            IERC20(museToken).safeTransferFrom(
                msg.sender,
                address(this),
                request.payment
            );
        }

        require(request.payment > 0, "BAY:payment is not enough");

        (bytes memory vaultId, , , ) = _sismoVerify(
            sismoConnectResponse,
            _to,
            _nonce
        );

        bytes32 encryptedVaultId = keccak256(vaultId);

        require(
            IMecenateUsers(usersMouduleContract).checkifUserExist(
                keccak256(vaultId)
            ),
            "user does not exist"
        );

        require(request.stake > 0, "BAY:stake is not enough");

        require(request.payment == msg.value, "BAY:payment is not enough");

        require(request.payment > 0, "BAY:payment is not enough");

        requests[encryptedVaultId].push(request);

        allRequests.push(request);

        allRequestsPrivate.push(
            Structures.BayRequestPrivate({
                vaultIdSeller: "0x00",
                sellerResponse: "0x00",
                vaultIdBuyer: vaultId,
                buyerResponse: sismoConnectResponse,
                buyerTo: _to,
                buyerNonce: _nonce
            })
        );

        contractCounter++;

        emit RequestCreated(encryptedVaultId, request, allRequests.length - 1);
    }

    function acceptRequest(
        uint256 index,
        address _feed,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) public {
        (bytes memory vaultId, , , ) = _sismoVerify(
            sismoConnectResponse,
            _to,
            _nonce
        );

        bytes32 encryptedVaultId = keccak256(vaultId);

        Structures.Feed memory feed = _getFeedInfo(_feed);

        require(
            allRequests[index].payment >= feed.paymentRequested,
            "BAY:payment is not the same of the feed"
        );

        require(
            feed.stakeRequested >= allRequests[index].stake,
            "BAY:stake is not the same of the feed"
        );

        require(
            IMecenateUsers(usersMouduleContract).checkifUserExist(
                encryptedVaultId
            ),
            "BAY:user does not exist"
        );

        allRequestsPrivate.push(
            Structures.BayRequestPrivate({
                vaultIdSeller: vaultId,
                sellerResponse: sismoConnectResponse,
                vaultIdBuyer: allRequestsPrivate[index].vaultIdBuyer,
                buyerResponse: allRequestsPrivate[index].buyerResponse,
                buyerTo: allRequestsPrivate[index].buyerTo,
                buyerNonce: allRequestsPrivate[index].buyerNonce
            })
        );

        if (allRequests[index].tokenId != Structures.Tokens.NaN) {
            if (allRequests[index].tokenId == Structures.Tokens.DAI) {
                IERC20(daiToken).approve(
                    msg.sender,
                    allRequests[index].payment
                );
            } else if (allRequests[index].tokenId == Structures.Tokens.MUSE) {
                IERC20(museToken).approve(
                    msg.sender,
                    allRequests[index].payment
                );
            }

            IMecenateFeed(_feed).acceptPost{value: 0}(
                allRequestsPrivate[index].buyerResponse,
                allRequestsPrivate[index].buyerTo,
                allRequestsPrivate[index].buyerNonce,
                allRequests[index].tokenId,
                allRequests[index].payment
            );
        } else {
            IMecenateFeed(_feed).acceptPost{value: allRequests[index].payment}(
                allRequestsPrivate[index].buyerResponse,
                allRequestsPrivate[index].buyerTo,
                allRequestsPrivate[index].buyerNonce,
                allRequests[index].tokenId,
                allRequests[index].payment
            );
        }

        allRequests[index].accepted = true;

        allRequests[index].postAddress = _feed;

        allRequests[index].postCount = feed.postCount;

        emit RequestAccepted(encryptedVaultId, allRequests[index], index);
    }

    function _sismoVerify(
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) internal view returns (bytes memory, uint256, uint256, bytes memory) {
        (
            bytes memory vaultId,
            uint256 twitterId,
            uint256 telegramId,
            bytes memory signedMessage
        ) = IMecenateVerifier(verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        (address to, bytes32 nonce) = abi.decode(
            signedMessage,
            (address, bytes32)
        );

        require(_nonce == nonce, "Not Same Nonce");

        return (vaultId, twitterId, telegramId, signedMessage);
    }

    function getRequests()
        public
        view
        returns (Structures.BayRequest[] memory)
    {
        return allRequests;
    }

    function getRequestForAddress(
        bytes32 _user
    ) public view returns (Structures.BayRequest[] memory) {
        return requests[_user];
    }

    // removéthe request and refund the user delete the array and move the last element to the index
    function removeRequest(
        uint256 index,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) public {
        (
            bytes memory vaultId,
            uint256 twitterId,
            uint256 telegramId,
            bytes memory signedMessage
        ) = _sismoVerify(sismoConnectResponse, _to, _nonce);

        bytes32 encryptedVaultId = keccak256(vaultId);

        require(
            encryptedVaultId ==
                keccak256(allRequestsPrivate[index].vaultIdBuyer),
            "BAY:you are not the buyer"
        );

        require(
            allRequests[index].accepted == false,
            "BAY:request is already accepted"
        );

        Structures.BayRequest memory requestToRemove = allRequests[index];

        //  send eth with data to the vaultctx
        (bool _result, ) = vaultContract.call{value: requestToRemove.payment}(
            sismoConnectResponse
        );

        require(_result, "BAY:Vault call failed");

        // Remove from allRequests array
        uint256 lastIndex = allRequests.length - 1;
        if (index < lastIndex) {
            allRequests[index] = allRequests[lastIndex];
        }
        allRequests.pop();

        // Remove from allRequestsPrivate array
        uint256 lastIndexPrivate = allRequestsPrivate.length - 1;
        if (index < lastIndexPrivate) {
            allRequestsPrivate[index] = allRequestsPrivate[lastIndexPrivate];
        }
        allRequestsPrivate.pop();

        // Remove from requests mapping
        for (uint256 i = 0; i < requests[encryptedVaultId].length; i++) {
            if (
                requests[encryptedVaultId][i].payment ==
                requestToRemove.payment &&
                requests[encryptedVaultId][i].stake == requestToRemove.stake &&
                requests[encryptedVaultId][i].postAddress ==
                requestToRemove.postAddress &&
                requests[encryptedVaultId][i].postCount ==
                requestToRemove.postCount
            ) {
                uint256 lastIndexSender = requests[encryptedVaultId].length - 1;
                if (i < lastIndexSender) {
                    requests[encryptedVaultId][i] = requests[encryptedVaultId][
                        lastIndexSender
                    ];
                }
                requests[encryptedVaultId].pop();
                break;
            }
        }
    }

    function changeVault(address _vault) external onlyOwner {
        vaultContract = _vault;
    }

    function changeVerifier(address _verifier) external onlyOwner {
        verifierContract = _verifier;
    }

    function changeUsersModule(address _usersModule) external onlyOwner {
        usersMouduleContract = _usersModule;
    }

    receive() external payable {}
}

contracts/library/Structures.sol.content:
/**
 * @title Structures
 * @dev This library defines various data structures used in the Mecenate platform.
 */
pragma solidity 0.8.19;

library Structures {
    enum PostStatus {
        Waiting,
        Proposed,
        Accepted,
        Submitted,
        Finalized,
        Punished,
        Revealed,
        Renounced
    }
    enum Tokens {
        NaN,
        MUSE,
        DAI
    }
    enum PostType {
        Text,
        Image,
        Video,
        Audio,
        File
    }
    enum PostDuration {
        OneDay,
        ThreeDays,
        OneWeek,
        TwoWeeks,
        OneMonth
    }

    struct FeedSettings {
        uint256 punishmentRatio;
        uint256 postCount;
        address usersModuleContract;
        address factoryContract;
        address verifierContract;
        address vaultContract;
        address router;
        bytes encodedSymKey;
        bytes lastMessageForBuyer;
        bytes lastMessageForSeller;
        string version;
    }

    struct FactorySettings {
        address treasuryContract;
        address usersModuleContract;
        address verifierContract;
        address vaultContract;
        address museToken;
        address daiToken;
        address wethToken;
        address router;
    }

    /**
     * @dev Struct representing a post on the Mecenate platform.
     */
    struct Post {
        User creator;
        PostData postdata;
    }

    /**
     * @dev Struct representing the data of a post on the Mecenate platform.
     */
    struct PostData {
        PostSettings settings;
        PostEscrow escrow;
        PostEncryptedData data;
    }

    /**
     * @dev Struct representing the encrypted data of a post on the Mecenate platform.
     */
    struct PostEncryptedData {
        bytes encryptedData;
        bytes encryptedKey;
        bytes decryptedData;
    }

    /**
     * @dev Struct representing the settings of a post on the Mecenate platform.
     */
    struct PostSettings {
        PostStatus status;
        PostType postType;
        uint256 creationTimeStamp;
        uint256 endTimeStamp;
        uint256 duration;
        Tokens tokenId;
    }

    /**
     * @dev Struct representing the private settings of a post on the Mecenate platform.
     */
    struct PostSettingPrivate {
        bytes vaultIdSeller;
        uint256 sellerTwitterId;
        uint256 sellerTelegramId;
        bytes vaultIdBuyer;
        uint256 buyerTwitterId;
        uint256 buyerTelegramId;
    }

    /**
     * @dev Struct representing the escrow of a post on the Mecenate platform.
     */
    struct PostEscrow {
        uint256 stake;
        uint256 payment;
        uint256 punishment;
        uint256 penalty;
    }

    /**
     * @dev Struct representing a user on the Mecenate platform.
     */
    struct User {
        bytes32 vaultId;
    }

    /**
     * @dev Struct representing a feed on the Mecenate platform.
     */
    struct Feed {
        address contractAddress;
        bytes32 operator;
        uint256 sellerStake;
        uint256 buyerStake;
        uint256 totalStake;
        uint256 postCount;
        uint256 paymentRequested;
        uint256 stakeRequested;
        PostStatus status;
        Tokens tokenId;
        string version;
    }

    /**
     * @dev Struct representing a bay request on the Mecenate platform.
     */
    struct BayRequest {
        bytes32 request;
        uint256 payment;
        uint256 stake;
        address postAddress;
        bool accepted;
        uint256 postCount;
        Tokens tokenId;
    }

    /**
     * @dev Struct representing the private settings of a bay request on the Mecenate platform.
     */
    struct BayRequestPrivate {
        bytes vaultIdSeller;
        bytes sellerResponse;
        bytes vaultIdBuyer;
        bytes buyerResponse;
        address buyerTo;
        bytes32 buyerNonce;
    }
}

contracts/MecenateVault.sol.content:
/**
 * @title MecenateVault
 * @dev This contract allows users to deposit and withdraw ETH and ERC20 tokens, and pay ETH and ERC20 tokens to other addresses.
 * It also keeps track of the deposited balances for each user using a commitment hash.
 */
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "./interfaces/IMecenateVerifier.sol";
import "./interfaces/IMecenateFeedFactory.sol";

contract MecenateVault is Ownable, ReentrancyGuard {
    using Address for address;
    using Address for address payable;

    mapping(bytes32 => uint256) private ethDeposits;
    mapping(bytes32 => mapping(address => uint256)) private tokenDeposits;

    address public WETH;
    address public DAI;
    address public USDC;
    address public MUSE;
    address public verifierContract;
    address public factoryContract;
    address public mecenateBay;
    address public mecenateUsers;
    address private relayer;

    uint256 public relayerFeePercentage = 200;
    uint256 public constant MAX_RELAYER_FEE_PERCENTAGE = 500;

    event MetaTransactionExecuted(
        address userAddress,
        address relayerAddress,
        bytes functionSignature
    );

    event Withdrawn(
        bytes32 indexed commitment,
        uint256 amount,
        string secret,
        address token
    );

    modifier onlyRelayer() {
        require(msg.sender == relayer, "Not relayer");
        _;
    }

    constructor(
        address _verifierContract,
        address _factoryContract,
        address _bayContract,
        address _usersContract,
        address _relayer
    ) {
        verifierContract = _verifierContract;
        factoryContract = _factoryContract;
        mecenateBay = _bayContract;
        mecenateUsers = _usersContract;
        relayer = _relayer;
    }

    function setTokens(
        address _WETH,
        address _DAI,
        address _USDC,
        address _MUSE
    ) external onlyOwner {
        WETH = _WETH;
        DAI = _DAI;
        USDC = _USDC;
        MUSE = _MUSE;
    }

    function changeRelayerFee(
        uint256 _newRelayerFeePercentage
    ) external onlyOwner {
        require(
            _newRelayerFeePercentage <= MAX_RELAYER_FEE_PERCENTAGE,
            "New relayer fee percentage is too high"
        );
        relayerFeePercentage = _newRelayerFeePercentage;
    }

    function depositETH(bytes32 encryptedVaultId) public payable nonReentrant {
        // 1. Add the deposit to the correct deposit mapping
        ethDeposits[encryptedVaultId] += msg.value;
    }

    function approveTokenToFeed(
        address _token,
        uint256 _amount,
        address _feed,
        bytes32 encryptedVaultId
    ) public {
        require(_token != address(0), "Token address cannot be 0");
        require(_amount > 0, "Amount must be greater than zero");
        require(
            _token == WETH || _token == DAI || _token == USDC || _token == MUSE,
            "Token not supported"
        );
        require(
            tokenDeposits[encryptedVaultId][_token] >= _amount,
            "Not enough balance"
        );

        require(
            IMecenateFeedFactory(factoryContract).isFeed(_feed) ||
                _feed == mecenateBay,
            "Not a feed"
        );

        // The user must first approve the token transfer
        // to this contract
        IERC20 token = IERC20(_token);
        // Approve the token to the feed
        token.approve(_feed, _amount);
    }

    function depositToken(
        address _token,
        uint256 _amount,
        bytes32 encryptedVaultId
    ) external {
        require(_token != address(0), "Token address cannot be 0");

        require(_amount > 0, "Amount must be greater than zero");

        require(
            _token == WETH || _token == DAI || _token == USDC || _token == MUSE,
            "Token not supported"
        );

        // The user must first approve the token transfer
        // to this contract
        IERC20 token = IERC20(_token);

        require(
            token.transferFrom(msg.sender, address(this), _amount),
            "Token transfer failed"
        );

        // Add the deposited amount to the tokenDeposits mapping
        tokenDeposits[encryptedVaultId][_token] += _amount;
    }

    function withdrawETH(
        uint256 _amount,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) public onlyRelayer nonReentrant {
        uint256 initialGas = gasleft();

        (
            bytes memory vaultId,
            ,
            ,
            bytes memory signedMessage
        ) = IMecenateVerifier(verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        (address to, bytes32 nonce) = abi.decode(
            signedMessage,
            (address, bytes32)
        );

        require(to == _to, "Not Same Address");

        require(nonce == _nonce, "Not Same Nonce");

        bytes32 encryptedVaultId = keccak256(vaultId);

        uint256 totalRequired = _amount; // Initialize with _value

        totalRequired -= tx.gasprice * initialGas; // Add maximum possible gas cost

        require(
            ethDeposits[encryptedVaultId] >= totalRequired,
            "Not enough balance"
        );

        // 1. Verify that the commitment exists and the amount is not zero
        require(ethDeposits[encryptedVaultId] > 0, "Commitment does not exist");

        require(_amount > 0, "Amount must be greater than zero");

        // 2. Verify that the commitment has enough balance to withdraw from
        require(ethDeposits[encryptedVaultId] >= _amount, "Not enough balance");

        //decode signedMessage as address
        ethDeposits[encryptedVaultId] -= _amount;

        uint256 gasUsed = initialGas - gasleft();

        uint256 gasCost = gasUsed * tx.gasprice;

        uint256 relayerFee = (gasCost * relayerFeePercentage) / 10000;

        require(_amount >= gasCost + relayerFee, "Not enough balance for gas");

        uint256 newAmount = _amount - gasCost - relayerFee;

        (bool result, ) = payable(to).call{value: newAmount}("");

        require(result, "ETH transfer failed");

        (bool result2, ) = payable(msg.sender).call{
            value: gasCost + relayerFee
        }("");

        require(result2, "ETH transfer failed with gas");
    }

    function withdrawWithSecret(
        string memory _secret,
        address _token,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) public onlyRelayer nonReentrant {
        bytes32 commitment = keccak256(abi.encodePacked(_secret));
        uint256 amount;

        if (_token == address(0)) {
            amount = ethDeposits[commitment];
            require(amount > 0, "No ETH deposit for this secret");
            ethDeposits[commitment] = 0;
            payable(_to).transfer(amount);
        } else {
            amount = tokenDeposits[commitment][_token];
            require(amount > 0, "No Token deposit for this secret");
            tokenDeposits[commitment][_token] = 0;
            IERC20(_token).transfer(_to, amount);
        }

        emit Withdrawn(commitment, amount, _secret, _token);
    }

    function withdrawToken(
        address _token,
        uint256 _amount,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) public onlyRelayer nonReentrant {
        uint256 initialGas = gasleft();

        uint256 totalRequired = tx.gasprice * initialGas;

        (
            bytes memory vaultId,
            ,
            ,
            bytes memory signedMessage
        ) = IMecenateVerifier(verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        (address to, bytes32 nonce) = abi.decode(
            signedMessage,
            (address, bytes32)
        );

        require(
            ethDeposits[keccak256(vaultId)] >= totalRequired,
            "Not enough ETH for gas required"
        );

        require(to == _to, "Not Same Address");

        require(nonce == _nonce, "Not Same Nonce");

        // Check if the commitment exists
        // and the amount is greater than the deposit.
        require(
            tokenDeposits[keccak256(vaultId)][_token] >= _amount,
            "Not enough token balance"
        );

        // Decrease the token balance for the commitment.
        tokenDeposits[keccak256(vaultId)][_token] -= _amount;

        // Transfer the tokens to msg.sender or operator.
        IERC20(_token).transfer(to, _amount);

        uint256 gasUsed = initialGas - gasleft();

        uint256 gasCost = gasUsed * tx.gasprice;

        uint256 relayerFee = (gasCost * relayerFeePercentage) / 10000;

        require(
            ethDeposits[keccak256(vaultId)] >= gasCost + relayerFee,
            "Not enough balance for gas used"
        );

        ethDeposits[keccak256(vaultId)] -= gasCost + relayerFee;

        (bool result, ) = payable(msg.sender).call{value: gasCost + relayerFee}(
            ""
        );

        require(result, "ETH transfer failed with gas");
    }

    function getEthDeposit(
        bytes32 encryptedVaultId
    ) public view returns (uint256) {
        return ethDeposits[encryptedVaultId];
    }

    receive() external payable {
        revert("Use depositETH function");
    }

    fallback() external payable {
        require(msg.data.length > 0, "Data required for Sismo verification.");
        bytes32 encryptedVaultId = abi.decode(msg.data, (bytes32));
        ethDeposits[encryptedVaultId] += msg.value;
    }

    function getTokenDeposit(
        address _token,
        bytes32 encryptedVaultId
    ) public view returns (uint256) {
        return tokenDeposits[encryptedVaultId][_token];
    }

    function execute(
        address _target,
        bytes calldata _data,
        uint256 _value,
        bytes32 _encryptedVaultId
    ) external onlyRelayer nonReentrant returns (bool) {
        uint256 availableBalance = ethDeposits[_encryptedVaultId];

        uint256 ethBalanceB4 = address(this).balance;
        uint256 daiBalanceB4 = IERC20(DAI).balanceOf(address(this));
        uint256 museBalanceB4 = IERC20(MUSE).balanceOf(address(this));

        uint256 totalRequired = _value + (tx.gasprice * gasleft());

        require(availableBalance >= totalRequired, "Insufficient ETH balance");

        bool success; // variabile per verificare il successo delle transazioni
        bytes memory retData; // dati di ritorno dalle transazioni

        if (_data.length == 0) {
            (success, ) = payable(_target).call{value: _value}("");
        } else {
            if (_value == 0) {
                (success, retData) = _target.call(_data);
            } else {
                (success, retData) = _target.call{value: _value}(_data);
            }
        }

        require(success, "Transaction failed");

        uint256 gasUsed = totalRequired - _value - (tx.gasprice * gasleft());

        uint256 relayerFee = (gasUsed * relayerFeePercentage) / 10000;

        require(
            availableBalance >= gasUsed + relayerFee,
            "Insufficient balance for gas and fee"
        );

        uint256 ethBalance = address(this).balance;
        uint256 daiBalance = IERC20(DAI).balanceOf(address(this));
        uint256 museBalance = IERC20(MUSE).balanceOf(address(this));

        uint256 diffEth;
        uint256 diffDai;
        uint256 diffMuse;

        if (ethBalanceB4 > ethBalance) {
            diffEth = ethBalanceB4 - ethBalance;

            if (diffEth > 0 && ethBalanceB4 != 0) {
                ethDeposits[_encryptedVaultId] -= diffEth;
            }
        }

        ethDeposits[_encryptedVaultId] = gasUsed - relayerFee;

        (success, ) = payable(msg.sender).call{value: gasUsed + relayerFee}("");

        require(success, "ETH transfer failed");

        if (daiBalanceB4 > daiBalance) {
            diffDai = daiBalanceB4 - daiBalance;

            if (diffDai > 0 && daiBalanceB4 != 0) {
                tokenDeposits[_encryptedVaultId][DAI] -= diffDai;
            }
        }

        if (museBalanceB4 > museBalance) {
            diffMuse = museBalanceB4 - museBalance;

            if (diffMuse > 0 && museBalanceB4 != 0) {
                tokenDeposits[_encryptedVaultId][MUSE] -= diffMuse;
            }
        }

        return true;
    }

    function setRelayer(address _relayer) external onlyOwner {
        relayer = _relayer;
    }

    function setVerifierContract(address _verifierContract) external onlyOwner {
        verifierContract = _verifierContract;
    }

    function setFactoryContract(address _factoryContract) external onlyOwner {
        factoryContract = _factoryContract;
    }

    function setMecenateBay(address _mecenateBay) external onlyOwner {
        mecenateBay = _mecenateBay;
    }

    function setMecenateUsers(address _mecenateUsers) external onlyOwner {
        mecenateUsers = _mecenateUsers;
    }
}

contracts/modules/FeedViewer.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "../library/Structures.sol";
import "../interfaces/IMecenateFeed.sol";

contract FeedViewer {
    function _getFeedInfo(
        address feed
    ) internal view returns (Structures.Feed memory) {
        Structures.Feed memory f;

        IMecenateFeed mecenateFeed = IMecenateFeed(feed);

        f.contractAddress = feed;
        f.operator = mecenateFeed.owner();
        f.sellerStake = mecenateFeed.getSellerStake();
        f.buyerStake = mecenateFeed.getBuyerStake();
        f.totalStake = mecenateFeed.getTotalStaked();
        f.postCount = mecenateFeed.postCount();
        f.paymentRequested = mecenateFeed.getPaymentRequested();
        f.stakeRequested = mecenateFeed.getStakeRequested();
        f.status = mecenateFeed.getStatus();
        f.tokenId = mecenateFeed.getTokenId();
        f.version = mecenateFeed.version();

        return f;
    }

    function _getFeedsInfo(
        address[] memory _feeds
    ) internal view returns (Structures.Feed[] memory) {
        uint256 len = _feeds.length;
        Structures.Feed[] memory f = new Structures.Feed[](len);

        for (uint256 i = 0; i < len; i++) {
            f[i] = _getFeedInfo(_feeds[i]);
        }

        return f;
    }
}

