//Objective:
//Anyone can send Ethers
//Only owner can withdraw
//Anyone can check wallet balance

pragma solidity 0.8.19;

import "./interfaces/IMecenateVerifier.sol";
// import IERC20
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract MecenateForwarder {
    address public verifierContract;
    address public dai;
    address public muse;

    constructor(address _verifierContract, address _dai, address _muse) {
        verifierContract = _verifierContract;
        dai = _dai;
        muse = _muse;
    }

    receive() external payable {}

    //Verify Owner or Not
    modifier verifyOwner(
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) {
        (
            bytes memory vaultId,
            ,
            ,
            bytes memory signedMessage
        ) = IMecenateVerifier(verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        (address to, bytes32 nonce) = abi.decode(
            signedMessage,
            (address, bytes32)
        );

        require(to == _to, "Not Same Address");
        require(nonce == _nonce, "Not Same Nonce");
        _;
    }

    //Transfer the amount from caller's account to this wallet (smart contract)
    function withdraw(
        uint _amount,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) external verifyOwner(sismoConnectResponse, _to, _nonce) {
        payable(_to).transfer(_amount);
    }

    function withdrawToken(
        address _token,
        uint _amount,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) external verifyOwner(sismoConnectResponse, _to, _nonce) {
        IERC20(_token).transfer(_to, _amount);
    }

    //To know the wallet balance
    function getETHBalance() external view returns (uint) {
        return address(this).balance;
    }

    function getDAIBalance() external view returns (uint) {
        return IERC20(dai).balanceOf(address(this));
    }

    function getMUSEBalance() external view returns (uint) {
        return IERC20(muse).balanceOf(address(this));
    }

    function execute(
        address _target,
        bytes calldata _data,
        uint256 _value,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) external verifyOwner(sismoConnectResponse, _to, _nonce) returns (bool) {
        bool success;
        bytes memory retData;
        string memory revertReason;

        if (_data.length == 0) {
            (success, retData) = payable(_target).call{value: _value}("");
        } else {
            if (_value == 0) {
                (success, retData) = _target.call(_data);
            } else {
                (success, retData) = _target.call{value: _value}(_data);
            }
        }

        if (!success) {
            // Estrai il motivo del revert dal payload di ritorno
            if (retData.length > 0) {
                // Nota: Solidity memorizza il motivo del revert come un "Error(string)"
                // che è un selettore di funzione di 4 byte seguito dalla lunghezza della stringa
                // e dai dati della stringa stessa.
                assembly {
                    revertReason := add(retData, 0x24)
                }
            }
            require(false, revertReason);
        }

        return success;
    }
}
