// SPDX-License-Identifier: MIT

/**
 * @title Structures
 * @dev This library defines various data structures used in the Mecenate platform.
 */
pragma solidity 0.8.19;

library Structures {
    enum PostStatus {
        Waiting,
        Proposed,
        Accepted,
        Submitted,
        Finalized,
        Punished,
        Revealed,
        Renounced
    }

    enum PostResult {
        None,
        Valid,
        Punished
    }

    enum Tokens {
        NaN,
        MUSE,
        DAI
    }

    enum PostType {
        Text,
        Image,
        Video,
        Audio,
        File
    }
    enum PostDuration {
        OneDay,
        ThreeDays,
        OneWeek,
        TwoWeeks,
        OneMonth
    }

    struct PostTimestamp {
        PostResult postResult;
        uint256 creationTimeStamp;
        uint256 endTimeStamp;
    }

    struct FeedSettings {
        uint256 punishmentRatio;
        uint256 postCount;
        address usersModuleContract;
        address factoryContract;
        address router;
        string version;
    }

    struct FactorySettings {
        address treasuryContract;
        address usersModuleContract;
        address easContract;
        bytes32 easSchema;
        address museToken;
        address daiToken;
        address wethToken;
        address router;
    }

    /**
     * @dev Struct representing a post on the Mecenate platform.
     */
    struct Post {
        User creator;
        PostData postdata;
    }

    /**
     * @dev Struct representing the data of a post on the Mecenate platform.
     */
    struct PostData {
        PostSettings settings;
        PostEscrow escrow;
        PostEncryptedData data;
    }

    /**
     * @dev Struct representing the encrypted data of a post on the Mecenate platform.
     */
    struct PostEncryptedData {
        bytes encryptedData;
        bytes encryptedKey;
        bytes decryptedData;
    }

    /**
     * @dev Struct representing the settings of a post on the Mecenate platform.
     */
    struct PostSettings {
        PostStatus status;
        PostType postType;
        uint256 creationTimeStamp;
        uint256 endTimeStamp;
        uint256 duration;
        Tokens tokenId;
        bytes32 postId;
    }

    /**
     * @dev Struct representing the escrow of a post on the Mecenate platform.
     */
    struct PostEscrow {
        address buyer;
        address seller;
        uint256 stake;
        uint256 payment;
        uint256 punishment;
        uint256 penalty;
    }

    /**
     * @dev Struct representing a user on the Mecenate platform.
     */
    struct User {
        address evmAddress;
        bytes sismoVaultId;
        bytes publicKey;
    }

    /**
     * @dev Struct representing a feed on the Mecenate platform.
     */
    struct Feed {
        address contractAddress;
        address owner;
        uint256 sellerStake;
        uint256 buyerStake;
        uint256 totalStake;
        uint256 postCount;
        uint256 paymentRequested;
        uint256 stakeRequested;
        PostStatus status;
        Tokens tokenId;
        string version;
        bytes32 postId;
    }

    /**
     * @dev Struct representing a bay request on the Mecenate platform.
     */
    struct BayRequest {
        bytes request;
        uint256 payment;
        uint256 stake;
        address postAddress;
        bool accepted;
        uint256 postCount;
        Tokens tokenId;
        address buyerAddress;
        bytes32 postId;
    }
}

contracts/modules/Acceptance.sol.content:
/**
 * @title Acceptance
 * @dev This contract is an abstract contract that inherits from Events and Staking contracts. It provides a function to accept a post and add stake to the post. The function accepts a Sismo Connect response as a parameter and verifies the user existence. It also checks if the user is not the seller and if the post is in Proposed status. If the post has an escrow payment, it checks if the buyer has paid enough and adds the payment to the post's escrow. If the post does not have an escrow payment, it requires a payment from the buyer and adds it to the post's escrow. Finally, it changes the post status to Accepted and emits an Accepted event.
 */
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "./Events.sol";
import "./Staking.sol";

abstract contract Acceptance is Events, Staking {
    function acceptPost(
        Structures.Tokens tokenId,
        uint256 paymentAmount,
        address funder,
        address buyer,
        bool useStake
    ) external payable virtual {
        require(locked == false, "LOCKED");
        require(
            validStatuses[uint8(Structures.PostStatus.Proposed)],
            "INVALID_STATUS"
        );

        _checkToken(tokenId);

        uint256 amountToAdd;

        if (!useStake) {
            amountToAdd = tokenId == Structures.Tokens.NaN
                ? msg.value
                : paymentAmount;
        }

        uint256 sellerStake = Deposit._getDeposit(
            tokenId,
            post.postdata.escrow.seller
        );

        require(sellerStake >= post.postdata.escrow.stake, "STAKE_INCORRECT");
        require(
            IMecenateUsers(settings.usersModuleContract).checkifUserExist(
                buyer
            ),
            "USERT_NOT_EXIST"
        );
        require(
            msg.sender != post.postdata.escrow.seller ||
                buyer != post.postdata.escrow.seller,
            "YOU_ARE_THE_SELLER"
        );

        if (post.postdata.escrow.payment > 0) {
            require(
                paymentAmount >= post.postdata.escrow.payment,
                "NOT_ENOUGH_PAYMENT"
            );
        }

        uint256 payment;

        if (useStake) {
            require(
                Deposit._getDeposit(tokenId, msg.sender) >= paymentAmount,
                "PAYMENT_INCORRECT"
            );

            payment = paymentAmount;
        } else {
            if (tokenId == Structures.Tokens.NaN) {
                require(msg.value >= paymentAmount, "WRONG_MSG_VALUE");
            }

            payment = _addStake(tokenId, buyer, funder, paymentAmount);
        }

        post.postdata.escrow.payment = payment;
        post.postdata.escrow.buyer = buyer;
        post.postdata.settings.status = Structures.PostStatus.Accepted;

        _changeStatus(Structures.PostStatus.Accepted);

        locked = true;

        emit Accepted(post);
    }
}

contracts/modules/BurnDAI.sol.content:
pragma solidity 0.8.19;

import "./BurnMUSE.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "../interfaces/IWETH.sol";

abstract contract BurnDAI is BurnMUSE {
    using SafeMathUpgradeable for uint256;

    function _burnFrom(
        address from,
        uint256 value
    ) internal override returns (bool success) {
        IERC20Upgradeable(
            IMecenateFeedFactory(settings.factoryContract).daiToken()
        ).transferFrom(from, address(this), value);

        _burn(value);

        return true;
    }

    function _burnDai(uint256 value) internal returns (bool success) {
        // approve uniswap for token transfer
        IERC20Upgradeable(
            IMecenateFeedFactory(settings.factoryContract).daiToken()
        ).approve(settings.router, value);

        // swap IMecenateFeedFactory(settings.factoryContract).daiToken() for MUSE
        uint256 tokens_sold = value;

        uint256 tokens_bought = _swapTokensForToken(
            IMecenateFeedFactory(settings.factoryContract).daiToken(),
            IMecenateFeedFactory(settings.factoryContract).wethToken(),
            IMecenateFeedFactory(settings.factoryContract).getRouterFee(0),
            tokens_sold
        );

        uint256 tokens_sold_to_muse = _swapTokensForToken(
            IMecenateFeedFactory(settings.factoryContract).wethToken(),
            IMecenateFeedFactory(settings.factoryContract).museToken(),
            IMecenateFeedFactory(settings.factoryContract).getRouterFee(1),
            tokens_bought
        );

        IERC20Upgradeable(
            IMecenateFeedFactory(settings.factoryContract).museToken()
        ).approve(
                IMecenateFeedFactory(settings.factoryContract).museToken(),
                tokens_sold_to_muse
            );

        // burn MUSE
        BurnMUSE._burn(tokens_sold_to_muse);

        return true;
    }

    function _burnWeth(uint256 value) internal returns (bool success) {
        // deposit IMecenateFeedFactory(settings.factoryContract).wethToken()
        IWETH(IMecenateFeedFactory(settings.factoryContract).wethToken())
            .deposit{value: value}();

        // approve uniswap for token transfer
        IERC20Upgradeable(
            IMecenateFeedFactory(settings.factoryContract).wethToken()
        ).approve(settings.router, value);

        // swap IMecenateFeedFactory(settings.factoryContract).wethToken() for MUSE
        uint256 tokens_sold = value;

        uint256 tokens_bought = _swapTokensForToken(
            IMecenateFeedFactory(settings.factoryContract).wethToken(),
            IMecenateFeedFactory(settings.factoryContract).museToken(),
            IMecenateFeedFactory(settings.factoryContract).getRouterFee(1),
            tokens_sold
        );

        IERC20Upgradeable(
            IMecenateFeedFactory(settings.factoryContract).museToken()
        ).approve(
                IMecenateFeedFactory(settings.factoryContract).museToken(),
                tokens_bought
            );

        // burn MUSE
        BurnMUSE._burn(tokens_bought);

        return true;
    }

    function getTokenAddress()
        internal
        view
        virtual
        override
        returns (address token)
    {
        return IMecenateFeedFactory(settings.factoryContract).daiToken();
    }

    function getExchangeAddress()
        internal
        view
        override
        returns (address exchange)
    {
        exchange = settings.router;
    }

    function _swapTokensForToken(
        address tokenIn,
        address tokenOut,
        uint24 fee,
        uint256 amountIn
    ) internal returns (uint256 amountOut) {
        // Check and approve allowance
        uint256 allowance = IERC20Upgradeable(tokenIn).allowance(
            address(this),
            settings.router
        );
        if (allowance < amountIn) {
            IERC20Upgradeable(tokenIn).approve(
                settings.router,
                type(uint256).max
            );
        }

        // Prepare parameters
        address recipient = address(this);
        uint256 deadline = block.timestamp + 60; // 15 seconds from the current block timestamp

        // Perform the swap
        // Refer to the Uniswap V3 documentation for details on the params.
        amountOut = ISwapRouter(settings.router).exactInputSingle(
            ISwapRouter.ExactInputSingleParams({
                tokenIn: tokenIn,
                tokenOut: tokenOut,
                fee: fee,
                recipient: recipient,
                deadline: deadline,
                amountIn: amountIn,
                amountOutMinimum: 0, // you may want to set a minimum here
                sqrtPriceLimitX96: 0
            })
        );

        return amountOut;
    }
}

