// SPDX LICENSE: MIT
pragma solidity 0.8.19;
import "../interfaces/IMUSE.sol";
import "../interfaces/IMecenateFeedFactory.sol";
import "./Events.sol";

abstract contract BurnMUSE is Events {
    function _burn(uint256 value) internal virtual returns (bool success) {
        IMUSE(IMecenateFeedFactory(settings.factoryContract).museToken()).burn(
            value
        );
        return true;
    }

    function _burnFrom(
        address from,
        uint256 value
    ) internal virtual returns (bool success) {
        IMUSE(IMecenateFeedFactory(settings.factoryContract).museToken())
            .transferFrom(from, address(this), value);
        _burn(value);
        return true;
    }

    function getTokenAddress() internal view virtual returns (address token) {
        return IMecenateFeedFactory(settings.factoryContract).museToken();
    }

    function getExchangeAddress()
        internal
        view
        virtual
        returns (address exchange)
    {
        exchange = IMecenateFeedFactory(settings.factoryContract).router();
    }
}

contracts/modules/Creation.sol.content:
/**
 * @title Creation
 * @dev This contract is an abstract contract that inherits from Staking contract. It provides a function to create a post with the given parameters. The function requires the user to be the owner of the post and to have a stake. The post can have different durations and types. The function returns the created post.
 */
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./Staking.sol";

abstract contract Creation is Staking {
    function createPost(
        bytes memory encryptedHash,
        Structures.PostType postType,
        Structures.PostDuration postDuration,
        uint256 payment,
        uint256 stakeAmount,
        Structures.Tokens tokenId,
        address funder,
        address seller,
        bool useStake
    ) external payable onlyValidTokenID(tokenId) {
        require(msg.sender == owner, "NOT_OWNER");
        require(locked == false, "LOCKED");

        require(
            IMecenateUsers(settings.usersModuleContract).checkifUserExist(
                seller
            ),
            "USER_NOT_EXIST"
        );

        require(
            validStatuses[uint8(post.postdata.settings.status)],
            "INVALID_STATUS"
        );

        require(stakeAmount > 0, "STAKE_AMOUNT_ZERO");

        require(payment > 0, "PAYMENT_ZERO");

        uint256 stake;

        if (useStake) {
            require(
                Deposit._getDeposit(tokenId, seller) >= stakeAmount,
                "STAKE_INCORRECT"
            );

            stake = stakeAmount;
        } else {
            if (tokenId == Structures.Tokens.NaN) {
                require(msg.value == stakeAmount, "WRONG_MSG_VALUE");
            }

            stake = _addStake(tokenId, seller, funder, stakeAmount);
        }

        uint256 duration = postDurationToDays[uint8(postDuration)];

        _changeStatus(Structures.PostStatus.Proposed);

        Structures.User memory creator = IMecenateUsers(
            settings.usersModuleContract
        ).getUserMetadata(seller);

        bytes32 newPostId = keccak256(
            abi.encodePacked(encryptedHash, block.timestamp, creator.evmAddress)
        );

        // Initialize the new Post struct with named arguments for clarity
        Structures.Post memory newPost = Structures.Post({
            creator: creator,
            postdata: Structures.PostData({
                settings: Structures.PostSettings({
                    postType: postType,
                    status: Structures.PostStatus.Proposed,
                    creationTimeStamp: block.timestamp,
                    endTimeStamp: 0,
                    duration: duration,
                    tokenId: tokenId,
                    postId: newPostId
                }),
                escrow: Structures.PostEscrow({
                    buyer: address(0),
                    seller: seller,
                    stake: stake,
                    payment: payment,
                    punishment: 0,
                    penalty: 0
                }),
                data: Structures.PostEncryptedData({
                    encryptedData: encryptedHash,
                    encryptedKey: ZEROHASH,
                    decryptedData: ZEROHASH
                })
            })
        });

        // Update storage and emit event
        post = newPost;

        // add intto postIds
        addPostId(newPostId);

        settings.postCount++;

        postTimestamps[newPostId] = Structures.PostTimestamp({
            postResult: Structures.PostResult.None,
            creationTimeStamp: block.timestamp,
            endTimeStamp: 0
        });

        emit Created(newPost);
    }
}

contracts/modules/Data.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "../interfaces/IMecenateFeed.sol";
import "../interfaces/IMecenateUsers.sol";
import "../interfaces/IMecenateTreasury.sol";
import "../interfaces/IMecenateFeedFactory.sol";
import "../helpers/eas/IEAS.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";

/**
 * @title Data
 * @dev This contract stores data related to Mecenate posts and provides functions to interact with it.
 */
abstract contract Data {
    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.Bytes32Set;
    EnumerableSetUpgradeable.Bytes32Set internal postIds;

    bytes internal constant ZEROHASH = "0x00";

    address public owner;

    Structures.Post public post;
    Structures.FeedSettings internal settings;

    mapping(uint8 => uint256) internal postDurationToDays;
    mapping(uint8 => bool) internal validStatuses;
    mapping(bytes32 => Structures.PostTimestamp) internal postTimestamps;

    bool public locked;

    function _changeStatus(Structures.PostStatus newStatus) internal {
        validStatuses[uint8(post.postdata.settings.status)] = false;
        validStatuses[uint8(newStatus)] = true;
        post.postdata.settings.status = newStatus;
    }

    function addPostId(bytes32 postId) internal {
        postIds.add(postId);
    }

    function getAllPostIds() external view returns (bytes32[] memory) {
        uint256 length = postIds.length();
        bytes32[] memory ids = new bytes32[](length);

        for (uint256 i = 0; i < length; i++) {
            ids[i] = postIds.at(i);
        }

        return ids;
    }

    function getPostId() external view returns (bytes32) {
        return post.postdata.settings.postId;
    }

    function getPostTimestamp(
        bytes32 postId
    ) external view returns (Structures.PostTimestamp memory) {
        return postTimestamps[postId];
    }

    function containPostId(bytes32 postId) external view returns (bool) {
        return postIds.contains(postId);
    }

    function getStatus() external view returns (Structures.PostStatus) {
        return post.postdata.settings.status;
    }

    function getTokenId() external view returns (Structures.Tokens) {
        return post.postdata.settings.tokenId;
    }

    function getTokenIdAddress() public view returns (address) {
        if (post.postdata.settings.tokenId == Structures.Tokens.MUSE) {
            return IMecenateFeedFactory(settings.factoryContract).museToken();
        } else if (post.postdata.settings.tokenId == Structures.Tokens.DAI) {
            return IMecenateFeedFactory(settings.factoryContract).daiToken();
        } else {
            return address(0);
        }
    }

    function _checkToken(Structures.Tokens _token) internal view {
        require(_token == post.postdata.settings.tokenId, "WRONG_TOKEN");
    }

    function getPaymentRequested() external view returns (uint256) {
        return post.postdata.escrow.payment;
    }

    function getStakeRequested() external view returns (uint256) {
        return post.postdata.escrow.stake;
    }

    function postCount() external view returns (uint256) {
        return settings.postCount;
    }

    function getEncryptedPost() external view returns (bytes memory) {
        return post.postdata.data.encryptedData;
    }

    function getPost() external view returns (Structures.Post memory) {
        return post;
    }

    receive() external payable {}
}

contracts/modules/Deposit.sol.content:
pragma solidity 0.8.19;
import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";
import "./TokenManager.sol";

contract Deposit is TokenManager {
    using SafeMathUpgradeable for uint256;

    mapping(uint256 => mapping(address => uint256)) private _deposit;

    event DepositIncreased(
        Structures.Tokens tokenID,
        address user,
        uint256 amount,
        uint256 newDeposit
    );
    event DepositDecreased(
        Structures.Tokens tokenID,
        address user,
        uint256 amount,
        uint256 newDeposit
    );

    function _increaseDeposit(
        Structures.Tokens tokenID,
        address user,
        uint256 amountToAdd
    ) internal returns (uint256 newDeposit) {
        // calculate new deposit amount
        newDeposit = _deposit[uint256(tokenID)][user].add(amountToAdd);

        // set new stake to storage
        _deposit[uint256(tokenID)][user] = newDeposit;

        // emit event
        emit DepositIncreased(tokenID, user, amountToAdd, newDeposit);

        // return
        return newDeposit;
    }

    function _decreaseDeposit(
        Structures.Tokens tokenID,
        address user,
        uint256 amountToRemove
    ) internal returns (uint256 newDeposit) {
        // get current deposit
        uint256 currentDeposit = _deposit[uint256(tokenID)][user];

        // check if sufficient deposit
        require(currentDeposit >= amountToRemove, "INSUFFICIENT_DEPOSIT");

        // calculate new deposit amount
        newDeposit = currentDeposit.sub(amountToRemove);

        // set new stake to storage
        _deposit[uint256(tokenID)][user] = newDeposit;

        // emit event
        emit DepositDecreased(tokenID, user, amountToRemove, newDeposit);

        // return
        return newDeposit;
    }

    function _clearDeposit(
        Structures.Tokens tokenID,
        address user
    ) internal returns (uint256 amountRemoved) {
        // get current deposit
        uint256 currentDeposit = _deposit[uint256(tokenID)][user];
        // remove deposit
        _decreaseDeposit(tokenID, user, currentDeposit);
        // return
        return currentDeposit;
    }

    function _getDeposit(
        Structures.Tokens tokenID,
        address user
    ) internal view returns (uint256 deposit) {
        return _deposit[uint256(tokenID)][user];
    }
}

