contracts/layer/MecenateBay.sol.content:
/**
 * @title MecenateBay
 * @dev This contract manages the creation and acceptance of requests for Mecenate feeds. It also verifies user identity using the Sismo protocol.
 */
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "../interfaces/IMecenateVerifier.sol";
import "../library/Structures.sol";
import "../modules/FeedViewer.sol";
import "../interfaces/IMecenateUsers.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract MecenateBay is Ownable, FeedViewer {
    using SafeERC20 for IERC20;

    Structures.BayRequest[] public allRequests;

    Structures.BayRequestPrivate[] public allRequestsPrivate;

    address public usersMouduleContract;

    address public verifierContract;

    address public vaultContract;

    address public museToken;

    address public daiToken;

    mapping(bytes32 => Structures.BayRequest[]) public requests;

    uint256 public contractCounter;

    mapping(uint256 => bytes) private sismoResponseMapping;

    event RequestCreated(
        bytes32 indexed user,
        Structures.BayRequest,
        uint256 indexed index
    );

    event RequestAccepted(
        bytes32 indexed user,
        Structures.BayRequest,
        uint256 indexed index
    );

    constructor(
        address _usersMouduleContract,
        address _verifierContract,
        address _vaultContract
    ) {
        usersMouduleContract = _usersMouduleContract;
        verifierContract = _verifierContract;
        vaultContract = _vaultContract;
    }

    function changeMuseToken(address _museToken) external onlyOwner {
        museToken = _museToken;
    }

    function changeDaiToken(address _daiToken) external onlyOwner {
        daiToken = _daiToken;
    }

    function createRequest(
        Structures.BayRequest memory request,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) public payable returns (Structures.BayRequest memory) {
        if (request.tokenId == Structures.Tokens.NaN) {
            require(msg.value > 0, "BAY:payment is not enough");
        } else if (request.tokenId == Structures.Tokens.DAI) {
            IERC20(daiToken).safeTransferFrom(
                msg.sender,
                address(this),
                request.payment
            );
        } else if (request.tokenId == Structures.Tokens.MUSE) {
            IERC20(museToken).safeTransferFrom(
                msg.sender,
                address(this),
                request.payment
            );
        }

        require(request.payment > 0, "BAY:payment is not enough");

        (bytes memory vaultId, , , ) = _sismoVerify(
            sismoConnectResponse,
            _to,
            _nonce
        );

        bytes32 encryptedVaultId = keccak256(vaultId);

        require(
            IMecenateUsers(usersMouduleContract).checkifUserExist(
                keccak256(vaultId)
            ),
            "user does not exist"
        );

        require(request.stake > 0, "BAY:stake is not enough");

        require(request.payment == msg.value, "BAY:payment is not enough");

        require(request.payment > 0, "BAY:payment is not enough");

        requests[encryptedVaultId].push(request);

        allRequests.push(request);

        allRequestsPrivate.push(
            Structures.BayRequestPrivate({
                vaultIdSeller: "0x00",
                sellerResponse: "0x00",
                vaultIdBuyer: vaultId,
                buyerResponse: sismoConnectResponse,
                buyerTo: _to,
                buyerNonce: _nonce
            })
        );

        contractCounter++;

        emit RequestCreated(encryptedVaultId, request, allRequests.length - 1);
    }

    function acceptRequest(
        uint256 index,
        address _feed,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) public {
        (bytes memory vaultId, , , ) = _sismoVerify(
            sismoConnectResponse,
            _to,
            _nonce
        );

        bytes32 encryptedVaultId = keccak256(vaultId);

        Structures.Feed memory feed = _getFeedInfo(_feed);

        require(
            allRequests[index].payment >= feed.paymentRequested,
            "BAY:payment is not the same of the feed"
        );

        require(
            feed.stakeRequested >= allRequests[index].stake,
            "BAY:stake is not the same of the feed"
        );

        require(
            IMecenateUsers(usersMouduleContract).checkifUserExist(
                encryptedVaultId
            ),
            "BAY:user does not exist"
        );

        allRequestsPrivate.push(
            Structures.BayRequestPrivate({
                vaultIdSeller: vaultId,
                sellerResponse: sismoConnectResponse,
                vaultIdBuyer: allRequestsPrivate[index].vaultIdBuyer,
                buyerResponse: allRequestsPrivate[index].buyerResponse,
                buyerTo: allRequestsPrivate[index].buyerTo,
                buyerNonce: allRequestsPrivate[index].buyerNonce
            })
        );

        if (allRequests[index].tokenId != Structures.Tokens.NaN) {
            if (allRequests[index].tokenId == Structures.Tokens.DAI) {
                IERC20(daiToken).approve(
                    msg.sender,
                    allRequests[index].payment
                );
            } else if (allRequests[index].tokenId == Structures.Tokens.MUSE) {
                IERC20(museToken).approve(
                    msg.sender,
                    allRequests[index].payment
                );
            }

            IMecenateFeed(_feed).acceptPost{value: 0}(
                allRequestsPrivate[index].buyerResponse,
                allRequestsPrivate[index].buyerTo,
                allRequestsPrivate[index].buyerNonce,
                allRequests[index].tokenId,
                allRequests[index].payment
            );
        } else {
            IMecenateFeed(_feed).acceptPost{value: allRequests[index].payment}(
                allRequestsPrivate[index].buyerResponse,
                allRequestsPrivate[index].buyerTo,
                allRequestsPrivate[index].buyerNonce,
                allRequests[index].tokenId,
                allRequests[index].payment
            );
        }

        allRequests[index].accepted = true;

        allRequests[index].postAddress = _feed;

        allRequests[index].postCount = feed.postCount;

        emit RequestAccepted(encryptedVaultId, allRequests[index], index);
    }

    function _sismoVerify(
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) internal view returns (bytes memory, uint256, uint256, bytes memory) {
        (
            bytes memory vaultId,
            uint256 twitterId,
            uint256 telegramId,
            bytes memory signedMessage
        ) = IMecenateVerifier(verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        (address to, bytes32 nonce) = abi.decode(
            signedMessage,
            (address, bytes32)
        );

        require(_nonce == nonce, "Not Same Nonce");

        return (vaultId, twitterId, telegramId, signedMessage);
    }

    function getRequests()
        public
        view
        returns (Structures.BayRequest[] memory)
    {
        return allRequests;
    }

    function getRequestForAddress(
        bytes32 _user
    ) public view returns (Structures.BayRequest[] memory) {
        return requests[_user];
    }

    // removéthe request and refund the user delete the array and move the last element to the index
    function removeRequest(
        uint256 index,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) public {
        (
            bytes memory vaultId,
            uint256 twitterId,
            uint256 telegramId,
            bytes memory signedMessage
        ) = _sismoVerify(sismoConnectResponse, _to, _nonce);

        bytes32 encryptedVaultId = keccak256(vaultId);

        require(
            encryptedVaultId ==
                keccak256(allRequestsPrivate[index].vaultIdBuyer),
            "BAY:you are not the buyer"
        );

        require(
            allRequests[index].accepted == false,
            "BAY:request is already accepted"
        );

        Structures.BayRequest memory requestToRemove = allRequests[index];

        //  send eth with data to the vaultctx
        (bool _result, ) = vaultContract.call{value: requestToRemove.payment}(
            sismoConnectResponse
        );

        require(_result, "BAY:Vault call failed");

        // Remove from allRequests array
        uint256 lastIndex = allRequests.length - 1;
        if (index < lastIndex) {
            allRequests[index] = allRequests[lastIndex];
        }
        allRequests.pop();

        // Remove from allRequestsPrivate array
        uint256 lastIndexPrivate = allRequestsPrivate.length - 1;
        if (index < lastIndexPrivate) {
            allRequestsPrivate[index] = allRequestsPrivate[lastIndexPrivate];
        }
        allRequestsPrivate.pop();

        // Remove from requests mapping
        for (uint256 i = 0; i < requests[encryptedVaultId].length; i++) {
            if (
                requests[encryptedVaultId][i].payment ==
                requestToRemove.payment &&
                requests[encryptedVaultId][i].stake == requestToRemove.stake &&
                requests[encryptedVaultId][i].postAddress ==
                requestToRemove.postAddress &&
                requests[encryptedVaultId][i].postCount ==
                requestToRemove.postCount
            ) {
                uint256 lastIndexSender = requests[encryptedVaultId].length - 1;
                if (i < lastIndexSender) {
                    requests[encryptedVaultId][i] = requests[encryptedVaultId][
                        lastIndexSender
                    ];
                }
                requests[encryptedVaultId].pop();
                break;
            }
        }
    }

    function changeVault(address _vault) external onlyOwner {
        vaultContract = _vault;
    }

    function changeVerifier(address _verifier) external onlyOwner {
        verifierContract = _verifier;
    }

    function changeUsersModule(address _usersModule) external onlyOwner {
        usersMouduleContract = _usersModule;
    }

    receive() external payable {}
}

contracts/library/Structures.sol.content:
/**
 * @title Structures
 * @dev This library defines various data structures used in the Mecenate platform.
 */
pragma solidity 0.8.19;

library Structures {
    enum PostStatus {
        Waiting,
        Proposed,
        Accepted,
        Submitted,
        Finalized,
        Punished,
        Revealed,
        Renounced
    }
    enum Tokens {
        NaN,
        MUSE,
        DAI
    }
    enum PostType {
        Text,
        Image,
        Video,
        Audio,
        File
    }
    enum PostDuration {
        OneDay,
        ThreeDays,
        OneWeek,
        TwoWeeks,
        OneMonth
    }

    struct FeedSettings {
        uint256 punishmentRatio;
        uint256 postCount;
        address usersModuleContract;
        address factoryContract;
        address verifierContract;
        address vaultContract;
        address router;
        bytes encodedSymKey;
        bytes lastMessageForBuyer;
        bytes lastMessageForSeller;
        string version;
    }

    struct FactorySettings {
        address treasuryContract;
        address usersModuleContract;
        address verifierContract;
        address vaultContract;
        address museToken;
        address daiToken;
        address wethToken;
        address router;
    }

    /**
     * @dev Struct representing a post on the Mecenate platform.
     */
    struct Post {
        User creator;
        PostData postdata;
    }

    /**
     * @dev Struct representing the data of a post on the Mecenate platform.
     */
    struct PostData {
        PostSettings settings;
        PostEscrow escrow;
        PostEncryptedData data;
    }

    /**
     * @dev Struct representing the encrypted data of a post on the Mecenate platform.
     */
    struct PostEncryptedData {
        bytes encryptedData;
        bytes encryptedKey;
        bytes decryptedData;
    }

    /**
     * @dev Struct representing the settings of a post on the Mecenate platform.
     */
    struct PostSettings {
        PostStatus status;
        PostType postType;
        uint256 creationTimeStamp;
        uint256 endTimeStamp;
        uint256 duration;
        Tokens tokenId;
    }

    /**
     * @dev Struct representing the private settings of a post on the Mecenate platform.
     */
    struct PostSettingPrivate {
        bytes vaultIdSeller;
        uint256 sellerTwitterId;
        uint256 sellerTelegramId;
        bytes vaultIdBuyer;
        uint256 buyerTwitterId;
        uint256 buyerTelegramId;
    }

    /**
     * @dev Struct representing the escrow of a post on the Mecenate platform.
     */
    struct PostEscrow {
        uint256 stake;
        uint256 payment;
        uint256 punishment;
        uint256 penalty;
    }

    /**
     * @dev Struct representing a user on the Mecenate platform.
     */
    struct User {
        bytes32 vaultId;
    }

    /**
     * @dev Struct representing a feed on the Mecenate platform.
     */
    struct Feed {
        address contractAddress;
        bytes32 operator;
        uint256 sellerStake;
        uint256 buyerStake;
        uint256 totalStake;
        uint256 postCount;
        uint256 paymentRequested;
        uint256 stakeRequested;
        PostStatus status;
        Tokens tokenId;
    }

    /**
     * @dev Struct representing a bay request on the Mecenate platform.
     */
    struct BayRequest {
        bytes32 request;
        uint256 payment;
        uint256 stake;
        address postAddress;
        bool accepted;
        uint256 postCount;
        Tokens tokenId;
    }

    /**
     * @dev Struct representing the private settings of a bay request on the Mecenate platform.
     */
    struct BayRequestPrivate {
        bytes vaultIdSeller;
        bytes sellerResponse;
        bytes vaultIdBuyer;
        bytes buyerResponse;
        address buyerTo;
        bytes32 buyerNonce;
    }
}

contracts/MecenateForwarder.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IMecenateVault {
    function depositToken(
        address token,
        uint256 amount,
        bytes32 encryptedVaultId
    ) external;

    function depositETH(bytes32 encryptedVaultId) external payable;
}

interface IMecenateETHForwarderFactory {
    function getVault() external view returns (address);
}

contract MecenateForwarder {
    bytes32 private encryptedVaultId;
    address public factory;

    constructor(bytes32 _encryptedVaultId) {
        encryptedVaultId = _encryptedVaultId;
        factory = msg.sender;
    }

    function depositToken(address token, uint256 amount) external {
        address vault = IMecenateETHForwarderFactory(factory).getVault();

        // Approva il token
        require(IERC20(token).approve(vault, amount), "Approve failed");

        IMecenateVault(vault).depositToken(token, amount, encryptedVaultId);
    }

    receive() external payable {
        address vault = IMecenateETHForwarderFactory(factory).getVault();

        // Esegui il deposito in ETH
        (bool success, ) = payable(vault).call{value: msg.value}(
            abi.encodeWithSignature("depositETH(bytes32)", encryptedVaultId)
        );
        require(success, "Deposit failed");
    }
}

contracts/MecenateStats.sol.content:
/**
 * @title MecenateStats
 * @dev This contract provides statistics about the Mecenate platform, including the total number of users, bay requests, feeds, global fee, fixed fee, and treasury balance.
 */
pragma solidity 0.8.19;

import "./interfaces/IMecenateUsers.sol";
import "./interfaces/IMecenateFeedFactory.sol";
import "./interfaces/IMecenateBay.sol";
import "./interfaces/IMecenateTreasury.sol";

contract MecenateStats {
    struct Stats {
        uint256 totalUsers;
        uint256 totalBayRequests;
        uint256 totalFeeds;
        uint256 globalFee;
        uint256 fixedFee;
        uint256 treasuryBalance;
    }

    IMecenateUsers public mecenateUsers;
    IMecenateFeedFactory public mecenateFeedFactory;
    IMecenateBay public mecenateBay;
    IMecenateTreasury public mecenateTreasury;

    constructor(
        address _mecenateUsers,
        address _mecenateFeedFactory,
        address _mecenateBay,
        address _mecenateTreasury
    ) {
        mecenateUsers = IMecenateUsers(_mecenateUsers);
        mecenateFeedFactory = IMecenateFeedFactory(_mecenateFeedFactory);
        mecenateBay = IMecenateBay(_mecenateBay);
        mecenateTreasury = IMecenateTreasury(_mecenateTreasury);
    }

    function getStats() public view returns (Stats memory) {
        uint256 totalBayRequests = mecenateBay.contractCounter();
        uint256 totalFeeds = mecenateFeedFactory.contractCounter();

        return
            Stats(
                mecenateUsers.getUserCount(),
                totalBayRequests,
                totalFeeds,
                mecenateTreasury.globalFee(),
                mecenateTreasury.fixedFee(),
                address(mecenateTreasury).balance
            );
    }
}

