/**
 * @title MecenateTreasury
 * @dev This contract handles the treasury of the Mecenate platform, which is responsible for distributing funds to users and collecting fees.
 */
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "./modules/Swapper.sol";
import "./interfaces/IMecenateUsers.sol";

import "./interfaces/IMecenateVerifier.sol";

contract MecenateTreasury is Ownable, Swapper {
    using SafeERC20 for IERC20;

    uint256 public globalFee = 100;

    uint256 public fixedFee = 0.01 ether;

    uint256 public lastDistributed;

    uint256 public distributableBalance;

    uint256 public ownerFee;

    mapping(bytes32 => uint256) public userReward;

    function addFunds() external payable {
        // Aggiungi fondi al contratto e aggiorna il saldo distribuibile
        distributableBalance += msg.value;
    }

    function setGlobalFee(uint256 _globalFee) external onlyOwner {
        globalFee = _globalFee;
    }

    function setFixedFee(uint256 _fixedFee) external onlyOwner {
        fixedFee = _fixedFee;
    }

    function withdrawTokens(
        address _token,
        address _to,
        uint256 _amount
    ) external onlyOwner {
        IERC20(_token).safeTransfer(_to, _amount);
    }

    function withdrawNative(
        address payable _to,
        uint256 _amount
    ) external onlyOwner {
        _to.transfer(_amount);
    }

    function distribute(address _usersContract) external {
        require(
            block.timestamp - lastDistributed >= 1 days,
            "Can only distribute once a day"
        );
        require(distributableBalance > 0, "No distributable balance");

        uint256 fee = (distributableBalance * globalFee) / 10000;
        ownerFee += fee;

        uint256 total = distributableBalance - fee;
        uint256 userCount = IMecenateUsers(_usersContract).getUserCount();

        require(userCount > 0, "No users to distribute to");

        uint256 perIdentity = total / userCount;

        for (uint256 i = 0; i < userCount; i++) {
            bytes32 user = IMecenateUsers(_usersContract).getUserVaultIdAt(i);
            userReward[user] += perIdentity;
        }

        // Aggiorna le variabili di stato
        lastDistributed = block.timestamp;
        distributableBalance = 0; // Azzera il saldo distribuibile
    }

    function takeReward(
        address _receiver,
        address _verifierContract,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) external returns (uint256) {
        (
            bytes memory vaultId,
            ,
            ,
            bytes memory signedMessage
        ) = IMecenateVerifier(_verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        (address to, bytes32 nonce) = abi.decode(
            signedMessage,
            (address, bytes32)
        );

        require(_nonce == nonce, "Not Same Nonce");

        bytes32 _user = keccak256(vaultId);

        uint256 amountToSend = userReward[_user];

        userReward[_user] = 0;

        // send eth weith data
        (bool success, ) = payable(to).call{value: amountToSend}(
            sismoConnectResponse
        );

        return amountToSend;
    }

    function getReward(
        address _verifierContract,
        bytes32 encryptedVaultId
    ) external view returns (uint256) {
        return userReward[encryptedVaultId];
    }

    receive() external payable {
        // Aggiungi fondi al contratto e aggiorna il saldo distribuibile
        distributableBalance += msg.value;
    }
}

contracts/MecenateVault.sol.content:
/**
 * @title MecenateVault
 * @dev This contract allows users to deposit and withdraw ETH and ERC20 tokens, and pay ETH and ERC20 tokens to other addresses.
 * It also keeps track of the deposited balances for each user using a commitment hash.
 */
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "./interfaces/IMecenateVerifier.sol";
import "./interfaces/IMecenateFeedFactory.sol";

contract MecenateVault is Ownable, ReentrancyGuard {
    using Address for address;
    using Address for address payable;

    mapping(bytes32 => uint256) private ethDeposits;
    mapping(bytes32 => mapping(address => uint256)) private tokenDeposits;

    address public WETH;
    address public DAI;
    address public USDC;
    address public MUSE;
    address public verifierContract;
    address public factoryContract;
    address public mecenateBay;
    address public mecenateUsers;
    address private relayer;

    uint256 public relayerFeePercentage = 200;
    uint256 public constant MAX_RELAYER_FEE_PERCENTAGE = 500;

    event MetaTransactionExecuted(
        address userAddress,
        address relayerAddress,
        bytes functionSignature
    );

    event Withdrawn(
        bytes32 indexed commitment,
        uint256 amount,
        string secret,
        address token
    );

    modifier onlyRelayer() {
        require(msg.sender == relayer, "Not relayer");
        _;
    }

    constructor(
        address _verifierContract,
        address _factoryContract,
        address _bayContract,
        address _usersContract,
        address _relayer
    ) {
        verifierContract = _verifierContract;
        factoryContract = _factoryContract;
        mecenateBay = _bayContract;
        mecenateUsers = _usersContract;
        relayer = _relayer;
    }

    function setTokens(
        address _WETH,
        address _DAI,
        address _USDC,
        address _MUSE
    ) external onlyOwner {
        WETH = _WETH;
        DAI = _DAI;
        USDC = _USDC;
        MUSE = _MUSE;
    }

    function changeRelayerFee(
        uint256 _newRelayerFeePercentage
    ) external onlyOwner {
        require(
            _newRelayerFeePercentage <= MAX_RELAYER_FEE_PERCENTAGE,
            "New relayer fee percentage is too high"
        );
        relayerFeePercentage = _newRelayerFeePercentage;
    }

    function depositETH(bytes32 encryptedVaultId) public payable nonReentrant {
        // 1. Add the deposit to the correct deposit mapping
        ethDeposits[encryptedVaultId] += msg.value;
    }

    function depositToken(
        address _token,
        uint256 _amount,
        bytes32 encryptedVaultId
    ) external {
        require(_token != address(0), "Token address cannot be 0");

        require(_amount > 0, "Amount must be greater than zero");

        require(
            _token == WETH || _token == DAI || _token == USDC || _token == MUSE,
            "Token not supported"
        );

        // The user must first approve the token transfer
        // to this contract
        IERC20 token = IERC20(_token);

        require(
            token.transferFrom(msg.sender, address(this), _amount),
            "Token transfer failed"
        );

        // Add the deposited amount to the tokenDeposits mapping
        tokenDeposits[encryptedVaultId][_token] += _amount;
    }

    function withdrawETH(
        uint256 _amount,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) public onlyRelayer nonReentrant {
        uint256 initialGas = gasleft();

        (
            bytes memory vaultId,
            ,
            ,
            bytes memory signedMessage
        ) = IMecenateVerifier(verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        (address to, bytes32 nonce) = abi.decode(
            signedMessage,
            (address, bytes32)
        );

        require(to == _to, "Not Same Address");

        require(nonce == _nonce, "Not Same Nonce");

        bytes32 encryptedVaultId = keccak256(vaultId);

        uint256 totalRequired = _amount; // Initialize with _value

        totalRequired -= tx.gasprice * initialGas; // Add maximum possible gas cost

        require(
            ethDeposits[encryptedVaultId] >= totalRequired,
            "Not enough balance"
        );

        // 1. Verify that the commitment exists and the amount is not zero
        require(ethDeposits[encryptedVaultId] > 0, "Commitment does not exist");

        require(_amount > 0, "Amount must be greater than zero");

        // 2. Verify that the commitment has enough balance to withdraw from
        require(ethDeposits[encryptedVaultId] >= _amount, "Not enough balance");

        //decode signedMessage as address
        ethDeposits[encryptedVaultId] -= _amount;

        uint256 gasUsed = initialGas - gasleft();

        uint256 gasCost = gasUsed * tx.gasprice;

        uint256 relayerFee = (gasCost * relayerFeePercentage) / 10000;

        require(_amount >= gasCost + relayerFee, "Not enough balance for gas");

        uint256 newAmount = _amount - gasCost - relayerFee;

        (bool result, ) = payable(to).call{value: newAmount}("");
        require(result, "ETH transfer failed");

        (bool result2, ) = payable(msg.sender).call{
            value: gasCost + relayerFee
        }("");

        require(result2, "ETH transfer failed with gas");
    }

    function withdrawWithSecret(
        string memory _secret,
        address _token,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) public onlyRelayer nonReentrant {
        bytes32 commitment = keccak256(abi.encodePacked(_secret));
        uint256 amount;

        if (_token == address(0)) {
            amount = ethDeposits[commitment];
            require(amount > 0, "No ETH deposit for this secret");
            ethDeposits[commitment] = 0;
            payable(_to).transfer(amount);
        } else {
            amount = tokenDeposits[commitment][_token];
            require(amount > 0, "No Token deposit for this secret");
            tokenDeposits[commitment][_token] = 0;
            IERC20(_token).transfer(_to, amount);
        }

        emit Withdrawn(commitment, amount, _secret, _token);
    }

    function withdrawToken(
        address _token,
        uint256 _amount,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) public onlyRelayer nonReentrant {
        uint256 initialGas = gasleft();

        uint256 totalRequired = tx.gasprice * initialGas;

        (
            bytes memory vaultId,
            ,
            ,
            bytes memory signedMessage
        ) = IMecenateVerifier(verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        (address to, bytes32 nonce) = abi.decode(
            signedMessage,
            (address, bytes32)
        );

        require(
            ethDeposits[keccak256(vaultId)] >= totalRequired,
            "Not enough ETH for gas required"
        );

        require(to == _to, "Not Same Address");

        require(nonce == _nonce, "Not Same Nonce");

        // Check if the commitment exists
        // and the amount is greater than the deposit.
        require(
            tokenDeposits[keccak256(vaultId)][_token] >= _amount,
            "Not enough token balance"
        );

        // Decrease the token balance for the commitment.
        tokenDeposits[keccak256(vaultId)][_token] -= _amount;

        // Transfer the tokens to msg.sender or operator.
        IERC20(_token).transfer(to, _amount);

        uint256 gasUsed = initialGas - gasleft();

        uint256 gasCost = gasUsed * tx.gasprice;

        uint256 relayerFee = (gasCost * relayerFeePercentage) / 10000;

        require(
            ethDeposits[keccak256(vaultId)] >= gasCost + relayerFee,
            "Not enough balance for gas used"
        );

        ethDeposits[keccak256(vaultId)] -= gasCost + relayerFee;

        (bool result, ) = payable(msg.sender).call{value: gasCost + relayerFee}(
            ""
        );

        require(result, "ETH transfer failed with gas");
    }

    function getEthDeposit(
        bytes32 encryptedVaultId
    ) public view returns (uint256) {
        return ethDeposits[encryptedVaultId];
    }

    receive() external payable {
        revert("Use depositETH function");
    }

    fallback() external payable {
        require(msg.data.length > 0, "Data required for Sismo verification.");
        bytes32 encryptedVaultId = abi.decode(msg.data, (bytes32));
        ethDeposits[encryptedVaultId] += msg.value;
    }

    function getTokenDeposit(
        address _token,
        bytes32 encryptedVaultId
    ) public view returns (uint256) {
        return tokenDeposits[encryptedVaultId][_token];
    }

    function execute(
        address _target,
        bytes calldata _data,
        uint256 _value,
        bytes32 _encryptedVaultId
    ) external onlyRelayer nonReentrant returns (bool) {
        // Reduce storage reads by using a memory variable
        uint256 availableBalance = ethDeposits[_encryptedVaultId];

        // Estimate total required balance
        uint256 totalRequired = _value + (tx.gasprice * gasleft());

        // Check if the vault has enough balance
        require(availableBalance >= totalRequired, "Insufficient balance");

        // Execute the call
        if (_data.length == 0) {
            payable(_target).sendValue(_value);
        } else {
            if (_value == 0) {
                _target.functionCall(_data);
            } else {
                _target.functionCallWithValue(_data, _value);
            }
        }

        // Update available balance
        availableBalance -= _value;

        // Calculate gas costs and relayer fee
        uint256 gasUsed = totalRequired - _value - (tx.gasprice * gasleft());
        uint256 relayerFee = (gasUsed * relayerFeePercentage) / 10000;

        // Check again if the vault has enough balance to cover the gas and relayer fee
        require(
            availableBalance >= gasUsed + relayerFee,
            "Insufficient balance for gas and fee"
        );

        // Update storage only once to reflect all changes
        ethDeposits[_encryptedVaultId] =
            availableBalance -
            gasUsed -
            relayerFee;

        // Transfer fee to the relayer
        (bool result, ) = payable(msg.sender).call{value: gasUsed + relayerFee}(
            ""
        );
        require(result, "ETH transfer failed");

        return true;
    }

    function setRelayer(address _relayer) external onlyOwner {
        relayer = _relayer;
    }

    function setVerifierContract(address _verifierContract) external onlyOwner {
        verifierContract = _verifierContract;
    }

    function setFactoryContract(address _factoryContract) external onlyOwner {
        factoryContract = _factoryContract;
    }

    function setMecenateBay(address _mecenateBay) external onlyOwner {
        mecenateBay = _mecenateBay;
    }

    function setMecenateUsers(address _mecenateUsers) external onlyOwner {
        mecenateUsers = _mecenateUsers;
    }
}

contracts/modules/Acceptance.sol.content:
/**
 * @title Acceptance
 * @dev This contract is an abstract contract that inherits from Events and Staking contracts. It provides a function to accept a post and add stake to the post. The function accepts a Sismo Connect response as a parameter and verifies the user existence. It also checks if the user is not the seller and if the post is in Proposed status. If the post has an escrow payment, it checks if the buyer has paid enough and adds the payment to the post's escrow. If the post does not have an escrow payment, it requires a payment from the buyer and adds it to the post's escrow. Finally, it changes the post status to Accepted and emits an Accepted event.
 */
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "./Events.sol";
import "./Staking.sol";

abstract contract Acceptance is Events, Staking {
    function acceptPost(
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce,
        Structures.Tokens tokenId,
        uint256 _paymentAmount
    ) external payable virtual {
        require(
            validStatuses[uint8(Structures.PostStatus.Proposed)],
            "INVALID_STATUS"
        );
        _checkToken(tokenId);

        uint256 amountToAdd = tokenId == Structures.Tokens.NaN
            ? msg.value
            : _paymentAmount;
        bytes32 sellerVaultIdHash = keccak256(postSettingPrivate.vaultIdSeller);

        // Get encryptedVaultId only once
        (
            bytes memory vaultId,
            uint256 twitterId,
            uint256 telegramId,

        ) = _verifyNonce(sismoConnectResponse, _to, _nonce);
        bytes32 encryptedVaultId = keccak256(vaultId);

        // Use local variable for repeated calls
        uint256 sellerStake = Deposit._getDeposit(tokenId, sellerVaultIdHash);

        require(sellerStake >= post.postdata.escrow.stake, "STAKE_INCORRECT");
        require(
            IMecenateUsers(settings.usersModuleContract).checkifUserExist(
                encryptedVaultId
            ),
            "USERT_NOT_EXIST"
        );
        require(encryptedVaultId != sellerVaultIdHash, "YOU_ARE_THE_SELLER");

        if (post.postdata.escrow.payment > 0) {
            require(
                _paymentAmount >= post.postdata.escrow.payment,
                "NOT_ENOUGH_PAYMENT"
            );
        } else {
            require(msg.value > 0, "ZERO_MSGVALUE");
            require(_paymentAmount > 0, "ZERO_PAYMENT");
        }

        uint256 payment = _addStake(
            tokenId,
            encryptedVaultId,
            msg.sender,
            amountToAdd
        );

        // Update all at once
        post.postdata.escrow.payment = payment;
        post.postdata.settings.status = Structures.PostStatus.Accepted;
        _changeStatus(Structures.PostStatus.Accepted);

        // Update private settings
        postSettingPrivate.vaultIdBuyer = vaultId;
        postSettingPrivate.buyerTwitterId = twitterId;
        postSettingPrivate.buyerTelegramId = telegramId;

        emit Accepted(post);
    }
}

contracts/modules/BurnDAI.sol.content:
pragma solidity 0.8.19;

import "./BurnMUSE.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

abstract contract BurnDAI is BurnMUSE {
    using SafeMath for uint256;

    function _burnFrom(address from, uint256 value) internal override {
        IERC20(IMecenateFeedFactory(settings.factoryContract).daiToken())
            .transferFrom(from, address(this), value);

        _burn(value);
    }

    function _burnDai(uint256 value) internal {
        // approve uniswap for token transfer
        IERC20(IMecenateFeedFactory(settings.factoryContract).daiToken())
            .approve(settings.router, value);

        // swap dai for MUSE
        uint256 tokens_sold = value;

        uint256 tokens_bought = _swapTokensForToken(
            getTokenAddress(),
            IMecenateFeedFactory(settings.factoryContract).wethToken(),
            uint24(
                IMecenateFeedFactory(settings.factoryContract).getRouterFee(0)
            ),
            tokens_sold
        );

        uint256 tokens_sold_to_muse = _swapTokensForToken(
            IMecenateFeedFactory(settings.factoryContract).wethToken(),
            BurnMUSE.getTokenAddress(),
            uint24(
                IMecenateFeedFactory(settings.factoryContract).getRouterFee(1)
            ),
            tokens_bought
        );

        // burn MUSE
        BurnMUSE._burn(tokens_sold_to_muse);
    }

    function getTokenAddress()
        internal
        view
        virtual
        override
        returns (address token)
    {
        return IMecenateFeedFactory(settings.factoryContract).daiToken();
    }

    function getExchangeAddress()
        internal
        view
        override
        returns (address exchange)
    {
        exchange = settings.router;
    }

    function _swapTokensForToken(
        address tokenIn,
        address tokenOut,
        uint24 fee,
        uint256 amountIn
    ) internal returns (uint256 amountOut) {
        // Check and approve allowance
        uint256 allowance = IERC20(tokenIn).allowance(
            address(this),
            settings.router
        );
        if (allowance < amountIn) {
            IERC20(tokenIn).approve(settings.router, type(uint256).max);
        }

        // Prepare parameters
        address recipient = address(this);
        uint256 deadline = block.timestamp + 15; // 15 seconds from the current block timestamp

        // Perform the swap
        // Refer to the Uniswap V3 documentation for details on the params.
        amountOut = ISwapRouter(settings.router).exactInputSingle(
            ISwapRouter.ExactInputSingleParams({
                tokenIn: tokenIn,
                tokenOut: tokenOut,
                fee: fee,
                recipient: recipient,
                deadline: deadline,
                amountIn: amountIn,
                amountOutMinimum: 0, // you may want to set a minimum here
                sqrtPriceLimitX96: 0
            })
        );

        return amountOut;
    }
}

contracts/modules/BurnMUSE.sol.content:
pragma solidity 0.8.19;
import "../interfaces/IMUSE.sol";
import "../interfaces/IMecenateFeedFactory.sol";
import "./Events.sol";

abstract contract BurnMUSE is Events {
    function _burn(uint256 value) internal virtual {
        require(
            IMUSE(IMecenateFeedFactory(settings.factoryContract).museToken())
                .burn(value),
            "muse burn failed"
        );
    }

    function _burnFrom(address from, uint256 value) internal virtual {
        IMUSE(IMecenateFeedFactory(settings.factoryContract).museToken())
            .transferFrom(from, address(this), value);
        _burn(value);
    }

    function getTokenAddress() internal view virtual returns (address token) {
        return IMecenateFeedFactory(settings.factoryContract).museToken();
    }

    function getExchangeAddress()
        internal
        view
        virtual
        returns (address exchange)
    {
        exchange = IMecenateFeedFactory(settings.factoryContract).router();
    }
}

contracts/modules/Creation.sol.content:
/**
 * @title Creation
 * @dev This contract is an abstract contract that inherits from Staking contract. It provides a function to create a post with the given parameters. The function requires the user to be the owner of the post and to have a stake. The post can have different durations and types. The function returns the created post.
 */
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./Staking.sol";

abstract contract Creation is Staking {
    function createPost(
        bytes memory encryptedHash,
        Structures.PostType postType,
        Structures.PostDuration postDuration,
        uint256 payment,
        uint256 stakeAmount,
        Structures.Tokens tokenId,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    )
        external
        payable
        onlyValidTokenID(tokenId)
        returns (Structures.Post memory)
    {
        (
            bytes memory vaultId,
            uint256 twitterId,
            uint256 telegramId,

        ) = _verifyNonce(sismoConnectResponse, _to, _nonce);
        bytes32 encryptedVaultId = keccak256(vaultId);

        // Early exit conditions
        require(encryptedVaultId == owner, "NOT_OWNER");
        require(
            IMecenateUsers(settings.usersModuleContract).checkifUserExist(
                encryptedVaultId
            ),
            "USER_NOT_EXIST"
        );
        require(
            validStatuses[uint8(post.postdata.settings.status)],
            "INVALID_STATUS"
        );
        require(stakeAmount > 0, "STAKE_AMOUNT_ZERO");

        uint256 duration = postDurationToDays[uint8(postDuration)];
        uint256 stake = _addStake(
            tokenId,
            encryptedVaultId,
            msg.sender,
            stakeAmount
        );

        // Change status to Proposed
        _changeStatus(Structures.PostStatus.Proposed);

        // Initialize the new Post struct with named arguments for clarity
        Structures.Post memory newPost = Structures.Post({
            creator: Structures.User({vaultId: encryptedVaultId}),
            postdata: Structures.PostData({
                settings: Structures.PostSettings({
                    postType: postType,
                    status: Structures.PostStatus.Proposed,
                    creationTimeStamp: block.timestamp,
                    endTimeStamp: 0,
                    duration: duration,
                    tokenId: tokenId
                }),
                escrow: Structures.PostEscrow({
                    stake: stake,
                    payment: payment,
                    punishment: 0,
                    penalty: 0
                }),
                data: Structures.PostEncryptedData({
                    encryptedData: encryptedHash,
                    encryptedKey: ZEROHASH,
                    decryptedData: ZEROHASH
                })
            })
        });

        // Update storage and emit event
        post = newPost;
        settings.postCount++;

        postSettingPrivate.vaultIdSeller = vaultId;
        postSettingPrivate.sellerTwitterId = twitterId;
        postSettingPrivate.sellerTelegramId = telegramId;

        emit Created(newPost);

        return newPost;
    }
}

contracts/modules/Data.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "../interfaces/IMecenateFeed.sol";
import "../interfaces/IMecenateUsers.sol";
import "../interfaces/IMecenateTreasury.sol";
import "../interfaces/IMecenateFeedFactory.sol";
import "../interfaces/IMecenateVerifier.sol";

// import openzeppelin ECDSA
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

/**
 * @title Data
 * @dev This contract stores data related to Mecenate posts and provides functions to interact with it.
 */
contract Data {
    bytes internal constant ZEROHASH = "0x00";

    bytes32 public owner;

    Structures.Post public post;
    Structures.PostSettingPrivate internal postSettingPrivate;
    Structures.FeedSettings internal settings;

    mapping(uint8 => uint256) internal postDurationToDays;
    mapping(uint8 => bool) internal validStatuses;

    function onlyVault() internal view {
        require(msg.sender == settings.vaultContract, "ONLY_VAULT");
    }

    constructor(
        address _usersModuleContract,
        address _verifierContract,
        address _vaultContract,
        address _factoryContract,
        string memory _version
    ) {
        settings.punishmentRatio = 100000000000000000; // Constant value
        settings.postCount = 0; // Initialize postCount to 0
        settings.usersModuleContract = _usersModuleContract;
        settings.verifierContract = _verifierContract;
        settings.vaultContract = _vaultContract;
        settings.factoryContract = msg.sender;
        settings.router = IMecenateFeedFactory(_factoryContract).router();
        settings.encodedSymKey = ZEROHASH;
        settings.lastMessageForBuyer = ZEROHASH;
        settings.lastMessageForSeller = ZEROHASH;
        post.postdata.settings.status = Structures.PostStatus.Waiting;

        postDurationToDays[uint8(Structures.PostDuration.OneDay)] = 1 days;
        postDurationToDays[uint8(Structures.PostDuration.ThreeDays)] = 3 days;
        postDurationToDays[uint8(Structures.PostDuration.OneWeek)] = 7 days;
        postDurationToDays[uint8(Structures.PostDuration.TwoWeeks)] = 14 days;
        postDurationToDays[uint8(Structures.PostDuration.OneMonth)] = 30 days;
        validStatuses[uint8(Structures.PostStatus.Waiting)] = true;

        settings.version = _version;
    }

    function version() external view returns (string memory) {
        return settings.version;
    }

    function _changeStatus(Structures.PostStatus _newStatus) internal {
        validStatuses[uint8(post.postdata.settings.status)] = false;
        validStatuses[uint8(_newStatus)] = true;
        post.postdata.settings.status = _newStatus;
    }

    function _verifyNonce(
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) internal view returns (bytes memory, uint256, uint256, bytes memory) {
        (
            bytes memory vaultId,
            uint256 twitterId,
            uint256 telegramId,
            bytes memory signedMessage
        ) = IMecenateVerifier(settings.verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        (, bytes32 nonce) = abi.decode(signedMessage, (address, bytes32));

        require(_nonce == nonce, "WRONG_NONCE");

        return (vaultId, twitterId, telegramId, signedMessage);
    }

    function sismoVerify(
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) internal view returns (bytes memory, uint256, uint256, bytes memory) {
        (
            bytes memory vaultId,
            uint256 twitterId,
            uint256 telegramId,
            bytes memory signedMessage
        ) = IMecenateVerifier(settings.verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        return (vaultId, twitterId, telegramId, signedMessage);
    }

    function getStatus() external view returns (Structures.PostStatus) {
        return post.postdata.settings.status;
    }

    function getTokenId() external view returns (Structures.Tokens) {
        return post.postdata.settings.tokenId;
    }

    function getTokenIdAddress() public view returns (address) {
        if (post.postdata.settings.tokenId == Structures.Tokens.MUSE) {
            return IMecenateFeedFactory(settings.factoryContract).museToken();
        } else if (post.postdata.settings.tokenId == Structures.Tokens.DAI) {
            return IMecenateFeedFactory(settings.factoryContract).daiToken();
        } else {
            return address(0);
        }
    }

    function _checkToken(Structures.Tokens _token) internal view {
        require(_token == post.postdata.settings.tokenId, "WRONG_TOKEN");
    }

    function getPaymentRequested() external view returns (uint256) {
        return post.postdata.escrow.payment;
    }

    function getStakeRequested() external view returns (uint256) {
        return post.postdata.escrow.stake;
    }

    function postCount() external view returns (uint256) {
        return settings.postCount;
    }

    receive() external payable {}
}

contracts/modules/Deposit.sol.content:
pragma solidity 0.8.19;
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./TokenManager.sol";

contract Deposit {
    using SafeMath for uint256;

    mapping(uint256 => mapping(bytes32 => uint256)) private _deposit;

    event DepositIncreased(
        Structures.Tokens tokenID,
        bytes32 user,
        uint256 amount,
        uint256 newDeposit
    );
    event DepositDecreased(
        Structures.Tokens tokenID,
        bytes32 user,
        uint256 amount,
        uint256 newDeposit
    );

    function _increaseDeposit(
        Structures.Tokens tokenID,
        bytes32 user,
        uint256 amountToAdd
    ) internal returns (uint256 newDeposit) {
        // calculate new deposit amount
        newDeposit = _deposit[uint256(tokenID)][user].add(amountToAdd);

        // set new stake to storage
        _deposit[uint256(tokenID)][user] = newDeposit;

        // emit event
        emit DepositIncreased(tokenID, user, amountToAdd, newDeposit);

        // return
        return newDeposit;
    }

    function _decreaseDeposit(
        Structures.Tokens tokenID,
        bytes32 user,
        uint256 amountToRemove
    ) internal returns (uint256 newDeposit) {
        // get current deposit
        uint256 currentDeposit = _deposit[uint256(tokenID)][user];

        // check if sufficient deposit
        require(currentDeposit >= amountToRemove, "INSUFFICIENT_DEPOSIT");

        // calculate new deposit amount
        newDeposit = currentDeposit.sub(amountToRemove);

        // set new stake to storage
        _deposit[uint256(tokenID)][user] = newDeposit;

        // emit event
        emit DepositDecreased(tokenID, user, amountToRemove, newDeposit);

        // return
        return newDeposit;
    }

    function _clearDeposit(
        Structures.Tokens tokenID,
        bytes32 user
    ) internal returns (uint256 amountRemoved) {
        // get current deposit
        uint256 currentDeposit = _deposit[uint256(tokenID)][user];

        // remove deposit
        _decreaseDeposit(tokenID, user, currentDeposit);

        // return
        return currentDeposit;
    }

    function _getDeposit(
        Structures.Tokens tokenID,
        bytes32 user
    ) internal view returns (uint256 deposit) {
        return _deposit[uint256(tokenID)][user];
    }
}

