/**
 * @title Swapper
 * @dev This contract provides functions for swapping tokens on PancakeSwap and adding liquidity to a token pair.
 */
// SPDX-License-Identifier: MIT OR Apache-2.0

pragma solidity 0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@uniswap/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol";

abstract contract Swapper is Ownable {
    bool public splitLiquidity;

    ISwapRouter public swapRouter;
    INonfungiblePositionManager public positionManager;
    address public native;

    function _giveAllowances() internal {
        IERC20(address(native)).approve(address(swapRouter), 0);
        IERC20(address(native)).approve(
            address(swapRouter),
            uint256(2 ** 256 - 1)
        );
    }

    function customApprove(address _token, uint256 _amount) external onlyOwner {
        IERC20(_token).approve(address(swapRouter), _amount);
        IERC20(address(_token)).approve(
            address(swapRouter),
            uint256(2 ** 256 - 1)
        );
    }

    function configLiquidityProvider(
        address _native,
        ISwapRouter _swapRouter
    ) external onlyOwner {
        native = _native;
        swapRouter = _swapRouter;
        splitLiquidity = true;
        _giveAllowances();
    }

    function setSplitLiqudity(
        bool _splitLiquidity
    ) external onlyOwner returns (bool) {
        splitLiquidity = _splitLiquidity;
        return true;
    }

    function swapTokensForTokens(
        address token1,
        address token2,
        uint256 amount,
        uint24 fee
    ) external onlyOwner returns (uint256) {
        return _swapTokenForToken(token1, token2, fee, amount);
    }

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint24 fee,
        uint256 amountA,
        uint256 amountB,
        int24 tickLower,
        int24 tickUpper
    ) public onlyOwner {
        _addLiquidity(
            tokenA,
            tokenB,
            fee,
            amountA,
            amountB,
            tickLower,
            tickUpper
        );
    }

    function _swapTokenToETH(
        address token,
        uint256 amountIn,
        uint24 fee
    ) private returns (uint256) {
        // Approve tokens for the swap
        IERC20(token).approve(address(swapRouter), amountIn);

        // Params for the swap
        ISwapRouter.ExactInputSingleParams memory params = ISwapRouter
            .ExactInputSingleParams(
                token, // tokenIn
                native, // tokenOut
                fee, // fee
                address(this), // recipient
                block.timestamp + 10, // deadline
                amountIn, // amountIn
                0, // amountOutMinimum
                0 // sqrtPriceLimitX96
            );

        // Perform the swap and return the amount received
        uint256 amountOut = swapRouter.exactInputSingle(params);
        return amountOut;
    }

    function _addLiquidity(
        address tokenA,
        address tokenB,
        uint24 fee,
        uint256 amountA,
        uint256 amountB,
        int24 tickLower,
        int24 tickUpper
    ) internal {
        // Approve amounts to positionManager
        IERC20(tokenA).approve(address(positionManager), amountA);
        IERC20(tokenB).approve(address(positionManager), amountB);

        // Parameters for adding liquidity
        INonfungiblePositionManager.MintParams
            memory params = INonfungiblePositionManager.MintParams(
                tokenA,
                tokenB,
                fee,
                tickLower,
                tickUpper,
                amountA,
                amountB,
                0, // amount0Min
                0, // amount1Min
                address(this),
                block.timestamp // Deadline
            );

        // Add liquidity
        (uint256 tokenId, , , ) = positionManager.mint(params);
    }

    function _swapTokenForToken(
        address tokenIn,
        address tokenOut,
        uint24 fee,
        uint256 amountIn
    ) internal returns (uint256) {
        // Approve amount to swapRouter
        IERC20(tokenIn).approve(address(swapRouter), amountIn);

        // Parameters for the swap
        ISwapRouter.ExactInputSingleParams memory params = ISwapRouter
            .ExactInputSingleParams(
                tokenIn,
                tokenOut,
                fee,
                address(this),
                block.timestamp + 10, // Deadline
                amountIn, // amountIn
                0, // amountOutMinimum
                0 // sqrtPriceLimitX96
            );

        // Perform the swap
        uint256 amountOut = swapRouter.exactInputSingle(params);

        return amountOut;
    }
}

contracts/modules/TokenManager.sol.content:
pragma solidity 0.8.19;

import "./BurnDAI.sol";

abstract contract TokenManager is BurnDAI {
    function _getTokenAddress(
        Structures.Tokens tokenID
    ) internal view returns (address) {
        if (tokenID == Structures.Tokens.DAI) return BurnDAI.getTokenAddress();
        if (tokenID == Structures.Tokens.MUSE)
            return BurnMUSE.getTokenAddress();
        return address(0);
    }

    function _checkAllowance(
        Structures.Tokens tokenID,
        address from,
        address to,
        uint256 value
    ) internal view {
        address tokenAddress = _getTokenAddress(tokenID);
        require(
            IERC20(tokenAddress).allowance(from, to) >= value,
            "INSUFFICIENT_ALLOWANCE"
        );
    }

    modifier onlyValidTokenID(Structures.Tokens tokenID) {
        require(
            tokenID == Structures.Tokens.MUSE ||
                tokenID == Structures.Tokens.DAI ||
                tokenID == Structures.Tokens.NaN,
            "WRONG_TOKEN"
        );
        _;
    }

    function isValidTokenID(
        Structures.Tokens tokenID
    ) internal pure returns (bool validity) {
        return
            tokenID == Structures.Tokens.MUSE ||
            tokenID == Structures.Tokens.DAI;
    }

    function _transfer(
        Structures.Tokens tokenID,
        address to,
        uint256 value
    ) internal onlyValidTokenID(tokenID) {
        require(
            IERC20(_getTokenAddress(tokenID)).transfer(to, value),
            "TRANSFER_FAILED"
        );
    }

    function _transferFrom(
        Structures.Tokens tokenID,
        address from,
        address to,
        uint256 value
    ) internal onlyValidTokenID(tokenID) {
        address tokenAddress = _getTokenAddress(tokenID);
        _checkAllowance(tokenID, from, to, value);
        require(
            IERC20(tokenAddress).transferFrom(from, to, value),
            "TRANSFER_FAILED"
        );
    }

    function _burn(
        Structures.Tokens tokenID,
        address _receiver,
        uint256 value
    ) internal onlyValidTokenID(tokenID) {
        address treasury = IMecenateFeedFactory(settings.factoryContract)
            .treasuryContract();

        IMecenateFeedFactory factory = IMecenateFeedFactory(
            settings.factoryContract
        );

        if (tokenID == Structures.Tokens.NaN) {
            (bool result, ) = payable(_receiver).call{value: value}("");
            require(result, "Transfer failed.");
            return;
        }

        address tokenAddress = (tokenID == Structures.Tokens.DAI)
            ? BurnDAI.getTokenAddress()
            : BurnMUSE.getTokenAddress();

        if (factory.burnEnabled() == false) {
            IERC20(tokenAddress).transfer(treasury, value);
            return;
        }

        if (tokenID == Structures.Tokens.DAI) {
            BurnDAI._burnDai(value);
        } else if (tokenID == Structures.Tokens.MUSE) {
            BurnMUSE._burn(value);
        }
    }

    function _burnFrom(
        Structures.Tokens tokenID,
        address from,
        uint256 value
    ) internal onlyValidTokenID(tokenID) {
        if (tokenID == Structures.Tokens.DAI) {
            BurnDAI._burnFrom(from, value);
        } else if (tokenID == Structures.Tokens.MUSE) {
            BurnMUSE._burnFrom(from, value);
        }
    }

    function _approve(
        Structures.Tokens tokenID,
        address spender,
        uint256 value
    ) internal onlyValidTokenID(tokenID) {
        if (tokenID == Structures.Tokens.DAI) {
            require(
                IERC20(BurnDAI.getTokenAddress()).approve(spender, value),
                "APPROVE_FAILED"
            );
        } else if (tokenID == Structures.Tokens.MUSE) {
            address MUSE = BurnMUSE.getTokenAddress();
            uint256 currentAllowance = IMUSE(MUSE).allowance(
                msg.sender,
                spender
            );

            uint256 newAllowance = currentAllowance + value;
            require(
                IMUSE(MUSE).increaseAllowance(spender, newAllowance),
                "APPROVE_FAILED"
            );
        }
    }

    function totalSupply(
        Structures.Tokens tokenID
    ) internal view onlyValidTokenID(tokenID) returns (uint256 value) {
        return IERC20(_getTokenAddress(tokenID)).totalSupply();
    }

    function balanceOf(
        Structures.Tokens tokenID,
        address who
    ) internal view onlyValidTokenID(tokenID) returns (uint256 value) {
        return IERC20(_getTokenAddress(tokenID)).balanceOf(who);
    }

    function allowance(
        Structures.Tokens tokenID,
        address owner,
        address spender
    ) internal view onlyValidTokenID(tokenID) returns (uint256 value) {
        return IERC20(_getTokenAddress(tokenID)).allowance(owner, spender);
    }
}
