}

contracts/modules/Finalization.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;
import "./Staking.sol";

abstract contract Finalization is Staking {
    function finalizePost(
        bool valid,
        uint256 punishment,
        bytes32 uid
    ) external virtual {
        require(msg.sender == post.postdata.escrow.buyer, "NOT_SELLER");

        require(
            post.postdata.settings.status == Structures.PostStatus.Submitted,
            "NOT_SUBMITTED"
        );

        IEAS eas = IEAS(
            IMecenateFeedFactory(settings.factoryContract).easContract()
        );

        Attestation memory attestation = eas.getAttestation(uid);

        require(
            attestation.attester == post.postdata.escrow.buyer,
            "INVALID_ATTESTATION"
        );

        require(
            attestation.recipient == post.postdata.escrow.seller,
            "INVALID_RECIPIENT"
        );

        require(
            attestation.schema ==
                IMecenateFeedFactory(settings.factoryContract).easSchema(),
            "INVALID_SCHEMA"
        );

        (bool easResult, address feed, bytes memory postBytes) = abi.decode(
            attestation.data,
            (bool, address, bytes)
        );

        require(feed == address(this), "INVALID_FEED");

        require(
            keccak256(abi.encode(postBytes)) ==
                keccak256(abi.encode(post.postdata.data.encryptedData)),
            "INVALID_POST"
        );

        // Common contract addresses and variables
        address treasuryContract = IMecenateFeedFactory(
            settings.factoryContract
        ).treasuryContract();

        uint256 buyerFee = (post.postdata.escrow.payment *
            IMecenateTreasury(treasuryContract).globalFee()) / 10000;

        uint256 amountToAdd = post.postdata.escrow.payment - buyerFee;

        // Variables for stake changes
        uint256 buyerStake;

        uint256 sellerStake;

        if (post.postdata.settings.endTimeStamp < block.timestamp || valid) {
            require(easResult == valid, "INVALID_ATTESTATION");

            // Code for both the timeout and the valid case
            buyerStake = Deposit._decreaseDeposit(
                post.postdata.settings.tokenId,
                post.postdata.escrow.buyer,
                post.postdata.escrow.payment
            );

            sellerStake = Deposit._increaseDeposit(
                post.postdata.settings.tokenId,
                post.postdata.escrow.seller,
                amountToAdd
            );

            _burn(post.postdata.settings.tokenId, treasuryContract, buyerFee);

            post.postdata.escrow.stake = sellerStake;

            post.postdata.escrow.payment = buyerStake;

            _changeStatus(
                valid
                    ? Structures.PostStatus.Finalized
                    : Structures.PostStatus.Punished
            );

            emit Valid(post);
        } else if (!valid) {
            require(easResult == valid, "INVALID_ATTESTATION");

            require(
                punishment <= post.postdata.escrow.stake,
                "PUNISHMENT_TOO_HIGH"
            );
            require(settings.punishmentRatio < 1e18, "PUNISHMENT_RATIO_HIGH");

            uint256 penalty = (punishment * settings.punishmentRatio) / 1e18;

            post.postdata.escrow.payment = _burnStake(
                post.postdata.settings.tokenId,
                post.postdata.escrow.buyer,
                penalty
            );

            post.postdata.escrow.stake = _burnStake(
                post.postdata.settings.tokenId,
                post.postdata.escrow.seller,
                punishment
            );

            // Update status and penalties
            post.postdata.settings.status = Structures.PostStatus.Punished;

            post.postdata.escrow.punishment = punishment;

            post.postdata.escrow.penalty = penalty;

            _changeStatus(Structures.PostStatus.Punished);

            emit Invalid(post);
        }
    }
}

contracts/modules/Renounce.sol.content:
/**
 * @title Renounce
 * @dev This abstract contract extends the Staking contract and provides functionality for a seller to renounce a post.
 * The seller can only renounce a post if they are the original creator of the post and the post is in the Accepted or Submitted status.
 * Renouncing a post refunds the buyer's payment and transfers the seller's stake to the seller's address.
 * The post struct is reset and the post status is updated to Renounced.
 * This contract also provides an internal function for refunding a post, which is called by the renouncePost function.
 */
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "./Staking.sol";

abstract contract Renounce is Staking {
    function renouncePost() external {
        require(msg.sender == post.postdata.escrow.seller, "NOT_SELLER");

        // Validate the post status
        Structures.PostStatus currentStatus = post.postdata.settings.status;

        require(validStatuses[uint8(currentStatus)], "INVALID_STATUS");

        // Reset post and post settings
        post = Structures.Post(
            Structures.User({
                evmAddress: address(0),
                sismoVaultId: ZEROHASH,
                publicKey: ZEROHASH
            }),
            Structures.PostData({
                settings: Structures.PostSettings({
                    status: Structures.PostStatus.Renounced,
                    postType: Structures.PostType.Text,
                    creationTimeStamp: 0,
                    endTimeStamp: 0,
                    duration: 0,
                    tokenId: Structures.Tokens.NaN
                }),
                escrow: Structures.PostEscrow({
                    buyer: address(0),
                    seller: address(0),
                    stake: 0,
                    payment: 0,
                    punishment: 0,
                    penalty: 0
                }),
                data: Structures.PostEncryptedData({
                    encryptedData: "",
                    encryptedKey: "",
                    decryptedData: ""
                })
            })
        );

        // Emit event
        emit Renounced(post);
    }
}

contracts/modules/Staking.sol.content:
/**
 * @title Staking
 * @dev This contract handles staking functionality for the Mecenate platform.
 * It allows users to add, take, and burn stakes, and provides functions to get the total staked amount and individual stake amounts.
 * It also includes a modifier to check the status of a post before allowing stake-related transactions.
 */
pragma solidity 0.8.19;

import "./Deposit.sol";
import "./Events.sol";

abstract contract Staking is Events, Deposit, TokenManager {
    using SafeMath for uint256;

    event StakeBurned(address staker, uint256 amount);

    event StakeTaken(address staker, uint256 amount, Structures.Tokens tokenId);

    event StakeAdded(address staker, uint256 amount, Structures.Tokens tokenId);

    function _addStake(
        Structures.Tokens tokenId,
        address staker,
        address funder,
        uint256 amountToAdd
    ) internal returns (uint256 newStake) {
        Structures.PostStatus currentStatus = post.postdata.settings.status;

        require(
            currentStatus != Structures.PostStatus.Accepted &&
                currentStatus != Structures.PostStatus.Submitted,
            "INVALID_STATUS"
        );

        require(amountToAdd > 0, "STAKE_REQUIRED");

        if (tokenId != Structures.Tokens.NaN) {
            _transferFrom(tokenId, funder, address(this), amountToAdd);
        }

        newStake = Deposit._increaseDeposit(tokenId, staker, amountToAdd);

        emit StakeAdded(staker, amountToAdd, tokenId);

        return newStake;
    }

    function _takeStake(
        Structures.Tokens tokenId,
        address staker,
        address receiver,
        uint256 amountToTake
    ) internal returns (uint256 newStake) {
        // Memorizza lo status del post in una variabile per evitare accessi ridondanti allo storage
        Structures.PostStatus currentStatus = post.postdata.settings.status;

        // Verifica che lo status del post sia valido per procedere
        require(
            currentStatus != Structures.PostStatus.Accepted &&
                currentStatus != Structures.PostStatus.Submitted,
            "INVALID_STATUS"
        );

        // Effettua il trasferimento del token o dell'Ether
        if (tokenId == Structures.Tokens.NaN) {
            //(bool result, ) = payable(_to).call{value: amountToTake}("");
            //require(result, "CALL_FAILED");
            payable(receiver).transfer(amountToTake);
        } else {
            _transfer(tokenId, receiver, amountToTake);
        }

        // Aggiorna il deposito e emette un evento
        newStake = Deposit._decreaseDeposit(tokenId, staker, amountToTake);
        emit StakeTaken(staker, amountToTake, tokenId);

        return newStake;
    }

    function _takeFullStake(
        Structures.Tokens tokenId,
        address staker,
        address receiver
    ) internal returns (uint256 amountTaken) {
        uint256 currentDeposit = Deposit._getDeposit(tokenId, staker);

        uint256 newStake = _takeStake(
            tokenId,
            staker,
            receiver,
            currentDeposit
        );

        return newStake;
    }

    function _burnStake(
        Structures.Tokens tokenId,
        address staker,
        uint256 amountToBurn
    ) internal returns (uint256 newStake) {
        uint256 newDeposit = Deposit._decreaseDeposit(
            tokenId,
            staker,
            amountToBurn
        );

        bool burnEnabled = IMecenateFeedFactory(settings.factoryContract)
            .burnEnabled();

        address treasuryContract = IMecenateFeedFactory(
            settings.factoryContract
        ).treasuryContract();

        if (burnEnabled == false) {
            if (tokenId == Structures.Tokens.NaN) {
                (bool result, ) = payable(treasuryContract).call{
                    value: amountToBurn
                }("");
                require(result, "CALL_FAILED");
            } else {
                _transfer(tokenId, treasuryContract, amountToBurn);
            }
        } else {
            if (tokenId == Structures.Tokens.DAI) {
                _burnDai(amountToBurn);
            } else if (tokenId == Structures.Tokens.MUSE) {
                _burn(amountToBurn);
            } else if (tokenId == Structures.Tokens.NaN) {
                _burnWeth(amountToBurn);
            }
        }

        emit StakeBurned(staker, amountToBurn);

        return newDeposit;
    }

    function _burnFullStake(
        Structures.Tokens tokenId,
        address staker
    ) internal returns (uint256 amountBurned) {
        uint256 currentDeposit = Deposit._getDeposit(tokenId, staker);

        _burnStake(tokenId, staker, currentDeposit);

        return currentDeposit;
    }

    function getStake(
        Structures.Tokens tokenId,
        address staker
    ) external view returns (uint256 amount) {
        // get deposit
        amount = Deposit._getDeposit(tokenId, staker);
        // explicit return
        return amount;
    }

    function getTotalStaked() external view returns (uint256) {
        uint256 amountSeller = Deposit._getDeposit(
            post.postdata.settings.tokenId,
            post.postdata.escrow.seller
        );

        uint256 amountBuyer = Deposit._getDeposit(
            post.postdata.settings.tokenId,
            post.postdata.escrow.buyer
        );

        return (amountSeller + amountBuyer);
    }

    function addStake(
        Structures.Tokens tokenId,
        address _funder,
        uint256 amountToAdd
    ) external payable returns (uint256) {
        require(tokenId == post.postdata.settings.tokenId, "WRONG_TOKEN");

        // Check if the encryptedVaultId matches with either the buyer or the seller
        require(
            msg.sender == post.postdata.escrow.buyer ||
                msg.sender == post.postdata.escrow.seller,
            "WRONG_MSGSENDER"
        );

        // Determine the amount to add based on the role (buyer or seller)
        uint256 actualAmountToAdd = (msg.sender == post.postdata.escrow.seller)
            ? msg.value
            : amountToAdd;

        // Update the stake
        uint256 newStake = _addStake(
            tokenId,
            msg.sender,
            _funder,
            actualAmountToAdd
        );

        // Update the corresponding escrow value based on the role
        if (msg.sender == post.postdata.escrow.buyer) {
            post.postdata.escrow.payment = newStake;
        } else {
            post.postdata.escrow.stake = newStake;
        }

        return newStake;
    }

    function takeStake(
        Structures.Tokens tokenId,
        address receiver,
        uint256 amountToTake
    ) external returns (uint256) {
        require(tokenId == post.postdata.settings.tokenId, "WRONG_TOKEN");

        uint256 currentDeposit = Deposit._getDeposit(tokenId, msg.sender);

        require(currentDeposit >= amountToTake, "NOT_ENOUGH_STAKE");

        uint256 newBalance = _takeStake(
            tokenId,
            msg.sender,
            receiver,
            amountToTake
        );

        if (msg.sender == post.postdata.escrow.buyer) {
            post.postdata.escrow.payment = newBalance;
        } else {
            post.postdata.escrow.stake = newBalance;
        }

        return newBalance;
    }

    function takeFullStake(
        Structures.Tokens tokenId,
        address receiver
    ) external returns (uint256) {
        require(tokenId == post.postdata.settings.tokenId, "WRONG_TOKEN");

        uint256 newBalance = _takeFullStake(tokenId, msg.sender, receiver);

        if (msg.sender == post.postdata.escrow.buyer) {
            post.postdata.escrow.payment = newBalance;
        } else if (msg.sender == post.postdata.escrow.seller) {
            post.postdata.escrow.stake = newBalance;
        }

        return newBalance;
    }

    function getSellerStake() external view returns (uint256 amount) {
        // get deposit
        amount = Deposit._getDeposit(
            post.postdata.settings.tokenId,
            post.postdata.escrow.seller
        );
        // explicit return
        return amount;
    }

    function getBuyerStake() external view returns (uint256 amount) {
        // get deposit
        amount = Deposit._getDeposit(
            post.postdata.settings.tokenId,
            post.postdata.escrow.buyer
        );
        // explicit return
        return amount;
    }
}

contracts/modules/Submission.sol.content:
/**
 * @title Submission
 * @dev This contract is an abstract contract that defines the functions for submitting and revealing data for a post. It inherits from the Events contract.
 */
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./Events.sol";

abstract contract Submission is Events {
    function submitHash(bytes memory encryptedKey) external virtual {
        require(msg.sender == post.postdata.escrow.seller, "NOT_SELLER");

        Structures.PostStatus currentStatus = post.postdata.settings.status;
        require(
            validStatuses[uint8(currentStatus)] &&
                (currentStatus == Structures.PostStatus.Accepted ||
                    currentStatus == Structures.PostStatus.Submitted),
            "WRONG_STATUS"
        );

        require(
            IMecenateUsers(settings.usersModuleContract).checkifUserExist(
                msg.sender
            ),
            "User does not exist"
        );

        require(post.creator.evmAddress == msg.sender, "NOT_SELLER");

        _changeStatus(Structures.PostStatus.Submitted);

        post.postdata.data.encryptedKey = encryptedKey;
        post.postdata.settings.status = Structures.PostStatus.Submitted;
        post.postdata.settings.endTimeStamp =
            block.timestamp +
            post.postdata.settings.duration;

        emit Valid(post);
    }

    function revealData(
        bytes memory decryptedData
    ) external virtual returns (bytes memory) {
        Structures.PostStatus currentStatus = post.postdata.settings.status;
        require(
            validStatuses[uint8(currentStatus)] &&
                (currentStatus == Structures.PostStatus.Submitted ||
                    currentStatus == Structures.PostStatus.Revealed ||
                    currentStatus == Structures.PostStatus.Finalized),
            "INVALID_STATUS"
        );

        require(
            msg.sender != post.postdata.escrow.seller,
            "YOU_ARE_THE_SELLER"
        );

        require(
            currentStatus == Structures.PostStatus.Finalized,
            "NOT_FINALIZED"
        );

        post.postdata.data.decryptedData = decryptedData;
        post.postdata.settings.status = Structures.PostStatus.Revealed;

        emit MadePublic(post);

        return decryptedData;
    }
}

contracts/modules/TokenManager.sol.content:
pragma solidity 0.8.19;

import "./BurnDAI.sol";

abstract contract TokenManager is BurnDAI {
    function _getTokenAddress(
        Structures.Tokens tokenID
    ) internal view returns (address) {
        if (tokenID == Structures.Tokens.DAI)
            return IMecenateFeedFactory(settings.factoryContract).daiToken();
        if (tokenID == Structures.Tokens.MUSE)
            return IMecenateFeedFactory(settings.factoryContract).museToken();
        return address(0);
    }

    function _checkAllowance(
        Structures.Tokens tokenID,
        address from,
        address to,
        uint256 value
    ) internal view {
        address tokenAddress = _getTokenAddress(tokenID);
        require(
            IERC20(tokenAddress).allowance(from, to) >= value,
            "INSUFFICIENT_ALLOWANCE"
        );
    }

    modifier onlyValidTokenID(Structures.Tokens tokenID) {
        require(
            tokenID == Structures.Tokens.MUSE ||
                tokenID == Structures.Tokens.DAI ||
                tokenID == Structures.Tokens.NaN,
            "WRONG_TOKEN"
        );
        _;
    }

    function isValidTokenID(
        Structures.Tokens tokenID
    ) internal pure returns (bool validity) {
        return
            tokenID == Structures.Tokens.MUSE ||
            tokenID == Structures.Tokens.DAI;
    }

    function _transfer(
        Structures.Tokens tokenID,
        address to,
        uint256 value
    ) internal onlyValidTokenID(tokenID) {
        require(
            IERC20(_getTokenAddress(tokenID)).transfer(to, value),
            "TRANSFER_FAILED"
        );
    }

    function _transferFrom(
        Structures.Tokens tokenID,
        address from,
        address to,
        uint256 value
    ) internal onlyValidTokenID(tokenID) {
        address tokenAddress = _getTokenAddress(tokenID);
        _checkAllowance(tokenID, from, to, value);
        require(
            IERC20(tokenAddress).transferFrom(from, to, value),
            "TRANSFER_FAILED"
        );
    }

    function _burn(
        Structures.Tokens tokenID,
        address _receiver,
        uint256 value
    ) internal onlyValidTokenID(tokenID) returns (bool success) {
        bool burnEnabled = IMecenateFeedFactory(settings.factoryContract)
            .burnEnabled();

        if (burnEnabled == false) {
            if (tokenID == Structures.Tokens.NaN) {
                (bool result, ) = payable(_receiver).call{value: value}("");
                require(result, "CALL_FAILED");
            } else {
                TokenManager._transfer(tokenID, _receiver, value);
            }
        } else {
            if (tokenID == Structures.Tokens.DAI) {
                _burnDai(value);
            } else if (tokenID == Structures.Tokens.MUSE) {
                _burn(value);
            } else if (tokenID == Structures.Tokens.NaN) {
                _burnWeth(value);
            }
        }

        return true;
    }

    function _burnFrom(
        Structures.Tokens tokenID,
        address from,
        uint256 value
    ) internal onlyValidTokenID(tokenID) {
        address treasury = IMecenateFeedFactory(settings.factoryContract)
            .treasuryContract();

        if (tokenID == Structures.Tokens.DAI) {
            IERC20(IMecenateFeedFactory(settings.factoryContract).daiToken())
                .transferFrom(from, treasury, value);
        } else if (tokenID == Structures.Tokens.MUSE) {
            BurnMUSE._burnFrom(from, value);
        }
    }

    function _approve(
        Structures.Tokens tokenID,
        address spender,
        uint256 value
    ) internal onlyValidTokenID(tokenID) {
        if (tokenID == Structures.Tokens.DAI) {
            require(
                IERC20(
                    IMecenateFeedFactory(settings.factoryContract).daiToken()
                ).approve(spender, value),
                "APPROVE_FAILED"
            );
        } else if (tokenID == Structures.Tokens.MUSE) {
            uint256 currentAllowance = IMUSE(
                IMecenateFeedFactory(settings.factoryContract).museToken()
            ).allowance(msg.sender, spender);

            uint256 newAllowance = currentAllowance + value;
            require(
                IMUSE(IMecenateFeedFactory(settings.factoryContract).daiToken())
                    .increaseAllowance(spender, newAllowance),
                "APPROVE_FAILED"
            );
        }
    }

    function totalSupply(
        Structures.Tokens tokenID
    ) internal view onlyValidTokenID(tokenID) returns (uint256 value) {
        return IERC20(_getTokenAddress(tokenID)).totalSupply();
    }

    function balanceOf(
        Structures.Tokens tokenID,
        address who
    ) internal view onlyValidTokenID(tokenID) returns (uint256 value) {
        return IERC20(_getTokenAddress(tokenID)).balanceOf(who);
    }

    function allowance(
        Structures.Tokens tokenID,
        address owner,
        address spender
    ) internal view onlyValidTokenID(tokenID) returns (uint256 value) {
        return IERC20(_getTokenAddress(tokenID)).allowance(owner, spender);
    }
}

contracts/modules/Version.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.4;

import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";

/// @title Semver
/// @notice A simple contract for managing contract versions.
abstract contract Version {
    // Contract's major version number.
    uint256 private immutable _major;

    // Contract's minor version number.
    uint256 private immutable _minor;

    // Contract's patch version number.
    uint256 private immutable _path;

    /// @dev Create a new Semver instance.
    /// @param major Major version number.
    /// @param minor Minor version number.
    /// @param patch Patch version number.
    constructor(uint256 major, uint256 minor, uint256 patch) {
        _major = major;
        _minor = minor;
        _path = patch;
    }

    /// @notice Returns the full semver contract version.
    /// @return Semver contract version as a string.
    function version() external view returns (string memory) {
        return
            string(
                abi.encodePacked(
                    Strings.toString(_major),
                    ".",
                    Strings.toString(_minor),
                    ".",
                    Strings.toString(_path)
                )
            );
    }

    function _version() internal view returns (string memory) {
        return
            string(
                abi.encodePacked(
                    Strings.toString(_major),
                    ".",
                    Strings.toString(_minor),
                    ".",
                    Strings.toString(_path)
                )
            );
    }
}

contracts/stats/MecenateStats.sol.content:
/**
 * @title MecenateStats
 * @dev This contract provides statistics about the Mecenate platform, including the total number of users, bay requests, feeds, global fee, fixed fee, and treasury balance.
 */
pragma solidity 0.8.19;

import "../interfaces/IMecenateUsers.sol";
import "../interfaces/IMecenateFeedFactory.sol";
import "../interfaces/IMecenateBay.sol";
import "../interfaces/IMecenateTreasury.sol";

contract MecenateStats {
    struct Stats {
        uint256 totalUsers;
        uint256 totalBayRequests;
        uint256 totalFeeds;
        uint256 globalFee;
        uint256 fixedFee;
        uint256 treasuryBalance;
    }

    IMecenateUsers public mecenateUsers;
    IMecenateFeedFactory public mecenateFeedFactory;
    IMecenateBay public mecenateBay;
    IMecenateTreasury public mecenateTreasury;

    constructor(
        address mecenateUsersAddress,
        address mecenateFeedFactoryAddress,
        address mecenateBayAddress,
        address mecenateTreasuryAddress
    ) {
        mecenateUsers = IMecenateUsers(mecenateUsersAddress);
        mecenateFeedFactory = IMecenateFeedFactory(mecenateFeedFactoryAddress);
        mecenateBay = IMecenateBay(mecenateBayAddress);
        mecenateTreasury = IMecenateTreasury(mecenateTreasuryAddress);
    }

    function getStats() public view returns (Stats memory) {
        uint256 totalBayRequests = mecenateBay.contractCounter();
        uint256 totalFeeds = mecenateFeedFactory.contractCounter();

        return
            Stats(
                mecenateUsers.getUserCount(),
                totalBayRequests,
                totalFeeds,
                mecenateTreasury.globalFee(),
                mecenateTreasury.fixedFee(),
                address(mecenateTreasury).balance
            );
    }
}
