/**
 * @title Structures
 * @dev This library defines various data structures used in the Mecenate platform.
 */
pragma solidity 0.8.19;

library Structures {
    enum PostStatus {
        Waiting,
        Proposed,
        Accepted,
        Submitted,
        Finalized,
        Punished,
        Revealed,
        Renounced
    }
    enum Tokens {
        NaN,
        MUSE,
        DAI
    }
    enum PostType {
        Text,
        Image,
        Video,
        Audio,
        File
    }
    enum PostDuration {
        OneDay,
        ThreeDays,
        OneWeek,
        TwoWeeks,
        OneMonth
    }

    struct FeedSettings {
        uint256 punishmentRatio;
        uint256 postCount;
        address usersModuleContract;
        address factoryContract;
        address verifierContract;
        address vaultContract;
        address router;
        bytes encodedSymKey;
        bytes lastMessageForBuyer;
        bytes lastMessageForSeller;
        string version;
    }

    struct FactorySettings {
        address treasuryContract;
        address usersModuleContract;
        address verifierContract;
        address vaultContract;
        address museToken;
        address daiToken;
        address wethToken;
        address router;
    }

    /**
     * @dev Struct representing a post on the Mecenate platform.
     */
    struct Post {
        User creator;
        PostData postdata;
    }

    /**
     * @dev Struct representing the data of a post on the Mecenate platform.
     */
    struct PostData {
        PostSettings settings;
        PostEscrow escrow;
        PostEncryptedData data;
    }

    /**
     * @dev Struct representing the encrypted data of a post on the Mecenate platform.
     */
    struct PostEncryptedData {
        bytes encryptedData;
        bytes encryptedKey;
        bytes decryptedData;
    }

    /**
     * @dev Struct representing the settings of a post on the Mecenate platform.
     */
    struct PostSettings {
        PostStatus status;
        PostType postType;
        uint256 creationTimeStamp;
        uint256 endTimeStamp;
        uint256 duration;
        Tokens tokenId;
    }

    /**
     * @dev Struct representing the private settings of a post on the Mecenate platform.
     */
    struct PostSettingPrivate {
        bytes vaultIdSeller;
        uint256 sellerTwitterId;
        uint256 sellerTelegramId;
        bytes vaultIdBuyer;
        uint256 buyerTwitterId;
        uint256 buyerTelegramId;
    }

    /**
     * @dev Struct representing the escrow of a post on the Mecenate platform.
     */
    struct PostEscrow {
        uint256 stake;
        uint256 payment;
        uint256 punishment;
        uint256 penalty;
    }

    /**
     * @dev Struct representing a user on the Mecenate platform.
     */
    struct User {
        bytes32 vaultId;
    }

    /**
     * @dev Struct representing a feed on the Mecenate platform.
     */
    struct Feed {
        address contractAddress;
        bytes32 operator;
        uint256 sellerStake;
        uint256 buyerStake;
        uint256 totalStake;
        uint256 postCount;
        uint256 paymentRequested;
        uint256 stakeRequested;
        PostStatus status;
        Tokens tokenId;
        string version;
    }

    /**
     * @dev Struct representing a bay request on the Mecenate platform.
     */
    struct BayRequest {
        bytes32 request;
        uint256 payment;
        uint256 stake;
        address postAddress;
        bool accepted;
        uint256 postCount;
        Tokens tokenId;
    }

    /**
     * @dev Struct representing the private settings of a bay request on the Mecenate platform.
     */
    struct BayRequestPrivate {
        bytes vaultIdSeller;
        bytes sellerResponse;
        bytes vaultIdBuyer;
        bytes buyerResponse;
        address buyerTo;
        bytes32 buyerNonce;
    }
}

contracts/MecenateForwarder.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IMecenateVault {
    function depositToken(
        address _token,
        uint256 _amount,
        bytes32 encryptedVaultId
    ) external;

    function depositETH(bytes32 encryptedVaultId) external payable;
}

interface IMecenateForwarderFactory {
    function getVault() external view returns (address);
}

contract MecenateForwarder {
    bytes32 public encryptedVaultId;

    address public factory;

    constructor(bytes32 _encryptedVaultId, address _factory) {
        encryptedVaultId = _encryptedVaultId;
        factory = _factory;
    }

    function depositToken(address token, uint256 amount) external {
        address vault = IMecenateForwarderFactory(factory).getVault();

        // Approva il token
        require(IERC20(token).approve(vault, amount), "Approve failed");

        IMecenateVault(vault).depositToken(token, amount, encryptedVaultId);
    }

    receive() external payable {
        address vault = IMecenateForwarderFactory(factory).getVault();

        // Esegui il deposito in ETH
        (bool success, ) = payable(vault).call{value: msg.value}(
            abi.encodeWithSignature("depositETH(bytes32)", encryptedVaultId)
        );

        require(success, "Deposit failed");
    }
}

contracts/MecenateStats.sol.content:
/**
 * @title MecenateStats
 * @dev This contract provides statistics about the Mecenate platform, including the total number of users, bay requests, feeds, global fee, fixed fee, and treasury balance.
 */
pragma solidity 0.8.19;

import "./interfaces/IMecenateUsers.sol";
import "./interfaces/IMecenateFeedFactory.sol";
import "./interfaces/IMecenateBay.sol";
import "./interfaces/IMecenateTreasury.sol";

contract MecenateStats {
    struct Stats {
        uint256 totalUsers;
        uint256 totalBayRequests;
        uint256 totalFeeds;
        uint256 globalFee;
        uint256 fixedFee;
        uint256 treasuryBalance;
    }

    IMecenateUsers public mecenateUsers;
    IMecenateFeedFactory public mecenateFeedFactory;
    IMecenateBay public mecenateBay;
    IMecenateTreasury public mecenateTreasury;

    constructor(
        address _mecenateUsers,
        address _mecenateFeedFactory,
        address _mecenateBay,
        address _mecenateTreasury
    ) {
        mecenateUsers = IMecenateUsers(_mecenateUsers);
        mecenateFeedFactory = IMecenateFeedFactory(_mecenateFeedFactory);
        mecenateBay = IMecenateBay(_mecenateBay);
        mecenateTreasury = IMecenateTreasury(_mecenateTreasury);
    }

    function getStats() public view returns (Stats memory) {
        uint256 totalBayRequests = mecenateBay.contractCounter();
        uint256 totalFeeds = mecenateFeedFactory.contractCounter();

        return
            Stats(
                mecenateUsers.getUserCount(),
                totalBayRequests,
                totalFeeds,
                mecenateTreasury.globalFee(),
                mecenateTreasury.fixedFee(),
                address(mecenateTreasury).balance
            );
    }
}

contracts/MecenateTreasury.sol.content:
/**
 * @title MecenateTreasury
 * @dev This contract handles the treasury of the Mecenate platform, which is responsible for distributing funds to users and collecting fees.
 */
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "./modules/Swapper.sol";
import "./interfaces/IMecenateUsers.sol";

import "./interfaces/IMecenateVerifier.sol";
import "./interfaces/IMUSE.sol";

contract MecenateTreasury is Ownable, Swapper {
    using SafeERC20 for IERC20;

    uint256 public globalFee = 100;

    uint256 public fixedFee = 0.01 ether;

    uint256 public lastDistributed;

    uint256 public distributableBalance;

    uint256 public ownerFee;

    mapping(bytes32 => uint256) public userReward;
    mapping(bytes32 => mapping(address => uint256)) public userRewardERC20;

    mapping(address => uint256) public distributableERC20Balance;

    function addFunds() external payable {
        // Aggiungi fondi al contratto e aggiorna il saldo distribuibile
        distributableBalance += msg.value;
    }

    function setGlobalFee(uint256 _globalFee) external onlyOwner {
        globalFee = _globalFee;
    }

    function setFixedFee(uint256 _fixedFee) external onlyOwner {
        fixedFee = _fixedFee;
    }

    /* function withdrawTokens(
        address _token,
        address _to,
        uint256 _amount
    ) external onlyOwner {
        IERC20(_token).safeTransfer(_to, _amount);
    }

    function withdrawNative(
        address payable _to,
        uint256 _amount
    ) external onlyOwner {
        _to.transfer(_amount);
    } */

    function addERC20Funds(address token, uint256 amount) external onlyOwner {
        distributableERC20Balance[token] += amount;
    }

    function distribute(address _usersContract) external {
        require(
            block.timestamp - lastDistributed >= 1 days,
            "Can only distribute once a day"
        );
        require(distributableBalance > 0, "No distributable balance");

        uint256 fee = (distributableBalance * globalFee) / 10000;

        ownerFee += fee;

        uint256 total = distributableBalance - fee;
        uint256 userCount = IMecenateUsers(_usersContract).getUserCount();

        require(userCount > 0, "No users to distribute to");

        uint256 perIdentity = total / userCount;

        for (uint256 i = 0; i < userCount; i++) {
            bytes32 user = IMecenateUsers(_usersContract).getUserVaultIdAt(i);
            userReward[user] += perIdentity;
        }

        // Aggiorna le variabili di stato
        lastDistributed = block.timestamp;
        distributableBalance = 0; // Azzera il saldo distribuibile
    }

    function distributeERC20(address token, address _usersContract) external {
        require(
            distributableERC20Balance[token] > 0,
            "No distributable balance"
        );

        uint256 fee = (distributableERC20Balance[token] * globalFee) / 10000;
        ownerFee += fee; // Assuming ownerFee can hold ERC20 as well

        uint256 total = distributableERC20Balance[token] - fee;
        uint256 userCount = IMecenateUsers(_usersContract).getUserCount();

        require(userCount > 0, "No users to distribute to");

        uint256 perIdentity = total / userCount;

        for (uint256 i = 0; i < userCount; i++) {
            bytes32 user = IMecenateUsers(_usersContract).getUserVaultIdAt(i);
            // Implement logic to distribute perIdentity ERC20 tokens to each user
            // For example: IERC20(token).safeTransfer(userAddress, perIdentity);
            userRewardERC20[user][token] += perIdentity;
        }

        // Reset the distributable balance for the token
        distributableERC20Balance[token] = 0;
    }

    function setSwapRouter(ISwapRouter _swapRouter) external onlyOwner {
        swapRouter = _swapRouter;
    }

    function takeReward(
        address _receiver,
        address _verifierContract,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) external returns (uint256) {
        (
            bytes memory vaultId,
            ,
            ,
            bytes memory signedMessage
        ) = IMecenateVerifier(_verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        (address to, bytes32 nonce) = abi.decode(
            signedMessage,
            (address, bytes32)
        );

        require(_nonce == nonce, "Not Same Nonce");

        bytes32 _user = keccak256(vaultId);

        uint256 amountToSend = userReward[_user];

        userReward[_user] = 0;

        // send eth weith data
        (bool success, ) = payable(to).call{value: amountToSend}(
            sismoConnectResponse
        );

        return amountToSend;
    }

    function takeRewardERC20(
        address _token,
        address _receiver,
        address _verifierContract,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) external returns (uint256) {
        // Similar verification logic as your current takeReward for ETH
        (
            bytes memory vaultId,
            ,
            ,
            bytes memory signedMessage
        ) = IMecenateVerifier(_verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        (address to, bytes32 nonce) = abi.decode(
            signedMessage,
            (address, bytes32)
        );

        require(_nonce == nonce, "Not Same Nonce");

        bytes32 _user = keccak256(vaultId);

        uint256 amountToSend = userRewardERC20[_user][_token]; // assuming userReward is now a double mapping
        require(amountToSend > 0, "No reward available");

        userRewardERC20[_user][_token] = 0; // reset the user's reward to 0

        IERC20(_token).safeTransfer(to, amountToSend);

        return amountToSend;
    }

    function getReward(
        address _verifierContract,
        bytes32 encryptedVaultId
    ) external view returns (uint256) {
        return userReward[encryptedVaultId];
    }

    function getRewardERC20(
        address _token,
        address _verifierContract,
        bytes32 encryptedVaultId
    ) external view returns (uint256) {
        return userRewardERC20[encryptedVaultId][_token]; // assuming userReward is now a double mapping
    }

    // swap token0 to token1 and burn token1
    function swapAndBurn(
        address _token0,
        address _token1,
        address _token2,
        uint24 _fee,
        uint24 _fee2
    ) external onlyOwner {
        uint256 _amount = IERC20(_token0).balanceOf(address(this));
        // Approva il token
        require(
            IERC20(_token0).approve(address(swapRouter), _amount),
            "Approve failed"
        );

        // swap token0 to token1
        uint256 tokens_bought = _swapTokensForTokens(
            _token0,
            _token1,
            _fee,
            _amount
        );

        require(tokens_bought > 0, "No tokens bought");

        // swap token1 to token2

        require(
            IERC20(_token1).approve(address(swapRouter), tokens_bought),
            "Approve failed"
        );

        uint256 tokens_sold_to_muse = _swapTokensForTokens(
            _token1,
            _token2,
            _fee2,
            tokens_bought
        );

        IMUSE(_token2).burn(tokens_sold_to_muse);
    }

    function getDistributionBalance() external view returns (uint256) {
        return distributableBalance;
    }

    function getDistributionERC20Balance(
        address token
    ) external view returns (uint256) {
        return distributableERC20Balance[token];
    }

    receive() external payable {
        // Aggiungi fondi al contratto e aggiorna il saldo distribuibile
        distributableBalance += msg.value;
    }
}

contracts/MecenateVault.sol.content:
/**
 * @title MecenateVault
 * @dev This contract allows users to deposit and withdraw ETH and ERC20 tokens, and pay ETH and ERC20 tokens to other addresses.
 * It also keeps track of the deposited balances for each user using a commitment hash.
 */
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "./interfaces/IMecenateVerifier.sol";
import "./interfaces/IMecenateFeedFactory.sol";

contract MecenateVault is Ownable, ReentrancyGuard {
    using Address for address;
    using Address for address payable;

    mapping(bytes32 => uint256) private ethDeposits;
    mapping(bytes32 => mapping(address => uint256)) private tokenDeposits;

    address public WETH;
    address public DAI;
    address public USDC;
    address public MUSE;
    address public verifierContract;
    address public factoryContract;
    address public mecenateBay;
    address public mecenateUsers;
    address private relayer;

    uint256 public relayerFeePercentage = 500;
    uint256 public constant MAX_RELAYER_FEE_PERCENTAGE = 500;

    event MetaTransactionExecuted(
        address userAddress,
        address relayerAddress,
        bytes functionSignature
    );

    event Withdrawn(
        bytes32 indexed commitment,
        uint256 amount,
        string secret,
        address token
    );

    modifier onlyRelayer() {
        require(msg.sender == relayer, "Not relayer");
        _;
    }

    constructor(
        address _verifierContract,
        address _factoryContract,
        address _bayContract,
        address _usersContract,
        address _relayer
    ) {
        verifierContract = _verifierContract;
        factoryContract = _factoryContract;
        mecenateBay = _bayContract;
        mecenateUsers = _usersContract;
        relayer = _relayer;
    }

    function setTokens(
        address _WETH,
        address _DAI,
        address _USDC,
        address _MUSE
    ) external onlyOwner {
        WETH = _WETH;
        DAI = _DAI;
        USDC = _USDC;
        MUSE = _MUSE;
    }

    function changeRelayerFee(
        uint256 _newRelayerFeePercentage
    ) external onlyOwner {
        require(
            _newRelayerFeePercentage <= MAX_RELAYER_FEE_PERCENTAGE,
            "New relayer fee percentage is too high"
        );
        relayerFeePercentage = _newRelayerFeePercentage;
    }

    function depositETH(bytes32 encryptedVaultId) public payable nonReentrant {
        // 1. Add the deposit to the correct deposit mapping
        ethDeposits[encryptedVaultId] += msg.value;
    }

    function approveTokenToFeed(
        address _token,
        uint256 _amount,
        address _feed,
        bytes32 encryptedVaultId
    ) public {
        require(_token != address(0), "Token address cannot be 0");
        require(_amount > 0, "Amount must be greater than zero");
        require(
            _token == WETH || _token == DAI || _token == USDC || _token == MUSE,
            "Token not supported"
        );
        require(
            tokenDeposits[encryptedVaultId][_token] >= _amount,
            "Not enough balance"
        );

        require(
            IMecenateFeedFactory(factoryContract).isFeed(_feed) ||
                _feed == mecenateBay,
            "Not a feed"
        );

        // The user must first approve the token transfer
        // to this contract
        IERC20 token = IERC20(_token);
        // Approve the token to the feed
        token.approve(_feed, _amount);
    }

    function depositToken(
        address _token,
        uint256 _amount,
        bytes32 encryptedVaultId
    ) external {
        require(_token != address(0), "Token address cannot be 0");

        require(_amount > 0, "Amount must be greater than zero");

        require(
            _token == WETH || _token == DAI || _token == USDC || _token == MUSE,
            "Token not supported"
        );

        // The user must first approve the token transfer
        // to this contract
        IERC20 token = IERC20(_token);

        require(
            token.transferFrom(msg.sender, address(this), _amount),
            "Token transfer failed"
        );

        // Add the deposited amount to the tokenDeposits mapping
        tokenDeposits[encryptedVaultId][_token] += _amount;
    }

    function withdrawETH(
        uint256 _amount,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) public onlyRelayer nonReentrant {
        uint256 initialGas = gasleft();

        (
            bytes memory vaultId,
            ,
            ,
            bytes memory signedMessage
        ) = IMecenateVerifier(verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        (address to, bytes32 nonce) = abi.decode(
            signedMessage,
            (address, bytes32)
        );

        require(to == _to, "Not Same Address");

        require(nonce == _nonce, "Not Same Nonce");

        bytes32 encryptedVaultId = keccak256(vaultId);

        uint256 totalRequired = _amount; // Initialize with _value

        totalRequired -= tx.gasprice * initialGas; // Add maximum possible gas cost

        require(
            ethDeposits[encryptedVaultId] >= totalRequired,
            "Not enough balance"
        );

        // 1. Verify that the commitment exists and the amount is not zero
        require(ethDeposits[encryptedVaultId] > 0, "Commitment does not exist");

        require(_amount > 0, "Amount must be greater than zero");

        // 2. Verify that the commitment has enough balance to withdraw from
        require(ethDeposits[encryptedVaultId] >= _amount, "Not enough balance");

        //decode signedMessage as address
        ethDeposits[encryptedVaultId] -= _amount;

        uint256 gasUsed = initialGas - gasleft();

        uint256 gasCost = gasUsed * tx.gasprice;

        uint256 relayerFee = (gasCost * relayerFeePercentage) / 10000;

        require(_amount >= gasCost + relayerFee, "Not enough balance for gas");

        uint256 newAmount = _amount - gasCost - relayerFee;

        (bool result, ) = payable(to).call{value: newAmount}("");

        require(result, "ETH transfer failed");

        (bool result2, ) = payable(msg.sender).call{
            value: gasCost + relayerFee
        }("");

        require(result2, "ETH transfer failed with gas");
    }

    function withdrawWithSecret(
        string memory _secret,
        address _token,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) public onlyRelayer nonReentrant {
        bytes32 commitment = keccak256(abi.encodePacked(_secret));
        uint256 amount;

        if (_token == address(0)) {
            amount = ethDeposits[commitment];
            require(amount > 0, "No ETH deposit for this secret");
            ethDeposits[commitment] = 0;
            payable(_to).transfer(amount);
        } else {
            amount = tokenDeposits[commitment][_token];
            require(amount > 0, "No Token deposit for this secret");
            tokenDeposits[commitment][_token] = 0;
            IERC20(_token).transfer(_to, amount);
        }

        emit Withdrawn(commitment, amount, _secret, _token);
    }

    function withdrawToken(
        address _token,
        uint256 _amount,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) public onlyRelayer nonReentrant {
        uint256 initialGas = gasleft();

        uint256 totalRequired = tx.gasprice * initialGas;

        (
            bytes memory vaultId,
            ,
            ,
            bytes memory signedMessage
        ) = IMecenateVerifier(verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        (address to, bytes32 nonce) = abi.decode(
            signedMessage,
            (address, bytes32)
        );

        require(
            ethDeposits[keccak256(vaultId)] >= totalRequired,
            "Not enough ETH for gas required"
        );

        require(to == _to, "Not Same Address");

        require(nonce == _nonce, "Not Same Nonce");

        // Check if the commitment exists
        // and the amount is greater than the deposit.
        require(
            tokenDeposits[keccak256(vaultId)][_token] >= _amount,
            "Not enough token balance"
        );

        // Decrease the token balance for the commitment.
        tokenDeposits[keccak256(vaultId)][_token] -= _amount;

        // Transfer the tokens to msg.sender or operator.
        IERC20(_token).transfer(to, _amount);

        uint256 gasUsed = initialGas - gasleft();

        uint256 gasCost = gasUsed * tx.gasprice;

        uint256 relayerFee = (gasCost * relayerFeePercentage) / 10000;

        require(
            ethDeposits[keccak256(vaultId)] >= gasCost + relayerFee,
            "Not enough balance for gas used"
        );

        ethDeposits[keccak256(vaultId)] -= gasCost + relayerFee;

        (bool result, ) = payable(msg.sender).call{value: gasCost + relayerFee}(
            ""
        );

        require(result, "ETH transfer failed with gas");
    }

    function getEthDeposit(
        bytes32 encryptedVaultId
    ) public view returns (uint256) {
        return ethDeposits[encryptedVaultId];
    }

    receive() external payable {
        revert("Use depositETH function");
    }

    fallback() external payable {
        require(msg.data.length > 0, "Data required for Sismo verification.");
        bytes32 encryptedVaultId = abi.decode(msg.data, (bytes32));
        ethDeposits[encryptedVaultId] += msg.value;
    }

    function getTokenDeposit(
        address _token,
        bytes32 encryptedVaultId
    ) public view returns (uint256) {
        return tokenDeposits[encryptedVaultId][_token];
    }

    function execute(
        address _target,
        bytes calldata _data,
        uint256 _value,
        bytes32 _encryptedVaultId
    ) external onlyRelayer nonReentrant returns (bool) {
        uint256 availableBalance = ethDeposits[_encryptedVaultId];

        uint256 ethBalanceB4 = address(this).balance; // Aggiunto per tracciare il saldo ETH
        uint256 daiBalanceB4 = IERC20(DAI).balanceOf(address(this));
        uint256 museBalanceB4 = IERC20(MUSE).balanceOf(address(this));

        uint256 totalRequired = _value + (tx.gasprice * gasleft());

        require(availableBalance >= totalRequired, "Insufficient ETH balance");

        bool success; // variabile per verificare il successo delle transazioni
        bytes memory retData; // dati di ritorno dalle transazioni

        if (_data.length == 0) {
            (success, ) = payable(_target).call{value: _value}("");
        } else {
            if (_value == 0) {
                (success, retData) = _target.call(_data);
            } else {
                (success, retData) = _target.call{value: _value}(_data);
            }
        }

        require(success, "Transaction failed");

        uint256 gasUsed = totalRequired - _value - (tx.gasprice * gasleft());
        uint256 relayerFee = (gasUsed * relayerFeePercentage) / 10000;

        require(
            availableBalance >= gasUsed + relayerFee,
            "Insufficient balance for gas and fee"
        );

        // Aggiornamento dei saldi ETH, DAI e MUSE
        uint256 ethBalance = address(this).balance;

        uint256 diffEth = ethBalanceB4 - ethBalance;

        if (diffEth > 0) {
            ethDeposits[_encryptedVaultId] -= diffEth;
        }

        uint256 daiBalance = IERC20(DAI).balanceOf(address(this));
        uint256 museBalance = IERC20(MUSE).balanceOf(address(this));

        uint256 diffDai;
        uint256 diffMuse;

        ethDeposits[_encryptedVaultId] = gasUsed - relayerFee;

        (success, ) = payable(msg.sender).call{value: gasUsed + relayerFee}("");

        require(success, "ETH transfer failed");

        if (daiBalanceB4 > daiBalance) {
            diffDai = daiBalanceB4 - daiBalance;

            if (diffDai > 0 && daiBalanceB4 != 0) {
                tokenDeposits[_encryptedVaultId][DAI] -= diffDai;
            }
        }

        if (museBalanceB4 > museBalance) {
            diffMuse = museBalanceB4 - museBalance;

            if (diffMuse > 0 && museBalanceB4 != 0) {
                tokenDeposits[_encryptedVaultId][MUSE] -= diffMuse;
            }
        }

        return true;
    }

    function setRelayer(address _relayer) external onlyOwner {
        relayer = _relayer;
    }

    function setVerifierContract(address _verifierContract) external onlyOwner {
        verifierContract = _verifierContract;
    }

    function setFactoryContract(address _factoryContract) external onlyOwner {
        factoryContract = _factoryContract;
    }

    function setMecenateBay(address _mecenateBay) external onlyOwner {
        mecenateBay = _mecenateBay;
    }

    function setMecenateUsers(address _mecenateUsers) external onlyOwner {
        mecenateUsers = _mecenateUsers;
    }
}

contracts/MecenateVerifier.sol.content:
/**
 * @title MecenateVerifier
 * @dev Contract that verifies user identity using SismoConnect and returns user's vaultId, twitterId and telegramId.
 */
pragma solidity ^0.8.17;
import "./helpers/SismoConnectLib.sol";

contract MecenateVerifier is SismoConnect {
    bytes16 public appId;

    constructor(bytes16 _appId) SismoConnect(buildConfig(_appId)) {
        appId = _appId;
    }

    function sismoVerify(
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) external view returns (bytes memory, uint256, uint256, bytes memory) {
        require(sismoConnectResponse.length > 0, "empty response");

        // Build authorization requests
        AuthRequest[] memory auths = new AuthRequest[](3);

        auths[0] = buildAuth(AuthType.VAULT);

        auths[1] = buildAuth({
            authType: AuthType.TWITTER,
            isOptional: true,
            isSelectableByUser: true
        });

        auths[2] = buildAuth({
            authType: AuthType.TELEGRAM,
            isOptional: true,
            isSelectableByUser: true
        });

        // Verify the response
        SismoConnectVerifiedResult memory result = verify({
            responseBytes: sismoConnectResponse,
            auths: auths,
            signature: buildSignature({message: abi.encode(_to, _nonce)})
        });

        bytes memory signedMessage = SismoConnectHelper.getSignedMessage(
            result
        );

        // Store the verified auths
        VerifiedAuth[] memory _verifiedAuths = new VerifiedAuth[](
            result.auths.length
        );

        for (uint256 i = 0; i < result.auths.length; i++) {
            _verifiedAuths[i] = result.auths[i];
        }

        // Get the vaultId of the user
        // --> vaultId = hash(userVaultSecret, appId)
        uint256 vaultId = SismoConnectHelper.getUserId(result, AuthType.VAULT);

        // Convert the vaultId to bytes
        bytes memory vaultIdBytes = abi.encodePacked(vaultId);

        // Get the userId of the user
        uint256 twitterId = 0;
        uint256 telegramId = 0;

        for (uint256 i = 0; i < _verifiedAuths.length; i++) {
            if (_verifiedAuths[i].authType == AuthType.TWITTER) {
                twitterId = SismoConnectHelper.getUserId(
                    result,
                    AuthType.TWITTER
                );
            } else if (_verifiedAuths[i].authType == AuthType.TELEGRAM) {
                telegramId = SismoConnectHelper.getUserId(
                    result,
                    AuthType.TELEGRAM
                );
            }
        }

        return (vaultIdBytes, twitterId, telegramId, signedMessage);
    }
}

