contracts/modules/Acceptance.sol.content:
/**
 * @title Acceptance
 * @dev This contract is an abstract contract that inherits from Events and Staking contracts. It provides a function to accept a post and add stake to the post. The function accepts a Sismo Connect response as a parameter and verifies the user existence. It also checks if the user is not the seller and if the post is in Proposed status. If the post has an escrow payment, it checks if the buyer has paid enough and adds the payment to the post's escrow. If the post does not have an escrow payment, it requires a payment from the buyer and adds it to the post's escrow. Finally, it changes the post status to Accepted and emits an Accepted event.
 */
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "./Events.sol";
import "./Staking.sol";

abstract contract Acceptance is Events, Staking {
    function acceptPost(
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce,
        Structures.Tokens tokenId,
        uint256 _paymentAmount
    ) external payable virtual {
        require(
            validStatuses[uint8(Structures.PostStatus.Proposed)],
            "INVALID_STATUS"
        );

        _checkToken(tokenId);

        uint256 amountToAdd = tokenId == Structures.Tokens.NaN
            ? msg.value
            : _paymentAmount;
        bytes32 sellerVaultIdHash = keccak256(postSettingPrivate.vaultIdSeller);

        // Get encryptedVaultId only once
        (
            bytes memory vaultId,
            uint256 twitterId,
            uint256 telegramId,

        ) = _verifyNonce(sismoConnectResponse, _to, _nonce);
        bytes32 encryptedVaultId = keccak256(vaultId);

        // Use local variable for repeated calls
        uint256 sellerStake = Deposit._getDeposit(tokenId, sellerVaultIdHash);

        require(sellerStake >= post.postdata.escrow.stake, "STAKE_INCORRECT");
        require(
            IMecenateUsers(settings.usersModuleContract).checkifUserExist(
                encryptedVaultId
            ),
            "USERT_NOT_EXIST"
        );
        require(encryptedVaultId != sellerVaultIdHash, "YOU_ARE_THE_SELLER");

        if (post.postdata.escrow.payment > 0) {
            require(
                _paymentAmount >= post.postdata.escrow.payment,
                "NOT_ENOUGH_PAYMENT"
            );
        } else {
            require(msg.value > 0, "ZERO_MSGVALUE");
            require(_paymentAmount > 0, "ZERO_PAYMENT");
        }

        uint256 payment = _addStake(
            tokenId,
            encryptedVaultId,
            settings.vaultContract,
            amountToAdd
        );

        // Update all at once
        post.postdata.escrow.payment = payment;
        post.postdata.settings.status = Structures.PostStatus.Accepted;
        _changeStatus(Structures.PostStatus.Accepted);

        // Update private settings
        postSettingPrivate.vaultIdBuyer = vaultId;
        postSettingPrivate.buyerTwitterId = twitterId;
        postSettingPrivate.buyerTelegramId = telegramId;

        emit Accepted(post);
    }
}

contracts/modules/BurnDAI.sol.content:
pragma solidity 0.8.19;

import "./BurnMUSE.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

import "../interfaces/IWETH.sol";

abstract contract BurnDAI is BurnMUSE {
    using SafeMath for uint256;

    function _burnFrom(
        address from,
        uint256 value
    ) internal override returns (bool success) {
        IERC20(IMecenateFeedFactory(settings.factoryContract).daiToken())
            .transferFrom(from, address(this), value);

        _burn(value);

        return true;
    }

    function _burnDai(uint256 value) internal returns (bool success) {
        // approve uniswap for token transfer
        IERC20(IMecenateFeedFactory(settings.factoryContract).daiToken())
            .approve(settings.router, value);

        // swap IMecenateFeedFactory(settings.factoryContract).daiToken() for MUSE
        uint256 tokens_sold = value;

        uint256 tokens_bought = _swapTokensForToken(
            IMecenateFeedFactory(settings.factoryContract).daiToken(),
            IMecenateFeedFactory(settings.factoryContract).wethToken(),
            IMecenateFeedFactory(settings.factoryContract).getRouterFee(0),
            tokens_sold
        );

        uint256 tokens_sold_to_muse = _swapTokensForToken(
            IMecenateFeedFactory(settings.factoryContract).wethToken(),
            IMecenateFeedFactory(settings.factoryContract).museToken(),
            IMecenateFeedFactory(settings.factoryContract).getRouterFee(1),
            tokens_bought
        );

        IERC20(IMecenateFeedFactory(settings.factoryContract).museToken())
            .approve(
                IMecenateFeedFactory(settings.factoryContract).museToken(),
                tokens_sold_to_muse
            );

        // burn MUSE
        BurnMUSE._burn(tokens_sold_to_muse);

        return true;
    }

    function _burnWeth(uint256 value) internal returns (bool success) {
        // deposit IMecenateFeedFactory(settings.factoryContract).wethToken()
        IWETH(IMecenateFeedFactory(settings.factoryContract).wethToken())
            .deposit{value: value}();

        // approve uniswap for token transfer
        IERC20(IMecenateFeedFactory(settings.factoryContract).wethToken())
            .approve(settings.router, value);

        // swap IMecenateFeedFactory(settings.factoryContract).wethToken() for MUSE
        uint256 tokens_sold = value;

        uint256 tokens_bought = _swapTokensForToken(
            IMecenateFeedFactory(settings.factoryContract).wethToken(),
            IMecenateFeedFactory(settings.factoryContract).museToken(),
            IMecenateFeedFactory(settings.factoryContract).getRouterFee(1),
            tokens_sold
        );

        IERC20(IMecenateFeedFactory(settings.factoryContract).museToken())
            .approve(
                IMecenateFeedFactory(settings.factoryContract).museToken(),
                tokens_bought
            );

        // burn MUSE
        BurnMUSE._burn(tokens_bought);

        return true;
    }

    function getTokenAddress()
        internal
        view
        virtual
        override
        returns (address token)
    {
        return IMecenateFeedFactory(settings.factoryContract).daiToken();
    }

    function getExchangeAddress()
        internal
        view
        override
        returns (address exchange)
    {
        exchange = settings.router;
    }

    function _swapTokensForToken(
        address tokenIn,
        address tokenOut,
        uint24 fee,
        uint256 amountIn
    ) internal returns (uint256 amountOut) {
        // Check and approve allowance
        uint256 allowance = IERC20(tokenIn).allowance(
            address(this),
            settings.router
        );
        if (allowance < amountIn) {
            IERC20(tokenIn).approve(settings.router, type(uint256).max);
        }

        // Prepare parameters
        address recipient = address(this);
        uint256 deadline = block.timestamp + 60; // 15 seconds from the current block timestamp

        // Perform the swap
        // Refer to the Uniswap V3 documentation for details on the params.
        amountOut = ISwapRouter(settings.router).exactInputSingle(
            ISwapRouter.ExactInputSingleParams({
                tokenIn: tokenIn,
                tokenOut: tokenOut,
                fee: fee,
                recipient: recipient,
                deadline: deadline,
                amountIn: amountIn,
                amountOutMinimum: 0, // you may want to set a minimum here
                sqrtPriceLimitX96: 0
            })
        );

        return amountOut;
    }
}

contracts/modules/BurnMUSE.sol.content:
pragma solidity 0.8.19;
import "../interfaces/IMUSE.sol";
import "../interfaces/IMecenateFeedFactory.sol";
import "./Events.sol";

abstract contract BurnMUSE is Events {
    function _burn(uint256 value) internal virtual returns (bool success) {
        IMUSE(IMecenateFeedFactory(settings.factoryContract).museToken()).burn(
            value
        );
        return true;
    }

    function _burnFrom(
        address from,
        uint256 value
    ) internal virtual returns (bool success) {
        IMUSE(IMecenateFeedFactory(settings.factoryContract).museToken())
            .transferFrom(from, address(this), value);
        _burn(value);
        return true;
    }

    function getTokenAddress() internal view virtual returns (address token) {
        return IMecenateFeedFactory(settings.factoryContract).museToken();
    }

    function getExchangeAddress()
        internal
        view
        virtual
        returns (address exchange)
    {
        exchange = IMecenateFeedFactory(settings.factoryContract).router();
    }
}

contracts/modules/Creation.sol.content:
/**
 * @title Creation
 * @dev This contract is an abstract contract that inherits from Staking contract. It provides a function to create a post with the given parameters. The function requires the user to be the owner of the post and to have a stake. The post can have different durations and types. The function returns the created post.
 */
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./Staking.sol";

abstract contract Creation is Staking {
    function createPost(
        bytes memory encryptedHash,
        Structures.PostType postType,
        Structures.PostDuration postDuration,
        uint256 payment,
        uint256 stakeAmount,
        Structures.Tokens tokenId,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    )
        external
        payable
        onlyValidTokenID(tokenId)
        returns (Structures.Post memory)
    {
        (
            bytes memory vaultId,
            uint256 twitterId,
            uint256 telegramId,

        ) = _verifyNonce(sismoConnectResponse, _to, _nonce);

        bytes32 encryptedVaultId = keccak256(vaultId);

        // Early exit conditions
        require(encryptedVaultId == owner, "NOT_OWNER");

        require(
            IMecenateUsers(settings.usersModuleContract).checkifUserExist(
                encryptedVaultId
            ),
            "USER_NOT_EXIST"
        );

        require(
            validStatuses[uint8(post.postdata.settings.status)],
            "INVALID_STATUS"
        );

        require(stakeAmount > 0, "STAKE_AMOUNT_ZERO");

        uint256 duration = postDurationToDays[uint8(postDuration)];

        uint256 stake = _addStake(
            tokenId,
            encryptedVaultId,
            settings.vaultContract,
            stakeAmount
        );

        // Change status to Proposed
        _changeStatus(Structures.PostStatus.Proposed);

        // Initialize the new Post struct with named arguments for clarity
        Structures.Post memory newPost = Structures.Post({
            creator: Structures.User({vaultId: encryptedVaultId}),
            postdata: Structures.PostData({
                settings: Structures.PostSettings({
                    postType: postType,
                    status: Structures.PostStatus.Proposed,
                    creationTimeStamp: block.timestamp,
                    endTimeStamp: 0,
                    duration: duration,
                    tokenId: tokenId
                }),
                escrow: Structures.PostEscrow({
                    stake: stake,
                    payment: payment,
                    punishment: 0,
                    penalty: 0
                }),
                data: Structures.PostEncryptedData({
                    encryptedData: encryptedHash,
                    encryptedKey: ZEROHASH,
                    decryptedData: ZEROHASH
                })
            })
        });

        // Update storage and emit event
        post = newPost;
        settings.postCount++;

        postSettingPrivate.vaultIdSeller = vaultId;
        postSettingPrivate.sellerTwitterId = twitterId;
        postSettingPrivate.sellerTelegramId = telegramId;

        emit Created(newPost);

        return newPost;
    }
}

contracts/modules/Data.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "../interfaces/IMecenateFeed.sol";
import "../interfaces/IMecenateUsers.sol";
import "../interfaces/IMecenateTreasury.sol";
import "../interfaces/IMecenateFeedFactory.sol";
import "../interfaces/IMecenateVerifier.sol";

// import openzeppelin ECDSA
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

/**
 * @title Data
 * @dev This contract stores data related to Mecenate posts and provides functions to interact with it.
 */
contract Data {
    bytes internal constant ZEROHASH = "0x00";

    bytes32 public owner;

    Structures.Post public post;
    Structures.PostSettingPrivate internal postSettingPrivate;
    Structures.FeedSettings internal settings;

    mapping(uint8 => uint256) internal postDurationToDays;
    mapping(uint8 => bool) internal validStatuses;

    function onlyVault() internal view {
        require(msg.sender == settings.vaultContract, "ONLY_VAULT");
    }

    constructor(
        address _usersModuleContract,
        address _verifierContract,
        address _vaultContract,
        address _factoryContract,
        string memory _version
    ) {
        settings.punishmentRatio = 100000000000000000; // Constant value
        settings.postCount = 0; // Initialize postCount to 0
        settings.usersModuleContract = _usersModuleContract;
        settings.verifierContract = _verifierContract;
        settings.vaultContract = _vaultContract;
        settings.factoryContract = msg.sender;
        settings.router = IMecenateFeedFactory(_factoryContract).router();
        settings.encodedSymKey = ZEROHASH;
        settings.lastMessageForBuyer = ZEROHASH;
        settings.lastMessageForSeller = ZEROHASH;
        post.postdata.settings.status = Structures.PostStatus.Waiting;

        postDurationToDays[uint8(Structures.PostDuration.OneDay)] = 1 days;
        postDurationToDays[uint8(Structures.PostDuration.ThreeDays)] = 3 days;
        postDurationToDays[uint8(Structures.PostDuration.OneWeek)] = 7 days;
        postDurationToDays[uint8(Structures.PostDuration.TwoWeeks)] = 14 days;
        postDurationToDays[uint8(Structures.PostDuration.OneMonth)] = 30 days;
        validStatuses[uint8(Structures.PostStatus.Waiting)] = true;

        settings.version = _version;
    }

    function version() external view returns (string memory) {
        return settings.version;
    }

    function _changeStatus(Structures.PostStatus _newStatus) internal {
        validStatuses[uint8(post.postdata.settings.status)] = false;
        validStatuses[uint8(_newStatus)] = true;
        post.postdata.settings.status = _newStatus;
    }

    function _verifyNonce(
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) internal view returns (bytes memory, uint256, uint256, bytes memory) {
        (
            bytes memory vaultId,
            uint256 twitterId,
            uint256 telegramId,
            bytes memory signedMessage
        ) = IMecenateVerifier(settings.verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        (, bytes32 nonce) = abi.decode(signedMessage, (address, bytes32));

        require(_nonce == nonce, "WRONG_NONCE");

        return (vaultId, twitterId, telegramId, signedMessage);
    }

    function sismoVerify(
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) internal view returns (bytes memory, uint256, uint256, bytes memory) {
        (
            bytes memory vaultId,
            uint256 twitterId,
            uint256 telegramId,
            bytes memory signedMessage
        ) = IMecenateVerifier(settings.verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        return (vaultId, twitterId, telegramId, signedMessage);
    }

    function getStatus() external view returns (Structures.PostStatus) {
        return post.postdata.settings.status;
    }

    function getTokenId() external view returns (Structures.Tokens) {
        return post.postdata.settings.tokenId;
    }

    function getTokenIdAddress() public view returns (address) {
        if (post.postdata.settings.tokenId == Structures.Tokens.MUSE) {
            return IMecenateFeedFactory(settings.factoryContract).museToken();
        } else if (post.postdata.settings.tokenId == Structures.Tokens.DAI) {
            return IMecenateFeedFactory(settings.factoryContract).daiToken();
        } else {
            return address(0);
        }
    }

    function _checkToken(Structures.Tokens _token) internal view {
        require(_token == post.postdata.settings.tokenId, "WRONG_TOKEN");
    }

    function getPaymentRequested() external view returns (uint256) {
        return post.postdata.escrow.payment;
    }

    function getStakeRequested() external view returns (uint256) {
        return post.postdata.escrow.stake;
    }

    function postCount() external view returns (uint256) {
        return settings.postCount;
    }

    receive() external payable {}
}

contracts/modules/Deposit.sol.content:
pragma solidity 0.8.19;
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./TokenManager.sol";

contract Deposit {
    using SafeMath for uint256;

    mapping(uint256 => mapping(bytes32 => uint256)) private _deposit;

    event DepositIncreased(
        Structures.Tokens tokenID,
        bytes32 user,
        uint256 amount,
        uint256 newDeposit
    );
    event DepositDecreased(
        Structures.Tokens tokenID,
        bytes32 user,
        uint256 amount,
        uint256 newDeposit
    );

    function _increaseDeposit(
        Structures.Tokens tokenID,
        bytes32 user,
        uint256 amountToAdd
    ) internal returns (uint256 newDeposit) {
        // calculate new deposit amount
        newDeposit = _deposit[uint256(tokenID)][user].add(amountToAdd);

        // set new stake to storage
        _deposit[uint256(tokenID)][user] = newDeposit;

        // emit event
        emit DepositIncreased(tokenID, user, amountToAdd, newDeposit);

        // return
        return newDeposit;
    }

    function _decreaseDeposit(
        Structures.Tokens tokenID,
        bytes32 user,
        uint256 amountToRemove
    ) internal returns (uint256 newDeposit) {
        // get current deposit
        uint256 currentDeposit = _deposit[uint256(tokenID)][user];

        // check if sufficient deposit
        require(currentDeposit >= amountToRemove, "INSUFFICIENT_DEPOSIT");

        // calculate new deposit amount
        newDeposit = currentDeposit.sub(amountToRemove);

        // set new stake to storage
        _deposit[uint256(tokenID)][user] = newDeposit;

        // emit event
        emit DepositDecreased(tokenID, user, amountToRemove, newDeposit);

        // return
        return newDeposit;
    }

    function _clearDeposit(
        Structures.Tokens tokenID,
        bytes32 user
    ) internal returns (uint256 amountRemoved) {
        // get current deposit
        uint256 currentDeposit = _deposit[uint256(tokenID)][user];

        // remove deposit
        _decreaseDeposit(tokenID, user, currentDeposit);

        // return
        return currentDeposit;
    }

    function _getDeposit(
        Structures.Tokens tokenID,
        bytes32 user
    ) internal view returns (uint256 deposit) {
        return _deposit[uint256(tokenID)][user];
    }
}

