/**
 * @title Structures
 * @dev This library defines various data structures used in the Mecenate platform.
 */
pragma solidity 0.8.19;

library Structures {
    enum PostStatus {
        Waiting,
        Proposed,
        Accepted,
        Submitted,
        Finalized,
        Punished,
        Revealed,
        Renounced
    }
    enum Tokens {
        NaN,
        MUSE,
        DAI
    }
    enum PostType {
        Text,
        Image,
        Video,
        Audio,
        File
    }
    enum PostDuration {
        OneDay,
        ThreeDays,
        OneWeek,
        TwoWeeks,
        OneMonth
    }

    struct FeedSettings {
        uint256 punishmentRatio;
        uint256 postCount;
        address usersModuleContract;
        address factoryContract;
        address verifierContract;
        address vaultContract;
        address router;
        bytes encodedSymKey;
        bytes lastMessageForBuyer;
        bytes lastMessageForSeller;
        string version;
    }

    struct FactorySettings {
        address treasuryContract;
        address usersModuleContract;
        address verifierContract;
        address vaultContract;
        address museToken;
        address daiToken;
        address wethToken;
        address router;
    }

    /**
     * @dev Struct representing a post on the Mecenate platform.
     */
    struct Post {
        User creator;
        PostData postdata;
    }

    /**
     * @dev Struct representing the data of a post on the Mecenate platform.
     */
    struct PostData {
        PostSettings settings;
        PostEscrow escrow;
        PostEncryptedData data;
    }

    /**
     * @dev Struct representing the encrypted data of a post on the Mecenate platform.
     */
    struct PostEncryptedData {
        bytes encryptedData;
        bytes encryptedKey;
        bytes decryptedData;
    }

    /**
     * @dev Struct representing the settings of a post on the Mecenate platform.
     */
    struct PostSettings {
        PostStatus status;
        PostType postType;
        uint256 creationTimeStamp;
        uint256 endTimeStamp;
        uint256 duration;
        Tokens tokenId;
    }

    /**
     * @dev Struct representing the private settings of a post on the Mecenate platform.
     */
    struct PostSettingPrivate {
        bytes vaultIdSeller;
        uint256 sellerTwitterId;
        uint256 sellerTelegramId;
        bytes vaultIdBuyer;
        uint256 buyerTwitterId;
        uint256 buyerTelegramId;
    }

    /**
     * @dev Struct representing the escrow of a post on the Mecenate platform.
     */
    struct PostEscrow {
        uint256 stake;
        uint256 payment;
        uint256 punishment;
        uint256 penalty;
    }

    /**
     * @dev Struct representing a user on the Mecenate platform.
     */
    struct User {
        bytes32 vaultId;
    }

    /**
     * @dev Struct representing a feed on the Mecenate platform.
     */
    struct Feed {
        address contractAddress;
        bytes32 operator;
        uint256 sellerStake;
        uint256 buyerStake;
        uint256 totalStake;
        uint256 postCount;
        uint256 paymentRequested;
        uint256 stakeRequested;
        PostStatus status;
        Tokens tokenId;
    }

    /**
     * @dev Struct representing a bay request on the Mecenate platform.
     */
    struct BayRequest {
        bytes32 request;
        uint256 payment;
        uint256 stake;
        address postAddress;
        bool accepted;
        uint256 postCount;
        Tokens tokenId;
    }

    /**
     * @dev Struct representing the private settings of a bay request on the Mecenate platform.
     */
    struct BayRequestPrivate {
        bytes vaultIdSeller;
        bytes sellerResponse;
        bytes vaultIdBuyer;
        bytes buyerResponse;
        address buyerTo;
        bytes32 buyerNonce;
    }
}

contracts/MecenateStats.sol.content:
/**
 * @title MecenateStats
 * @dev This contract provides statistics about the Mecenate platform, including the total number of users, bay requests, feeds, global fee, fixed fee, and treasury balance.
 */
pragma solidity 0.8.19;

import "./interfaces/IMecenateUsers.sol";
import "./interfaces/IMecenateFeedFactory.sol";
import "./interfaces/IMecenateBay.sol";
import "./interfaces/IMecenateTreasury.sol";

contract MecenateStats {
    struct Stats {
        uint256 totalUsers;
        uint256 totalBayRequests;
        uint256 totalFeeds;
        uint256 globalFee;
        uint256 fixedFee;
        uint256 treasuryBalance;
    }

    IMecenateUsers public mecenateUsers;
    IMecenateFeedFactory public mecenateFeedFactory;
    IMecenateBay public mecenateBay;
    IMecenateTreasury public mecenateTreasury;

    constructor(
        address _mecenateUsers,
        address _mecenateFeedFactory,
        address _mecenateBay,
        address _mecenateTreasury
    ) {
        mecenateUsers = IMecenateUsers(_mecenateUsers);
        mecenateFeedFactory = IMecenateFeedFactory(_mecenateFeedFactory);
        mecenateBay = IMecenateBay(_mecenateBay);
        mecenateTreasury = IMecenateTreasury(_mecenateTreasury);
    }

    function getStats() public view returns (Stats memory) {
        uint256 totalBayRequests = mecenateBay.contractCounter();
        uint256 totalFeeds = mecenateFeedFactory.contractCounter();

        return
            Stats(
                mecenateUsers.getUserCount(),
                totalBayRequests,
                totalFeeds,
                mecenateTreasury.globalFee(),
                mecenateTreasury.fixedFee(),
                address(mecenateTreasury).balance
            );
    }
}

contracts/MecenateVault.sol.content:
/**
 * @title MecenateVault
 * @dev This contract allows users to deposit and withdraw ETH and ERC20 tokens, and pay ETH and ERC20 tokens to other addresses.
 * It also keeps track of the deposited balances for each user using a commitment hash.
 */
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "./interfaces/IMecenateVerifier.sol";
import "./interfaces/IMecenateFeedFactory.sol";

contract MecenateVault is Ownable, ReentrancyGuard {
    using Address for address;
    using Address for address payable;

    mapping(bytes32 => uint256) private ethDeposits;
    mapping(bytes32 => mapping(address => uint256)) private tokenDeposits;

    address public WETH;
    address public DAI;
    address public USDC;
    address public MUSE;
    address public verifierContract;
    address public factoryContract;
    address public mecenateBay;
    address public mecenateUsers;
    address private relayer;

    uint256 public relayerFeePercentage = 200;
    uint256 public constant MAX_RELAYER_FEE_PERCENTAGE = 500;

    event MetaTransactionExecuted(
        address userAddress,
        address relayerAddress,
        bytes functionSignature
    );

    event Withdrawn(
        bytes32 indexed commitment,
        uint256 amount,
        string secret,
        address token
    );

    modifier onlyRelayer() {
        require(msg.sender == relayer, "Not relayer");
        _;
    }

    constructor(
        address _verifierContract,
        address _factoryContract,
        address _bayContract,
        address _usersContract,
        address _relayer
    ) {
        verifierContract = _verifierContract;
        factoryContract = _factoryContract;
        mecenateBay = _bayContract;
        mecenateUsers = _usersContract;
        relayer = _relayer;
    }

    function setTokens(
        address _WETH,
        address _DAI,
        address _USDC,
        address _MUSE
    ) external onlyOwner {
        WETH = _WETH;
        DAI = _DAI;
        USDC = _USDC;
        MUSE = _MUSE;
    }

    function changeRelayerFee(
        uint256 _newRelayerFeePercentage
    ) external onlyOwner {
        require(
            _newRelayerFeePercentage <= MAX_RELAYER_FEE_PERCENTAGE,
            "New relayer fee percentage is too high"
        );
        relayerFeePercentage = _newRelayerFeePercentage;
    }

    function depositETH(bytes32 encryptedVaultId) public payable nonReentrant {
        // 1. Add the deposit to the correct deposit mapping
        ethDeposits[encryptedVaultId] += msg.value;
    }

    function approveTokenToFeed(
        address _token,
        uint256 _amount,
        address _feed,
        bytes32 encryptedVaultId
    ) public {
        require(_token != address(0), "Token address cannot be 0");
        require(_amount > 0, "Amount must be greater than zero");
        require(
            _token == WETH || _token == DAI || _token == USDC || _token == MUSE,
            "Token not supported"
        );
        require(
            tokenDeposits[encryptedVaultId][_token] >= _amount,
            "Not enough balance"
        );
        require(
            IMecenateFeedFactory(factoryContract).isFeed(_feed),
            "Not a feed"
        );

        // The user must first approve the token transfer
        // to this contract
        IERC20 token = IERC20(_token);
        // Approve the token to the feed
        token.approve(_feed, _amount);
    }

    function depositToken(
        address _token,
        uint256 _amount,
        bytes32 encryptedVaultId
    ) external {
        require(_token != address(0), "Token address cannot be 0");

        require(_amount > 0, "Amount must be greater than zero");

        require(
            _token == WETH || _token == DAI || _token == USDC || _token == MUSE,
            "Token not supported"
        );

        // The user must first approve the token transfer
        // to this contract
        IERC20 token = IERC20(_token);

        require(
            token.transferFrom(msg.sender, address(this), _amount),
            "Token transfer failed"
        );

        // Add the deposited amount to the tokenDeposits mapping
        tokenDeposits[encryptedVaultId][_token] += _amount;
    }

    function withdrawETH(
        uint256 _amount,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) public onlyRelayer nonReentrant {
        uint256 initialGas = gasleft();

        (
            bytes memory vaultId,
            ,
            ,
            bytes memory signedMessage
        ) = IMecenateVerifier(verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        (address to, bytes32 nonce) = abi.decode(
            signedMessage,
            (address, bytes32)
        );

        require(to == _to, "Not Same Address");

        require(nonce == _nonce, "Not Same Nonce");

        bytes32 encryptedVaultId = keccak256(vaultId);

        uint256 totalRequired = _amount; // Initialize with _value

        totalRequired -= tx.gasprice * initialGas; // Add maximum possible gas cost

        require(
            ethDeposits[encryptedVaultId] >= totalRequired,
            "Not enough balance"
        );

        // 1. Verify that the commitment exists and the amount is not zero
        require(ethDeposits[encryptedVaultId] > 0, "Commitment does not exist");

        require(_amount > 0, "Amount must be greater than zero");

        // 2. Verify that the commitment has enough balance to withdraw from
        require(ethDeposits[encryptedVaultId] >= _amount, "Not enough balance");

        //decode signedMessage as address
        ethDeposits[encryptedVaultId] -= _amount;

        uint256 gasUsed = initialGas - gasleft();

        uint256 gasCost = gasUsed * tx.gasprice;

        uint256 relayerFee = (gasCost * relayerFeePercentage) / 10000;

        require(_amount >= gasCost + relayerFee, "Not enough balance for gas");

        uint256 newAmount = _amount - gasCost - relayerFee;

        (bool result, ) = payable(to).call{value: newAmount}("");
        require(result, "ETH transfer failed");

        (bool result2, ) = payable(msg.sender).call{
            value: gasCost + relayerFee
        }("");

        require(result2, "ETH transfer failed with gas");
    }

    function withdrawWithSecret(
        string memory _secret,
        address _token,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) public onlyRelayer nonReentrant {
        bytes32 commitment = keccak256(abi.encodePacked(_secret));
        uint256 amount;

        if (_token == address(0)) {
            amount = ethDeposits[commitment];
            require(amount > 0, "No ETH deposit for this secret");
            ethDeposits[commitment] = 0;
            payable(_to).transfer(amount);
        } else {
            amount = tokenDeposits[commitment][_token];
            require(amount > 0, "No Token deposit for this secret");
            tokenDeposits[commitment][_token] = 0;
            IERC20(_token).transfer(_to, amount);
        }

        emit Withdrawn(commitment, amount, _secret, _token);
    }

    function withdrawToken(
        address _token,
        uint256 _amount,
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce
    ) public onlyRelayer nonReentrant {
        uint256 initialGas = gasleft();

        uint256 totalRequired = tx.gasprice * initialGas;

        (
            bytes memory vaultId,
            ,
            ,
            bytes memory signedMessage
        ) = IMecenateVerifier(verifierContract).sismoVerify(
                sismoConnectResponse,
                _to,
                _nonce
            );

        (address to, bytes32 nonce) = abi.decode(
            signedMessage,
            (address, bytes32)
        );

        require(
            ethDeposits[keccak256(vaultId)] >= totalRequired,
            "Not enough ETH for gas required"
        );

        require(to == _to, "Not Same Address");

        require(nonce == _nonce, "Not Same Nonce");

        // Check if the commitment exists
        // and the amount is greater than the deposit.
        require(
            tokenDeposits[keccak256(vaultId)][_token] >= _amount,
            "Not enough token balance"
        );

        // Decrease the token balance for the commitment.
        tokenDeposits[keccak256(vaultId)][_token] -= _amount;

        // Transfer the tokens to msg.sender or operator.
        IERC20(_token).transfer(to, _amount);

        uint256 gasUsed = initialGas - gasleft();

        uint256 gasCost = gasUsed * tx.gasprice;

        uint256 relayerFee = (gasCost * relayerFeePercentage) / 10000;

        require(
            ethDeposits[keccak256(vaultId)] >= gasCost + relayerFee,
            "Not enough balance for gas used"
        );

        ethDeposits[keccak256(vaultId)] -= gasCost + relayerFee;

        (bool result, ) = payable(msg.sender).call{value: gasCost + relayerFee}(
            ""
        );

        require(result, "ETH transfer failed with gas");
    }

    function getEthDeposit(
        bytes32 encryptedVaultId
    ) public view returns (uint256) {
        return ethDeposits[encryptedVaultId];
    }

    receive() external payable {
        revert("Use depositETH function");
    }

    fallback() external payable {
        require(msg.data.length > 0, "Data required for Sismo verification.");
        bytes32 encryptedVaultId = abi.decode(msg.data, (bytes32));
        ethDeposits[encryptedVaultId] += msg.value;
    }

    function getTokenDeposit(
        address _token,
        bytes32 encryptedVaultId
    ) public view returns (uint256) {
        return tokenDeposits[encryptedVaultId][_token];
    }

    function execute(
        address _target,
        bytes calldata _data,
        uint256 _value,
        bytes32 _encryptedVaultId
    ) external onlyRelayer nonReentrant returns (bool) {
        uint256 availableBalance = ethDeposits[_encryptedVaultId];

        uint256 daiBalanceB4 = IERC20(DAI).balanceOf(address(this));
        uint256 museBalanceB4 = IERC20(MUSE).balanceOf(address(this));

        uint256 totalRequired = _value + (tx.gasprice * gasleft());

        require(availableBalance >= totalRequired, "Insufficient ETH balance");

        bool success; // variabile per verificare il successo delle transazioni
        bytes memory retData; // dati di ritorno dalle transazioni

        if (_data.length == 0) {
            (success, ) = payable(_target).call{value: _value}("");
        } else {
            if (_value == 0) {
                (success, retData) = _target.call(_data);
            } else {
                (success, retData) = _target.call{value: _value}(_data);
            }
        }

        require(success, "Transaction failed");

        uint256 gasUsed = totalRequired - _value - (tx.gasprice * gasleft());
        uint256 relayerFee = (gasUsed * relayerFeePercentage) / 10000;

        require(
            availableBalance >= gasUsed + relayerFee,
            "Insufficient balance for gas and fee"
        );

        ethDeposits[_encryptedVaultId] =
            availableBalance -
            gasUsed -
            relayerFee;

        (success, ) = payable(msg.sender).call{value: gasUsed + relayerFee}("");
        require(success, "ETH transfer failed");

        uint256 daiBalance = IERC20(DAI).balanceOf(address(this));
        uint256 museBalance = IERC20(MUSE).balanceOf(address(this));

        uint256 diffDai;
        uint256 diffMuse;

        if (daiBalanceB4 > daiBalance) {
            diffDai = daiBalanceB4 - daiBalance;

            if (diffDai > 0 && daiBalanceB4 != 0) {
                tokenDeposits[_encryptedVaultId][DAI] -= diffDai;
            }
        }

        if (museBalanceB4 > museBalance) {
            diffMuse = museBalanceB4 - museBalance;

            if (diffMuse > 0 && museBalanceB4 != 0) {
                tokenDeposits[_encryptedVaultId][MUSE] -= diffMuse;
            }
        }

        return true;
    }

    function setRelayer(address _relayer) external onlyOwner {
        relayer = _relayer;
    }

    function setVerifierContract(address _verifierContract) external onlyOwner {
        verifierContract = _verifierContract;
    }

    function setFactoryContract(address _factoryContract) external onlyOwner {
        factoryContract = _factoryContract;
    }

    function setMecenateBay(address _mecenateBay) external onlyOwner {
        mecenateBay = _mecenateBay;
    }

    function setMecenateUsers(address _mecenateUsers) external onlyOwner {
        mecenateUsers = _mecenateUsers;
    }
}

contracts/modules/Acceptance.sol.content:
/**
 * @title Acceptance
 * @dev This contract is an abstract contract that inherits from Events and Staking contracts. It provides a function to accept a post and add stake to the post. The function accepts a Sismo Connect response as a parameter and verifies the user existence. It also checks if the user is not the seller and if the post is in Proposed status. If the post has an escrow payment, it checks if the buyer has paid enough and adds the payment to the post's escrow. If the post does not have an escrow payment, it requires a payment from the buyer and adds it to the post's escrow. Finally, it changes the post status to Accepted and emits an Accepted event.
 */
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "./Events.sol";
import "./Staking.sol";

abstract contract Acceptance is Events, Staking {
    function acceptPost(
        bytes memory sismoConnectResponse,
        address _to,
        bytes32 _nonce,
        Structures.Tokens tokenId,
        uint256 _paymentAmount
    ) external payable virtual {
        require(
            validStatuses[uint8(Structures.PostStatus.Proposed)],
            "INVALID_STATUS"
        );
        _checkToken(tokenId);

        uint256 amountToAdd = tokenId == Structures.Tokens.NaN
            ? msg.value
            : _paymentAmount;
        bytes32 sellerVaultIdHash = keccak256(postSettingPrivate.vaultIdSeller);

        // Get encryptedVaultId only once
        (
            bytes memory vaultId,
            uint256 twitterId,
            uint256 telegramId,

        ) = _verifyNonce(sismoConnectResponse, _to, _nonce);
        bytes32 encryptedVaultId = keccak256(vaultId);

        // Use local variable for repeated calls
        uint256 sellerStake = Deposit._getDeposit(tokenId, sellerVaultIdHash);

        require(sellerStake >= post.postdata.escrow.stake, "STAKE_INCORRECT");
        require(
            IMecenateUsers(settings.usersModuleContract).checkifUserExist(
                encryptedVaultId
            ),
            "USERT_NOT_EXIST"
        );
        require(encryptedVaultId != sellerVaultIdHash, "YOU_ARE_THE_SELLER");

        if (post.postdata.escrow.payment > 0) {
            require(
                _paymentAmount >= post.postdata.escrow.payment,
                "NOT_ENOUGH_PAYMENT"
            );
        } else {
            require(msg.value > 0, "ZERO_MSGVALUE");
            require(_paymentAmount > 0, "ZERO_PAYMENT");
        }

        uint256 payment = _addStake(
            tokenId,
            encryptedVaultId,
            settings.vaultContract,
            amountToAdd
        );

        // Update all at once
        post.postdata.escrow.payment = payment;
        post.postdata.settings.status = Structures.PostStatus.Accepted;
        _changeStatus(Structures.PostStatus.Accepted);

        // Update private settings
        postSettingPrivate.vaultIdBuyer = vaultId;
        postSettingPrivate.buyerTwitterId = twitterId;
        postSettingPrivate.buyerTelegramId = telegramId;

        emit Accepted(post);
    }
}

